style.css:
@-moz-document url-prefix("https://notalone.tv/room/") {
main.room {
    background: #000;
}
#room_chat ul.messages {
    background: #000;
}
#new_player .top {
    background: rgba(0, 0, 0, 0.5)
}
header, #new_player .bottom .buttons {
    background: #121212;
}
li.chatMsg {
    background: #121212;
    border: 1px solid #151515;
}
li.chatMsg .right .info .name {
    color: #35ad79;
}
li.chatMsg .right .messages {
    color: #fff;
}
li.chatMsg .right .messages p {
    font-size: 14px;
    
}
li.chatMsg.colored .right .info span.date {
   color: #707070;
}
#room_chat form.new_message {
    background: #000;
}
#room_chat form.new_message textarea {
    border: 1px solid #151515;
    color: #fff;
    background: #121212;
}
.search_container .search_row input[type="text"] {
    border: 1px solid #151515;
    border-radius: 50px;
    background: rgba(255, 255, 255, 0.05);
    color: #fff
}
.search_container.focus {
    border-radius: 50px;
    transition: .15s
}
.room_setting .single_setting .inputContainer input {
    border: 1px solid #151515;
    border-radius: 15px;
    background: rgba(255, 255, 255, 0.05);
    color: #fff
}
.grid > [class*="grid"]:not(.grid):not([class*="grid_"]), .own-footer, footer {
    background: #121212
}
.playlist, .room_setting {
    border: 1px solid #151515
}
.single_setting .right button, .buttons_playlist > div button, .playlist li .buttons button {
    border-radius: 15px;
}
.room_setting .single_setting .right select {
    border: 1px solid #121212;
    border-radius: 15px 15px 15px 15px;
    color: #fff;
    background: #121212;
    appearance: none; 
    text-align: center;
}
#new_player .top .playerSettings button, #room_chat .topBar button {
    border-radius: 5px
}
.socialShare a.icon:not(:last-child), .socialShare a.icon.bm {
    border-radius: 50px;

}
#new_player ul.usersList li.user {
    border-radius: 50px;
}
#new_player ul.usersList li.user .statusBar .left, #new_player ul.usersList li.user .statusBar .right {
    margin: 25px 0px 0px 0px;
    border-radius: 10px 0px 0px 0px;
    background: #000c;
}
#new_player ul.usersList li.user > span {
    background: #000c;
    font-size: 12px !important;
}
#new_player ul.usersList li.user .statusBar i.success {
    color: #35ad79;
    font-size: 15px;
}
#new_player .bottom {
    background: rgba(0, 0, 0, 0.6);
}
#new_player .bottom .progress i.progressBackground {
background: rgba(60, 60, 60, 0.5);
}
.playlist li .buttons button.playing::before {
    border-radius: 50px 50px 0 0;
}
#new_player .bottom .progress .buffered_container i {
    background: #226e4d;
    border: 0px solid #fff;
    border-radius: 50px;
}
#new_player .bottom .progress.hover {
    margin-bottom: 0px;
}
input[type=range]::-moz-range-thumb {
    background-color: #fff !important;
    border-radius: 3px !important;
    border: 2px solid #fdfdfd !important;
    width: 3px !important;
    height: 15px !important;
    
}
input[type=range]::-moz-range-thumb:hover {
    background-color: #35ad79 !important;
    border: 2px solid #35ad79 !important;
}
#new_player .bottom .progress input[type=range]::-moz-range-thumb {
    opacity: 0;
}
#new_player .bottom .progress input[type=range] {
cursor: pointer !important;
}
#new_player .bottom .progress span.progressTime {
    background: rgba(0, 0, 0, 0.6);
    box-shadow: 0px 0px 0px #35ad79;
    padding: 1.5px 5px;
}
#new_player .bottom .progress i.progressBackground, #new_player .bottom .progress .buffered_container, #new_player .bottom .progress i.progressBackground {
    border: 0px solid #fff;
    border-radius: 50px;
}
#new_player .bottom .progress i.progressBar {
    border: 0px solid #fff;
    border-radius: 50px;
}
#new_player .bottom .buttons .group_right input[type="range"].volume-bar {
    max-width: 60px;
    margin: 0 -4px;
}
}
