style.css:
.js-quickfilter-button[title*="component"] {
    background: rgba(0, 0, 0, 0.05);
}
/* Better components. */
 #ghx-backlog-column .ghx-issue-content {
    display: flex;
}
#ghx-backlog-column .ghx-row.ghx-plan-extra-fields-1 {
    max-width: calc(100% - 70px);
}
#ghx-backlog-column .ghx-row.ghx-plan-extra-fields-2 {
    max-width: calc(100% - 70px);
}
.ghx-issue-compact .ghx-plan-extra-fields.ghx-plan-extra-fields.ghx-row {
    margin-left: 5px;
}
.ghx-extra-field[data-tooltip*="Component"] {
    padding: 0 6px;
    background: rgba(0, 0, 0, 0.2);
    border-radius: 0.3em;
    font-weight: 500;
    text-align: center;
}
.ghx-plan-extra-fields-1, .ghx-plan-extra-fields-2 {
    margin: 7px 6px 0 4px !important;
    display: inline-block;
}
.ghx-extra-field[data-tooltip*="Remaining"] {
    width: 15px;
    text-align: center;
}
.ghx-extra-field[data-tooltip*="Remaining"] .ghx-extra-field-content {
    background: rgba(0, 0, 0, 0.2);
    border-radius: 2em;
    color: rgba(51, 51, 51, 0.8);
    font-size: 11px;
    font-weight: bold;
    width: 35px;
    margin-top: 1px;
    line-height: 16px;
}
.ghx-statistic-badge {
    opacity: 0.85;
    background: none;
}
.ghx-extra-field-seperator {
    opacity: 0;
    width: 0;
}
.ghx-stat-total .ghx-estimate-badge {
    font-size: 10pt;
}
.ghx-controls-filters dd a.ghx-active, .ghx-controls-filters dd a.ghx-active:hover {
    border: 2px #344563 solid;
    background: transparent;
    color: #344563;
}
