style.css:
@-moz-document domain("index-education.net") {
.dotty {background-attachment: fixed;background-color: #1f1f1f;background-image: radial-gradient(circle, #1c1c1c 5%, transparent 10%), radial-gradient(circle, #1c1c1c 5%, transparent 10%);background-size: 2.6rem 2.6rem;background-position: 0 0, 1.3rem 1.3rem;}
 .ObjetBandeauEspace {position: relative;display: flex;align-items: center;justify-content: center;background-color: rgba(0, 0, 0, 0.9);color: #eee;height: 4.7rem;}
.objetBandeauEntete_secondmenu {background-color: #3f3f3f;padding-top: 0.2rem;display: flex;justify-content: space-between;}
.objetbandeauentete_global .objetBandeauEntete_menu:not(.ongletLudique) {background-color: #3a3a3a;padding-right: 0;height: 4.5rem;display: flex;justify-content: space-between;}
.objetBandeauEntete_secondmenu .objetBandeauEntete_fullsize .precedenteConnexion {padding: 0.4rem 0.8rem;font-size: 1rem;color: #eee;}
.widget.edt {background-color: #2c2c2c;}
.widget .filtre-conteneur .input-wrapper .as-input {border-radius: 1.6rem;padding: 0.4rem 1.6rem;background-color: #3a3a3a;border-color: #1f1f1f;color: #eee;}
i.ObjetBoutonBandeau_icone.ObjetBoutonBandeau_iconeInactive {color: #fff !important;cursor: not-allowed;}
.widget.travailafaire {background-color: #2c2c2c;color: #eee;}
.widget.travailafaire .content-container .no-events, .widget.travailafaire .content-container .no-events p, .widget.travailafaire .content-container .salle, .widget.travailafaire .content-container .event-type {color: #ccbaff;}
.widget.travailafaire h3 {color: #cbf;}
.widget.ressourcepedagogique {background-color: #2c2c2c;}
.widget.ressourcepedagogique .content-container .no-events, .widget.ressourcepedagogique .content-container .no-events p, .widget.ressourcepedagogique .content-container .salle, .widget.ressourcepedagogique .content-container .event-type {color: #ccbaff;}
.widget.ressourcepedagogique h3 {color: #cbf;}
.widget.kiosque {background-color: #2c2c2c;}
.widget.kiosque h3 {color: #cbf;}
.widget .content-container > div[id^=id_] h4 {margin: 0;padding: 0;font-size: 1.3rem;font-weight: 600;color: #eee;}
.kiosque .content-container > div[id^="id_"] ul > li .link-as-wrapper span {flex: 1 1 auto;margin-left: 0.4rem;font-weight: 600;color: #eee;height: 3.2rem;}
.widget.viescolaire {background-color: #2c2c2c;}
.widget.viescolaire h3 {color: #b2cdff;}
.widget .content-container > div[id^=id_] ul > li .file-name, .widget .content-container > div[id^=id_] ul > li .date {word-break: break-all;word-break: break-word;color: #fff;}
.widget.notes {background-color: #2c2c2c;}
.widget.notes h3 {color: #ffd1b2;}
.widget.agenda {background-color: #2c2c2c;}
.widget.agenda h3 {color: #87c1a2;}
.widget.agenda .content-container .no-events, .widget.agenda .content-container .no-events p, .widget.agenda .content-container .salle, .widget.agenda .content-container .event-type {color: #87c1a2;}
.widget.actualites {background-color: #2c2c2c;}
.widget.actualites h3 {color: #87c1a2;}
.widget.actualites .content-container .no-events, .widget.actualites .content-container .no-events p, .widget.actualites .content-container .salle, .widget.actualites .content-container .event-type {color: #87c1a2;}
.widget.discussions {background-color: #2c2c2c;}
.widget.discussions h3 {color: #87c1a2;}
.widget.discussions .content-container .no-events, .widget.discussions .content-container .no-events p, .widget.discussions .content-container .salle, .widget.discussions .content-container .event-type {color: #87c1a2;}
.widget.menudelacantine {background-color: #2c2c2c;}
.widget.menudelacantine h3 {color: #87c1a2;}
.widget.menudelacantine .content-container .no-events, .widget.menudelacantine .content-container .no-events p, .widget.menudelacantine .content-container .salle, .widget.menudelacantine .content-container .event-type {color: #87c1a2;}
body {width: 100%;height: 100%;margin: 0;-webkit-text-size-adjust: 100%;-moz-text-size-adjust: 100%;-ms-text-size-adjust: 100%;overscroll-behavior-y: contain;background-color: #1f1f1f;}
.jspTrack {background: rgba(200, 200, 200, 0.1);}
.jspVerticalBar .jspDrag {background-color: #3f3f3f;width: 7px;border: #3a3a3a 1px solid;margin-left: 1px;margin-right: 1px;}
.jspArrow {background-color: transparent;text-indent: -20000px;display: block;cursor: pointer;background-repeat: no-repeat;}
.jspVerticalBar .jspDrag.jspHover {   background-color: #5C5F60; }
.EspaceIndex #div > .interface_affV > div.interface_affV_client.no-tactile, .EspaceIndex #div > .interface_affV > div.interface_affV_client.over-scroll {overflow: auto;scrollbar-color: #E2E2E2 #F6F6F6;scrollbar-width: auto;background-color: #1f1f1f;}
 .Texte10
{
  font-size: 1.1rem;
  color: #f5f5f5;
}

}
