}

/* base.client.5f4c340e.css | https://www.chess.com/bundles/app/css/base.client.5f4c340e.css */

.ui_v5-button-component.ui_v5-button-basic {
  /* --borderColor: #bdbcb8; */
  --borderColor: #aeada6;
}

@media (hover: hover), (min--moz-device-pixel-ratio: 0), not all {
  .ui_v5-button-component.ui_v5-button-basic:not([disabled]):hover {
    /* background-color: var(--basicBghover,#e7e6e4); */
    /* color: var(--basicHoverColor); */
    background-color: var(--basicBghover,#5e9166);
    color: #e8dae0;
  }
}

.ui_v5-button-component.ui_v5-button-primary {
  /* background-color: #7fa650; */
  /* color: #fff; */
  background-color: #6ea060;
  color: #e8e8e8;
}

.ui_v5-button-component.ui_v5-button-disabled, .ui_v5-button-component:disabled {
  /* filter: grayscale(.4); */
  filter: grayscale(40%);
}

/* navigation.client.2015faf2.css | https://www.chess.com/bundles/app/css/navigation.client.2015faf2.css */

#sb .link span:not(.badge) {
  color: c1798c;
}

#sb .action:hover {
  /* color: #fff; */
