style.css:
@-moz-document domain("exchange.realvision.com") {
/** VARS **/

:root {
	--main-bg-color: #000;
	--main-btn-color: #ff04616e;
	--main-btn-color-no-opacity: #66062b;
	--main-btn-hover: #ff0461c7;
	--main-text-color: #fff;
	--main-card-border-color: #3c3a3a;
	--main-menu-color: #d0cbcb;
	--main-modal-bg: #101010;
	
	--main-seach-hover-bg: #0c0c0c;
}

/** BASE **/
#app, body {
	background: none;
	background-color: #000;
	color: var(--main-text-color);
}

#app .ui.horizontal.divider {
	background: none;
	color: inherit;
}

#app .ui.horizontal.divider::before,
#app .ui.horizontal.divider::after {
	background: none;
}

#app ::placeholder {
    color: var(--main-card-border-color) !important;
}

#app .ui.fluid.placeholder,
.ui.placeholder, 
.ui.placeholder .image.header:after, 
.ui.placeholder .line, 
.ui.placeholder .line:after, 
.ui.placeholder>:before,
.ui.placeholder .header .line, 
.ui.placeholder .image .line, 
.ui.placeholder .paragraph .line {
	opacity: .5;
	background: var(--main-bg-color) !important;
}

#app .ui.search>.results>.message .header {
	color: var(--main-text-color) !important;
}

.feed-list .last-action-bar .last-action-bar-content a {
	color: var(--main-text-color);
}

header.main-navigation .ui.top.right.pointing.dropdown.btn-add-dropdown {
	background: var(--main-bg-color);
}

#app > div > div > header > nav > div > div.right.menu > div.ui.top.right.pointing.dropdown.btn-add-dropdown > button,
header.main-navigation.right.menu .ui.button.btn-add {
	border: 1px solid var(--main-btn-color) !important;
    background-color: var(--main-btn-color) !important;
    border-radius: 3px !important;
    color: var(--main-text-color) !important;
	box-shadow: none;
}

#app > div > div > header > nav > div > div.right.menu > div.ui.top.right.pointing.dropdown.btn-add-dropdown > button:hover,
header.main-navigation.right.menu .ui.button.btn-add:hover {
	color: var(--main-text-color) !important;
	background-color: var(--main-btn-hover) !important;
}

.extra.content .ui.labeled.button .ui.basic.label {
	background-color: var(--main-text-color) !important;
}


.ui.label {
	background: #53bfd8 !important;
    color: var(--main-text-color);
}

.ct-grid,
.ct-labels, 
.ct-labels foreignObject, 
.ct-labels .ct-label.ct-horizontal.ct-end, 
g > foreignObject, 
g.ct-labels foreignObject > span {
    fill: var(--main-card-border-color) !important;
    stroke: var(--main-card-border-color) !important;
    stroke-width: 1px;
    stroke-dasharray: 2px;
    color: var(--main-text-color) !important;
}

.post-comment-form-wrapper {
	border: 0; 	
}


/** COMMENTS AND MENUS **/
.ui.comments .comment .text, 
.ui.menu .item {
    color: var(--main-menu-color);
}
.ui.secondary.vertical.menu .dropdown.item:hover,
.ui.secondary.vertical.menu .link.item:hover,
.ui.secondary.vertical.menu > .item.active,
.ui.secondary.vertical.menu a.item:hover {
    border: 1px solid var(--main-btn-color);
    background-color: var(--main-btn-color);
    border-radius: 3px;
    color: var(--main-text-color) !important;
}
.ui.menu .ui.dropdown .menu {
	background-color: var(--main-modal-bg);
}
.ui.menu .ui.dropdown .menu>.item,
.ui.menu .ui.dropdown .menu>.item:hover {
	color: var(--main-text-color) !important;
}
.ui.secondary.menu .dropdown.item:hover,
.ui.secondary.menu .link.item:hover, 
.ui.secondary.menu a.item:hover {
    background: rgba(0,0,0,.05);
    color: rgb(240 243 248);
}
.content-input .secondary.menu,
.ui.segment.editor-footer-wrapper {
	color: var(--main-text-color);
	border: 0 !important;
}
.content-input .secondary.menu .active.item, 
.content-input .secondary.menu .active.item:hover {
	background-color: var(--main-btn-color-no-opacity) !important;
	color: var(--main-text-color) !important;
}

.ui.selection.list .list>.item, 
.ui.selection.list>.item,
.ui.selection.list .list>.item:hover, 
.ui.selection.list>.item:hover {
	color: var(--main-text-color) !important;
}

.ui.selection.dropdown,
.ui.active.visible.selection.dropdown,
.ui.active.visible.selection.dropdown div.text, 
.ui.selection.dropdown .default.text{
	background-color: var(--main-modal-bg) !important;
	color: var(--main-text-color) !important;
}
.ui.selection.active.dropdown:hover, .ui.selection.active.dropdown:hover .menu,
.ui.selection.active.dropdown, .ui.selection.active.dropdown .menu {
	border-color: var(--main-card-border-color);
}
.ui.selection.dropdown .menu>.item {
	border-top: 1px solid var(--main-card-border-color) !important;
}

.ui.horizontal.selection.list.filter-bar-wrapper, 
.ui.horizontal.selection.list.filter-bar-wrapper .text,
.ui.horizontal.selection.list.filter-bar-wrapper .dropdown.icon,
.ui.horizontal.selection.list.filter-bar-wrapper .menu.visible .item {
    color: var(--main-text-color) !important;
}

.content-input {
    background: inherit;
    border: 1px solid var(--main-card-border-color);
    border-radius: 8px;
    margin-bottom: 15px;
}

.ui.mini.pointing.attached.three.item.menu {
	background: var(--main-background) !important;
	color: inherit;
	border:0;
}

.ui.mini.pointing.attached.three.item.menu  .item {
	border-color: var(--main-card-border-color) !important;
    border-width: 0 1px;
}

.ui.mini.pointing.attached.three.item.menu  .item:hover {
	color: var( --main-text-color);
}

.ui.mini.pointing.attached.three.item.menu  .active.item {
	background: var(--main-btn-color);
    color: var( --main-text-color);
}

.ui.fluid.card.post-item.post-list-item .card-head-content-type,
.ui.fluid.card.post-item.post-list-item a,
.ui.fluid.card.post-item.post-list-item .card-head-divider,
.ui.fluid.card.post-item.post-list-item .card-head-divider::after,
.ui.fluid.card.post-item.post-list-item .card-head-group-name {
	color: var(--main-text-color);
}

.ui.pointing.menu .active.item:after,
.ui.pointing.menu .active.item:hover:after,
.ui.vertical.pointing.menu .active.item:after,
.ui.vertical.pointing.menu .active.item:hover:after {
	background-color: var(--main-btn-color-no-opacity);
	border-color: var(--main-btn-color-no-opacity);
}

/** SEGMENTS **/
.ui.segment,
.ui.segments {
	background-color: var(--main-bg-color);
	border: 1px solid var(--main-card-border-color) !important;
	box-shadow: none;
	border-radius: .5rem;
}

.ui.segment .ui.header {
	color: var(--main-text-color);
}


.content-group-selector-wrapper {
	border-right: 1px solid var(--main-card-border-color) !important;
}
.ui.segment.editor-footer-wrapper,
.ui.segment .ui.header {
	color: var(--main-text-color);
	border-bottom-left-radius: .5rem !important;
	border-bottom-right-radius: .5rem !important;
}

.ui.segments:not(.horizontal) > .segment:first-child {
	border-top-right-radius: .5rem;
    border-top-left-radius: .5rem;
	border: 0 !important;
}

.ui.segments:not(.horizontal) > .segment:last-child {
    border-width: 1px 0 0 0 !important;
	box-shadow: none;
}

.ui.form.simple-post-create-form .ui.segments,
#ask-inline-form .ui.segments {
	border: none !important;	
}

.ui.form.simple-post-create-form .ui.segment.clearing,
.ui.clearing.segment.editor-footer-wrapper,
.ui.segment.create-post-messages {
	border-top: 1px solid var(--main-card-border-color) !important;
	background: inherit !important;
	border-radius: 0;
}

.ui.segment.user-reputation .ui.tiny.statistic .label,
.ui.segment.user-reputation .ui.tiny.statistic .value {
	color: var(--main-text-color);
}

/** BUTTONS **/
.ui.basic.button, 
.ui.basic.buttons .button {
	background-color: var(--main-btn-color) !important;
    color: var(--main-text-color) !important;
    box-shadow: inset 0 0 0 1px #ff046169;
}

.ui.basic.button:focus, .ui.basic.button:hover, 
.ui.basic.buttons .button:focus, 
.ui.basic.buttons .button:hover,
.ui.basic.primary.button:hover,
.ui.basic.primary.buttons .button:hover,
body .ui.basic.primary.buttons .button:hover, 
body .ui.basic.primary.button:hover,
body .ui.basic.primary.buttons .button:focus,
body .ui.basic.primary.button:focus{
    background: var(--main-btn-hover) !important;
    color: var(--main-text-color) !important;
    box-shadow: inset 0 0 0 1px rgba(34,36,38,.35), inset 0 0 0 0 #e8e8e8 !important;
	border: 0 !important;
}

.users-leaderboard .ui.segments {
	border: 1px solid var(--main-card-border-color);
	border-radius: .5rem;
}

.users-leaderboard .ui.segments .ui.header {
	border-width: 0 0 1px 0 !important;
}

.ui.basic.primary.buttons .button,
.ui.basic.primary.button i.blue.icon,
body .ui.basic.primary.buttons .button,
body .ui.basic.primary.button {
    box-shadow: none !important;
    color: var(--main-text-color) !important;
	border: none !important;
}

body .ui.basic.primary.buttons .button .button-number,
body .ui.basic.primary.button .button-number {
	border-color: var(--main-text-color);
}

/** INPUTS **/
.ui.checkbox+label, .ui.checkbox label {
	color: inherit !important;
}

.ui.form textarea,
.simple-post-title-input__control {
    padding: .5rem !important;
    border-radius: .5rem;
    color: var(--main-bg-color) !important;
}
.ui.form textarea {
	padding: .25rem !important;
}

textarea.simple-post-title-input__input {
    color: var(--main-text-color) !important;
}

.ui.left.icon.input.group-picker-search-input input {
    border-radius: .5rem !important;
}
.ui.left.icon i.search.icon {
	left: 2.5rem !important;
}


/** CARDS **/
.ui.card, .ui.cards>.card {
	background: var(--main-bg-color) !important;
	border: 1px solid var(--main-card-border-color);
}

.ui.card>.extra.content.card-footer {
	background: var(--main-bg-color) !important;
	border-top: 1px solid var(--main-card-border-color) !important;
}

.ui.card .user-bar .user-bar-content .header.user-bar-name a {
	color: var(--main-text-color);
}
.ui.answer-list-item .question-title,
.ui.answer-list-item > .content.card-head span,
.ui.answer-list-item > .content.card-head span a ,
.ui.question-list-item .question-title,
.ui.question-list-item > .content.card-head span,
.ui.card > .content.card-head .card-head-content-type,
.ui.card > .content.card-head .card-head-group-name,
.ui.card > .content.card-head span a {
	color: var(--main-text-color) !important;
}

.ui.card.question-item.question-list-item,
.ui.card.question-item.question-list-item .content.card-head,
.ui.card.question-item.question-list-item .content.card-head a,
.ui.card.question-item.question-list-item .content.card-head .card-head-content-type,
.ui.card.question-item.question-list-item .content.card-head .card-head-divider,
.ui.card.question-item.question-list-item .content.card-head .card-head-group-name,
.ui.card.question-item.answer-list-item,
.ui.card.question-item.answer-list-item .content.card-head,
.ui.card.question-item.answer-list-item .content.card-head .card-head-content-type a,
.ui.card.question-item.answer-list-item .content.card-head .card-head-content-type,
.ui.card.question-item.answer-list-item .content.card-head .card-head-content-type .card-head-divider,
.ui.card.question-item.answer-list-item .content.card-head .card-head-content-type .card-head-group-name,
.ui.message {
	background: var(--main-bg-color);
	color: var(--main-text-color) !important;
}

.ui.card.question-item.question-list-item .question-title {
	color: var(--main-text-color);
}

.ui.card.question-item.question-list-item .question-poll-item .question-poll-item-radio,
.question-poll-item-radio {
	background: var(--main-bg-color) !important;
	border: 1px solid var(--main-card-border-color);
}

.ui.card.question-item .question-poll-item .question-poll-item-radio .ui.radio.checkbox,
.question-poll-item-radio .ui.radio {
	color: var(--main-text-color) !important;
}

.ui.card.question-item .question-poll-item .question-poll-item-radio .question-poll-item-bar {
	background: var(--main-btn-color);
	color: var(--main-text-color) !important;
}


/** MODALs **/
.ui.dimmer {
	background-color: rgba(0,0,0,.90);
}

.ui.modal,
.ui.modal .header,
.ui.modal .content,
.ui.modal .footer,
.ui.modal .actions,
.ui.modal .group-picker-wrapper.content,
.ui.search>.results,
.ui.modal .scrolling.content .content > .summary,
.ui.modal .scrolling.content .content > .text {
	background-color: var(--main-modal-bg);
	color: var(--main-text-color) !important;
}

.ui.modal .scrolling.content .content > .summary .date {
	color: var(--main-card-border-color);
}

/** POPOVERS **/
.ui.popup {
	background-color: var(--main-modal-bg);
	border: 1px solid var(--main-modal-bg);
	border-radius: .5rem !important;
}

.ui.popup:before {
	background-color: var(--main-modal-bg);
	border: 1px solid var(--main-modal-bg);
}

.ui.popup.transition.visible.notifications-dropdown {
	top: 8.25rem !important;
}

.ui.popup.transition.visible .ui.list .item,
.ui.inverted.popup {
    color: var(--main-text-color);
    background-color: var(--main-modal-bg);
}

.ui.tiny.modal.transition.visible.active .ui.list,
.ui.tiny.modal.transition.visible.active .ui.list .content a {
    background: var(--main-modal-bg);
	color: var(--main-text-color);
}

.ui.inverted.bottom.popup:before, 
.ui.inverted.left.center.popup:before, 
.ui.inverted.right.center.popup:before, 
.ui.inverted.top.popup:before {
	background-color: var(--main-modal-bg);
}

.ui.popup.transition.visible.notifications-dropdown .ui.feed.notifications-list,
.ui.popup.transition.visible.notifications-dropdown .ui.feed.notifications-list a .content .summary,
.ui.popup.transition.visible.notifications-dropdown .ui.feed.notifications-list a .content .text.extra,
.ui.popup.transition.visible span {
	background: transparent;
	color: var(--main-text-color) !important;
}

.ui.popup.transition.visible.notifications-dropdown .ui.feed.notifications-list a .content .summary .date {
	color: var(--main-card-border-color);
}

.ui.popup.transition.visible.notifications-dropdown .ui.feed.notifications-list a:hover {
	background: var(--main-seach-hover-bg);
}

.ui.popup .notifications-header {
	background: var(--main-modal-bg);
	color: var(--main-text-color) !important;
	border-color: var(--main-card-border-color) !important;
}

.ui.popup .notifications-header a {
	color: var(--main-text-color) !important;
	
}

.ui.popup:before {
	background: var(--main-modal-bg) !important;
	box-shadow: none !important;
}

.ui.popup.notifications-dropdown .ui.feed.notifications-list .event {
	border-color: var(--main-card-border-color);
}

/** SEARCH **/
.ui.search>.results,
.ui.search>.results .result {
	background-color: var(--main-modal-bg);
	border-color: var(--main-card-border-color);
}

.ui.search>.results {
	background-color: var(--main-modal-bg);
	color: var(--main-text-color) !important;
}

.ui.search>.results .result .content,
.ui.search>.results .result .content .title,
.ui.search>.results .result .content .description {
	color: var(--main-text-color);
}
.ui.search>.results .result:hover,
.ui.search>.results .result:hover .content,
.ui.search>.results .result:hover .content .title,
.ui.search>.results .result:hover .content .description
.ui.search>.results .result.active,
.ui.search>.results .result.active .content,
.ui.search>.results .result.active .content .title,
.ui.search>.results .result.active .content .description
.ui.search>.results .result.active:hover,
.ui.search>.results .result.active:hover .content,
.ui.search>.results .result.active:hover .content .title,
.ui.search>.results .result.active:hover .content .description{
	background-color: var(--main-seach-hover-bg);
}

.ui.category.search >.results .category .result.active, 
.ui.search >.results .result.active {
	background-color: var(--main-seach-hover-bg);
}

/** LISTS **/
.ui.list .list>a.item i.icon, 
.ui.list>a.item i.icon,
.ui.list .list>a.item:hover .icon, 
.ui.list>a.item:hover .icon,
.ui.huge.horizontal.link.list.float-right .item a i {
	color: var(--main-text-color) !important;
}

.ui.list,
.post-comment-wrapper {
	border-color: var(--main-card-border-color) !important;
	background-color: var(--main-bg-color);
	color: var(--main-text-color) !important;
}

.ui.comments .comment .content .author,
.ui.comments .comment .content .actions a {
	color: var(--main-text-color) !important;
}

.ui.relaxed.list,
.ui.relaxed.list a, 
.ui.relaxed.list a .content .header {
	background-color: inherit;
	color: var(--main-text-color) !important;
}

.ui.relaxed.list a:hover {
    border-radius: .5rem;
}

.ui.relaxed.list a .content span {
	color: var(--main-btn-color) !important;
}

/** GRIDS **/
.ui.stackable.grid,
.ui.stackable.grid .header {
	background-color: var(--main-bg-color);
	color: var(--main-text-color) !important;
	border-radius: .5rem;
}

.ui.grid .ui.list .item,
.ui.grid .ui.list .item:hover {
	color: var(--main-card-border-color) !important;
}
.ui.grid .ui.list .item.active,
.ui.grid .ui.list .item.active:hover {
	color: var(--main-text-color) !important;
}

/** BREADCRUMBS **/
.ui.breadcrumbs,
.ui.breadcrumbs .section a {
	background-color: inherit;
	color: var(--main-text-color) !important;
}

/** TABS **/
.active.tab.content-input-pane.content-input-pain-question {
	border-top: 1px solid var(--main-card-border-color);
}

/** DROPDOWNS **/
.ui.dropdown .menu,
.ui.dropdown.btn-add-dropdown,
.ui.dropdown.btn-add-dropdown .menu.visible,
.ui.dropdown .menu .item,
.ui.dropdown.btn-add-dropdown a,
.ui.dropdown.btn-add-dropdown .menu.visible .item .ui.header,
.ui.dropdown.btn-add-dropdown .menu.visible .item .ui.header .emoji {
	background-color: var(--main-modal-bg);
	color: var(--main-text-color) !important;
}

.ui.dropdown .menu .item:hover,
.ui.menu .ui.dropdown .menu>.item:hover, 
.ui.menu .ui.dropdown .menu>.item:hover .ui.header {
	background: var(--main-seach-hover-bg) !important;
}

.ui.dropdown .menu:after {
	background-color: var(--main-modal-bg) !important;
}

/** MENUS **/
.ui.stackable.menu {
	background-color: var(--main-modal-bg);
	border: 1px solid var(--main-card-border-color);
}

.ui.stackable.menu a,
.ui.stackable.menu a:active {
	color: var(--main-text-color) !important;
}
.ui.stackable.menu .active.item {
	background-color: var(--main-btn-color-no-opacity) !important;
}


}
