style.css:
/* ==UserStyle==
@name         MAL-Night
@version      1.0.0
@description  Dark/Nightmode theme for MyAnimeList.net
@namespace    KillYoy
@author       KillYoy
@homepageURL  https://github.com/KillYoy/MAL-Night
@updateURL    https://raw.githubusercontent.com/KillYoy/MAL-Night/master/MAL-Night.user.css
==/UserStyle== */
@-moz-document domain("myanimelist.net") {
/* Advertisement Killer */
.mal-ad-unit,
.right_top_ad,
.grecaptcha-badge,
#gdpr-modal-bottom,
.btn-mal-service,
.btn-affiliate.icon-shop-default,
.page-common iframe.fb-page,
.page-common #headerSmall .banner-header-anime-straming,
.page-common #footer-block .banner-footer-anime-streaming {
	visibility: hidden;
	display: none !important;
}
/* Padding Killer */
.page-common .pb16,
.page-common .pb24 {
	padding-bottom: 0px !important;
}
.page-common .pt20 {
	padding-top: 0px !important;
}

/* Welcome */
.welcome-back.popup-background {
	background-color: rgba(0,0,0,.5);
}

/* Body */
body:not(.ownlist) {
	background-color: #111111;
	background-image: none;
	color: #FFFFFF;
}
.page-common #contentWrapper {
	background-color: #212121;
	padding-bottom: 1px;
}
#myanimelist:before,
#myanimelist:after {
	content: "";
	width: 100%;
	bottom: 0;
	position: fixed;
	display: block;
	background-repeat: no-repeat;
	background-position: center bottom;
	background-size: contain;
	z-index: -2;
}
#myanimelist:before {
	background-image: url(https://i.imgur.com/3CKtjS4.png);
	left: -39%;
	height: 96%;
	opacity: 0.4;
}
#myanimelist:after {
	background-image: url(https://i.imgur.com/gs8GGr3.png);
	right: -38%;
	height: 98%;
	opacity: 0.4;
}

/* Site Footer */
.page-common .footer-ranking,
.page-common #footer-block {
	width: 1060px;
	margin: 0 auto;
}
.page-common .footer-ranking {
	background-color: #191919;
}
.page-common #footer-block .footer-link-icon-block {
	height: 58px;
}
.page-common .pt24 {
	padding-top: 0px !important;
}
/* Top Header */
.page-common #menu {
	background-color: #2E51A2;
	position: fixed;
	top: 0;
	left: 0;
	width: 100%;
	z-index: 99;
}
.page-common #headerSmall {
	background-color: transparent;
	margin-bottom: -14px;
}
.page-common #menu_left {
	margin-left: 200px;
}
.page-common #headerSmall a.link-mal-logo {
	position: fixed;
	width: 179px;
	height: 32px;
	top: 4px;
	left: 10px;
	z-index: 999;
	filter: invert(1) saturate(0) brightness(1.5);
}
.page-common #headerSmall > a.link-mal-logo {
	background-image: url(https://cdn.myanimelist.net/images/mal-logo-xsmall.png?v=180226001);
	position: fixed;
	top: 2px;
}
.page-common #header-menu.pulldown {
	position: fixed;
	top: 2px;
	right: 2px;
}
.page-common #menu_right {
	position: absolute;
	left: 39.5%;
}
.page-common #header-menu .border::before {
	border-left: none;
}
.page-common .header-profile-button {
	background-color: transparent;
	box-shadow: none;
	height: 34px;
	width: 34px;
	top: -2px;
	right: -2px;
}
.page-common .header-profile-link {
	height: 20px;
}
.page-common .header-profile.link-bg,
.page-common .header-profile.link-bg:hover,
.page-common .header-profile.link-bg.on,
.page-common .header-profile.link-bg.on:hover {
	background-color: transparent !important;
}
.page-common .header-profile-link,
.page-common .header-profile-link:hover,
.page-common .header-list-button,
.page-common .header-list.on .header-list-button,
.page-common .header-message-button, 
.page-common .header-message-button .fa,
.page-common .header-notification-button,
.page-common .header-notification-button.has-unread {
	color: #FFFFFF;
}
.page-common .header-list.on .header-list-button,
.page-common .header-message-button .fa:hover {
	opacity: 0.7;
}
/* Header Notification Badges */
.page-common .header-message-button.has-unread.text1::after,
.page-common .header-notification-button.has-unread.text1::after {
	top: -1px;
}
.page-common .header-message-button.has-unread.text1::after,
.page-common .header-notification-button.new::after,
.page-common .header-message-button.has-unread::after ,
.page-common .header-notification-button.has-unread::after {
	background-color: #A12F31;
	border-radius: 8px;
}
/* Top Right Dropdowns */
.page-common .header-profile .header-profile-dropdown {
	right: -30px;
	top: 32px;
}
.page-common .header-list .header-list-dropdown {
	top: 32px;
}
.page-common .header-profile .header-profile-dropdown ul li a,
.page-common .header-list .header-list-dropdown ul li a {
	background-color: #131313;
}
.page-common .header-profile .header-profile-dropdown ul li a:hover,
.page-common .header-list .header-list-dropdown ul li a:hover {
	background-color: #191919;
	color: #FFFFFF;
}
/* Top Right Dropdown: Notifications */
.page-common .header-notification-dropdown {
	top: 32px;
}
.page-common .header-notification-dropdown-inner h3 {
	border: none;
}
.page-common .header-notification-dropdown .arrow_box {
	background-color: #161616;
}
.page-common .header-notification-item-header .time,
.page-common .header-notification-item > .inner.is-read,
.page-common .header-notification-item > .inner.is-read .header-notification-item-header .time,
.page-common .header-notification-item > .inner.is-read .header-notification-item-header .category {
	color: #CCCCCC;
}
.page-common .header-notification-dropdown-inner h3 .mark-all,
.page-common .header-notification-item-header .category,
.page-common .header-notification-item.new,
.page-common .header-notification-item {
	background-color: #161616;
	border-color: #333333;
	color: #FFFFFF !important;
}
.page-common .header-notification-item:hover {
	background-color: #191919;
}
.page-common .header-notification-view-all a,
.page-common .header-notification-view-all a:hover {
	background-color: #2E51A2;
	color: #FFFFFF;
}
/* Login / Signup Buttons */
.page-common #header-menu .header-menu-login .btn-signup,
.page-common #header-menu .header-menu-login .btn-signup:hover {
	background-color: transparent;
	border: none;
	position: fixed;
	padding: 0px 0px;
	right: 80px;
}
.page-common #header-menu .header-menu-login .btn-login,
.page-common #header-menu .header-menu-login .btn-login:hover {
	background-color: transparent;
	color: #FFFFFF;
	border: none;
	position: fixed;
	padding: 0px 0px;
	right: 0px;
}
/* Searchbar */
.page-common #searchBar.searchBar #topSearchText,
.page-common #searchBar.searchBar #topSearchValue option {
	background-color: #243763;
	border: none;
	color: #FFFFFF;
}
.page-common #searchBar.searchBar #topSearchButon.notActive,
.page-common #searchBar.searchBar #topSearchButon {
	background-color: #243763;
	border: 1px solid #243763;
	color: #A5A5A5;
}
.page-common #searchBar.searchBar #topSearchValue {
	background-position: 78px 8px;
	background-color: #DBC89C;
	border: none;
	filter: invert(1);
}
/* Small Header/My Panel */
.page-common .h1,
.initialize-tutorial {
	background-color: #191919;
	border: none;
	color: #FFFFFF;
}
.page-common .h1.edit-info {
	padding-right: 10px;
}
/* Main Content */
.page-common #content,
.page-common .normal_header {
	background-color: #212121;
	border: none;
	color: #CCCCCC;
}
.page-common .fn-blue1 {
	color: #0D80FF !important;
}
body.index .widget .index_h2_seo a,
.page-common a,
.page-common a:visited {
	color: #0D80FF;
}
body.index .widget .index_h2_seo a:hover,
.page-common a:visited:hover {
	color: #1D439B;
}
body.index .widget .widget-header {
	color: #FFFFFF;
	border-color: #333333;
}
.page-common .borderClass,
.page-common .border_top,
.page-common .borderDark,
.page-common .border_solid,
body.index .left-column,
body.index .widget.my_list_updates .item, 
body.index .widget.friend_list_updates .item, 
body.index .widget.clubs .item, 
body.index .widget.reviews .item {
	border-color: #333333;
}
body.index .widget.my_statistics table td:nth-child(1) {
	color: #CCCCCC;
}
.bgColor0.borderClass,
.bgColor1.borderClass {
	border: none;
}
.page-common .bgColor0 {
	background-color: #191919;
}
.page-common .bgColor1,
.page-common .bgColor2 {
	background-color: transparent;
}
/* Anime Boxart */
.widget-slide-block .widget-slide .btn-anime,
#widget-manga-store .widget-slide .btn-anime .link {
	border: none;
}
.widget-slide-block .widget-slide .btn-anime .external-link {
	background-color: #191919;
}

/* Top Anime Widgets */
.ranking-digest .ranking-header {
	background-color: #191919;
	border: none;
	color: #CCCCCC;
}
.ranking-digest {
	background-color: #1C1C1C;
}
.ranking-digest .ranking-unit .image {
	box-shadow: none;
}
.ranking-digest .ranking-unit .info {
	color: #CCCCCC;
}
/* Add To List */
.page-common a.button_edit.button_edit,
.page-common a.button_add.button_add {
	background-color: transparent;
	border-bottom: none;
	color: #0D80FF;
	text-transform: uppercase;
}
.page-common a.button_edit.button_edit.plantowatch {
	background-color: #C3C3C3;
	color: #000000;
	border-bottom: none;
}
.page-common a.button_add.button_add:hover {
	text-decoration: underline;
}

/* Anime Page: Tabs & Score Panel */
.page-common .breadcrumb,
.page-common #horiznav_nav ul li,
.page-common #horiznav_nav ul li a,
.anime-detail-header-stats .stats-block .score {
	background-color: transparent;
	color: #0D80FF;
}
.anime-detail-header-stats .stats-block {
	background-color: #191919;
	border: none;
	margin-bottom: -8px;
}
.anime-detail-header-stats .stats-block::before {
	background-color: #333333;
	-webkit-box-shadow: none;
}
.anime-detail-header-stats .stats-block .numbers,
.anime-detail-header-stats .stats-block .score::after,
.anime-detail-header-stats .stats-block .information {
	color: #CCCCCC;
}
.anime-detail-header-stats .stats-block .information {
	border-color: #333333;
}
/* Manga Panel */
.page-common .manga-store-preview {
	border: none;
	padding: 0px;
}
.page-common .manga-store-preview-info {
	background-color: #191919;
	padding: 6px 0px 0px 12px;
}
.page-common .manga-store-preview .manga-store-preview-info .manga-store-preview-info-header {
	border-color: #333333;
	color: #FFFFFF;
}
/* Add To My List Panel */
.anime-detail-header-stats .user-status-block,
.user-status-block .form-user-status {
	background-color: #131313;
	border: none;
}
.user-status-block .form-user-score,
.user-status-block .form-user-episode {
	background-color: #131313;
	border-color: #0D80FF70;
	color: #0D80FF;
}
.user-status-block .form-user-episode input[type="text"],
.user-status-block .form-user-episode.completed input[type="text"],
.user-status-block .form-user-episode input[type="text"]:focus,
.user-status-block .form-user-episode.completed input[type="text"]:focus {
	background-color: #131313;
	border: none;
	color: #0D80FF;
}
.page-common .notice_open_public {
	display: none;
}
.page-common .profileRows a,
.page-common .profileRows a.on {
	color: #0D80FF;
	border: none !important;
}
/* Anime Description */
.total_messages,
.page-common .fn-grey2,
.page-common .fn-grey4,
.page-common .fn-grey5,
.page-common h3,
.page-common h5 {
	color: #CCCCCC !important;
	border-color: #333333 !important;
}
.page-common .dark_text,
.page-common h2 {
	color: #FFFFFF !important;
	border-color: #333333 !important;
}
.page-common #profileRows a {
	color: #0D80FF;
	border: none !important;
}
.page-common #profileRows a:hover {
	background-color: transparent;
}
/* Anime Description: Episode Videos */
.anime-slide-block .anime-slide .btn-anime .link .title {
	padding: 15px 5px 2px;
}
/* Stats Hover Popup */
.statistics-info {
	background-color: #161616;
	border: none;
	color: #FFFFFF;
	font-size: 15px;
}
.statistics-info:before {
	filter: invert(1) contrast(0.8);
}
.statistics-info:after {
	display: none;
}
/* Manga Store */
a.left-info-block-manga-store-button {
	color: #000;
}
a.title[data-v-fe02bf62],
a.title[data-v-617a7b38] {
	color: #0D80FF;
}
.slider-wrap[data-v-083e412e],
.slider-wrap[data-v-d2f4bf8a] {
	border-bottom: none;
}
.volume a[data-v-fe02bf62],
.volume a[data-v-617a7b38] {
	color: #CCCCCC;
}
/* Characters & Voice Actors */
.detail-characters-list .left-column table:nth-of-type(1n), 
.detail-characters-list .left-right table:nth-of-type(1n) {
	background-color: #1C1C1C;
}
.page-common div.picSurround img {
	background-color: transparent;
	border: none;
}
/* .page-common td+.borderClass, */
.page-common .ac,
.page-common .ar.fw-n.borderClass,
.detail-characters-list .left-column.divider::after {
	border: none;
}
/* Reviews */
.reviews-horiznav-nav-sort-block,
.page-common .textReadability {
	border: none !important;
}
.page-common .lightLink {
	color: #A5A5A5;
}
.page-common .lightLink:link, 
.page-common .lightLink:visited, 
.page-common .lightLink:active {
	color: #0D80FF;
}
.page-common .button_form {
	background-color: #1C1C1C;
	border-bottom: 1px solid #0D80FF;
}
/* Anime Episodes Page */
.di-t.w100.mb8 {
	background-color: transparent !important;
}
.episode_list .episode-list-header,
.episode_list .episode-list-header td,
.episode_list .episode-list-header td:last-of-type {
	background-color: #191919 !important;
	border: none !important;
	color: #CCCCCC;
}
.episode_list td {
	border: none !important;
}
.episode_list .episode-list-data:nth-of-type(2n+1) td {
	background-color: #191919 !important;
	color: #CCCCCC;
}
.episode_list .episode-list-data:nth-of-type(2n+2) td {
	background-color: #1C1C1C !important;
	color: #CCCCCC;
}
/* Anime Episodes Page: Episode Carousel */
.contents-video-embed .episode-information {
	background-color: #161616;
	margin: 0px 3px 0px 0px;
}
.contents-video-embed .video-anime-slide-block.hori {
	background-color: #191919;
	border-color: #191919;
	padding: 8px 8px 8px 0px;
}
.contents-video-embed .btn-video-anime-slide.hori[data-dir="next"],
.contents-video-embed .btn-video-anime-slide.hori[data-dir="prev"] {
	background-color: #191919;
}
.contents-video-embed .video-anime-slide-block.hori .video-anime-slide .btn-anime,
.contents-video-embed .video-anime-slide-block.hori .video-anime-slide .btn-anime.play .link {
	background-color: #161616;
	padding-right: 0px;
}
.contents-video-embed .video-anime-slide-block.hori .video-anime-slide .btn-anime:hover .link {
	background-color: #111111;
}
.contents-video-embed .video-anime-slide-block.hori .video-anime-slide .btn-anime .link .text {
	color: #0D80FF;
}

/* Anime Stats Page */
.table-recently-updated tr:nth-of-type(1n) td {
	background-color: #1C1C1C;
}
.table-recently-updated tr:nth-of-type(2n) td {
	background-color: #202020;
}
.table-recently-updated .image-member,
.page-common td+td .borderClass {
	border: none !important;
}
.page-common div.updatesBar {
	background-color: #2E51A2;
}

/* Recent Forum Discussions & Articles */
.page-forum .forum_boardrow2 {
	background-color: #181818;
	border: none;
}
.page-forum .forum_boardrow1 {
	background-color: #1C1C1C;
	border: none;
}
.page-forum .forum_postusername a {
	color: #0D80FF !important;
}
.detail-page-featured-article .news-list .news-unit .news-unit-right .text,
.detail-page-featured-article .news-list .news-unit .news-unit-right .information .info,
.news-list .news-unit .information .pv {
	color: #CCCCCC;
}
#forumTopics .js-toggle-watching-topic-button,
#forumTopics .js-ignore-topic-button {
	filter: invert(0.9) hue-rotate(185deg) saturate(4);
}

/* Recommendations */
.btn-detail-recommendations-view-all,
.btn-detail-recommendations-view-all .icon-next {
	background-color: #191919;
}
.btn-detail-recommendations-view-all .text {
	color: #CCCCCC;
}
.anime-slide-block .anime-slide .btn-anime.auto .users {
	background-color: rgba(0,0,0,.7) !important;
	color: #CCCCCC !important;
}

/* Anime Search */
.search_all .content-result .content-left,
.search_all .content-result .list,
.anime-manga-search .genre-link .genre-list {
	border-color: #333333;
}
.auto-recommendations .item,
.anime-manga-search .genre-link .genre-list .genre-name-link:hover {
	background-color: #1C1C1C;
	color: #FFFFFF;
}
.search_all .content-result .list .information .users {
	color: #CCCCCC;
}
/* Anime/Manga Search: Big Searchbar */
.anime-search-form-block .anime-search-form-search .inputtext,
.manga-store .manga-store-search-form-search .inputtext,
.quickadd-content .quickadd-form-search .inputtext {
	background-color: #161616;
	border: 1px solid #161616;
	color: #FFFFFF;
	height: 38px;
}
.anime-search-form-block .anime-search-form-search .inputButton,
.manga-store .manga-store-search-form-search .inputButton,
.quickadd-content .quickadd-form-search .inputButton {
	background-color: #161616;
	border: none;
	color: #A5A5A5;
	height: 40px;
}
.anime-search-form-block,
.manga-store .manga-store-search-form-block {
	margin: 0px auto;
}
/* Anime Search Dropdown */
.page-common .incrementalSearchResultList,
.page-common .incrementalSearchResultList .list,
.page-common .incrementalSearchResultList .list.focus,
.page-common .incrementalSearchResultList .list:hover,
.page-common .incrementalSearchResultList .list.list-bottom.focus,
.page-common .incrementalSearchResultList .list.separator {
	background-color: #191919;
	border: 1px solid #191919;
	color: #FFFFFF;
}
.page-common .incrementalSearchResultList .list .off .info.anime .media-type,
.page-common .incrementalSearchResultList .list .on .info .media-type,
.page-common .incrementalSearchResultList .list .on .info .extra-info {
	color: #CCCCCC;
}
.page-common .incrementalSearchResultList .list .image {
	border: none;
}
/* Top Anime List Tables */
.top-ranking-table {
	width: 1060px;
	margin-left: -10px;
}
.top-ranking-table tr.ranking-list:nth-of-type(2n+1) td {
	background-color: #212121;
	border-left: 1px solid #191919;
}
.top-ranking-table tr.ranking-list td {
	background-color: #191919;
	border-left: 1px solid #212121;
}
.top-ranking-table .table-header td {
	background-color: #2E51A2;
	border: none;
}
.top-ranking-table tr td {
	padding-bottom: 5px;
	padding-top: 5px;
}
.top-ranking-table tr:last-of-type,
.top-ranking-table tr td:first-of-type,
.top-ranking-table tr td:last-of-type,
.top-ranking-table tr td:last-of-type {
	border: none !important;
}
.top-ranking-table .lightLink,
.top-ranking-table .score .text.on {
	color: #FFFFFF;
}
.top-ranking-table .your-score a .text.on,
.top-ranking-table .your-score a:hover .text.on {
	color: #FDDF04;
}
.top-ranking-table .title .link {
	color: #0D80FF;
}
.top-ranking-table .status .btn-addEdit-large.btn-anime-watch-status {
	background-color: transparent;
}
.top-ranking-table .status .btn-addEdit-large.btn-anime-watch-status.completed,
.top-ranking-table .status .btn-addEdit-large.btn-anime-watch-status.watching {
	background-color: #161616;
}

/* Seasonal Anime */
.navi-seasonal {
	background-color: #212121;
}
.navi-seasonal .horiznav-nav-seasonal {
	border-bottom: 1px solid #333333;
	padding-top: 10px;
}
.navi-seasonal .horiznav-nav-seasonal .btn-seasonal .btn-type,
.navi-seasonal .horiznav_nav ul li a.navtab,
.navi-seasonal .horiznav_nav ul li a {
	background-color: #191919;
	border: none;
	color: #CCCCCC;
}
.seasonal-anime-list .anime-header {
	background-color: #2E51A2;
	border: none;
}
.navi-seasonal.fixed::after {
	filter: invert(1) contrast(0.74);
}
.seasonal-anime-list .seasonal-anime,
.seasonal-anime-list .seasonal-anime .prodsrc,
.seasonal-anime-list .seasonal-anime .genres,
.seasonal-anime-list .seasonal-anime .image {
	background-color: #191919;
	border: none;
}
.seasonal-anime-list .seasonal-anime .information .info,
.seasonal-anime-list .seasonal-anime .information .scormem,
.seasonal-anime-list .seasonal-anime .information {
	background-color: #161616;
	border: none
