style.css:
@-moz-document domain("userstyles.org") {
/*Dark Scrollbar*/
::-webkit-scrollbar { width: 10px; }
::-webkit-scrollbar-track { background: #f3000000; }
::-webkit-scrollbar-thumb { background: #222; }
::-webkit-scrollbar-thumb:hover { background: #252525; }

body, html,

#nav-view-responsive,

tbody 
{
  background: #3d3d3f;
}

#notes_textarea,

.navigation,

.customize_button, #style_installed_button, .white_button,
.css_button, #send_feedback_button, .see_more,

tbody tr:nth-child(odd),

.choose_editor .boxesContainer .box,

.us-stylecard--long,
.us-stylecard--short,

.div-arrow,

#overlay-info
{
    background: #363639;
}

#header,

.navigation .links-box .navigation-link:hover,
#stylish-code,

#footer,

thead, .author-styles,

input, textarea, select,

.white_button:hover
{
    background: #2e2e2f !important;
}

.choose_editor
{
    background: #141415db !important;
}

td input,

.header-search .search_input *
{
  background-color: #0000 !important;
}

.header-search .search_input
{
  background: #ccc5 !important;
}

body, h1, h2, h3, h4, h5, h6, input, textarea, select,

#headline, #share_div, #information_div *, #applies_link, .text_style_page,

#notes_textarea,

.navigation *,
.white_button, #send_feedback_button,

.choose_editor .boxesContainer .box .subtitle,

.us-stylecard--long .us-title *,
.us-stylecard--short a, .us-stylecard--short a:visited,

#pages a, #pages a:active, #pages a:visited
{
  color: #ccc !important;
}

*
{
  border: none !important;
  box-shadow: none !important;
}

#top_android_button,
.choose_editor .boxesContainer .box:nth-of-type(2),

.hamburger
{
  display: none !important;
}

.navigation
{
  left: -12rem !important;
  padding: 0;  
  
  opacity: .4;
  
  transition: all 200ms;
}
.navigation:hover{
  left: 0rem !important;
  
  opacity: 1;
  
  transition: all 200ms;
}

.choose_editor .boxesContainer .box:first-child
{
  margin: 0
}
.choose_editor .boxesContainer
{
  margin-top: 100px; 
}

.navigation .links-box .navigation-link,

tbody tr td
{
  padding-left: 10px
}
.navigation .navigation-category
{
    text-align: center;
}

tr > td.obsolete
{
  opacity: .1 !important;
}

.choose_editor .main_title
{
  font-size: 0pt;
}
.choose_editor .main_title:after 
{
  font-size: 30px;
  content: "Let's create a style!";
}

#header, .navigation .links-box .navigation-link:hover, #stylish-code, #footer, thead, .author-styles, input, textarea, select, .white_button:hover
{
  max-width: 100%;
}

textarea,
#notes_textarea
{
  padding: 10px;
}

#footer .logo,

.navigation .links-box .navigation-link .logo-navigation-link,

.init_loader img
{
    filter: invert(1) hue-rotate(195deg) saturate(4) brightness(1);
    mix-blend-mode: color-dodge;
}
}

@-moz-document domain("forum.userstyles.org") {
.Item.Read,

.Button {
  background: #363639;
}

#Head {
  background: #2e2e2f !important;
}

input {
  background-color: #12121354 !important;
}

.FormWrapper {
  background: #0000;
}

.Button {
  color: #ccc
}

#Body,
#Head .Row {
  width: 80%;
}
#Body {
  margin: 0px auto !important;
}

.Row {
  width: 100%;
}

.Sprite16,
.ReactSprite,
.OptionsTitle {
  filter: invert();
}
}
