style.css:
/* i really want this to be global */
:root {
	--color-darkness: /*[[darkness]]*/;
}


body {
	background-color: var(--color-darkness);
}
.navbar.has-background,
.app-container.has-background .namespace-container,
.navbar.main-theme,
.app-container .namespace-container {
	background: rgba(33, 33, 44, .2);
	backdrop-filter: blur(5px);
}


.kanban .bucket {
	background: rgba(33, 33, 44, .2);
}

.tasks .task {
	background: rgba(33, 33, 44, .3);
	border-color: rgba(33, 33, 44, .8);
	backdrop-filter: blur(5px);
}
.tasks .task:hover {
	background: rgba(33, 33, 44, .8);
	border-color: rgba(33, 33, 44, .8);
}
.tasks .task:hover a {
	color: #eee;
}

.app-container.has-background .tasks {
	background: rgba(42, 42, 84, .1);
}

.tasks .task span:not(.tag) a {
	color: #ccc;
}

.title,
.list-title .icon,
.content h1,
.content h2,
.content h3,
.content h4,
.content h5,
.content h6,
.app-container .namespace-container .menu .menu-list a {
	color: #eee;
}

.has-background .title,
.has-background .list-title .icon,
.app-container.has-background .namespace-container .menu .menu-list a {
	color: #fff;
	text-shadow: 1px 1px rgba(33, 33, 44, .42);
}


.switch-view {
	background: rgba(33, 33, 44, .42);
}


.switch-view a {
	color: #eee;
}

.button,
.button.is-success,
.box,
.card,
.namespaces-list .namespace .lists .list,
.switch-view,
.router-link-exact-active.router-link-active.is-active,
.kanban .bucket .tasks .task {
	box-shadow: 2px 2px 2px rgba(33, 33, 44, .2);
}

.kanban .bucket .tasks .task {
	background: rgba(244, 244, 355, .8);
}

.app-container.has-background .kanban .tasks {
	padding-top: 8px
}
