style.css:
@-moz-document url-prefix("https://app.plex.tv/"), url-prefix("http://app.plex.tv/"), regexp("https?://.*:32400/.*"), regexp(".*:32400/.*") {
/* Changing the "--accent-colour" here will
let you choose the accent colour used almost 
everywhere in Plex Web UI If you choose too
bright of colour, you will miss some labels :( */
 :root {
    --accent-colour: #31d0f7;
    /* The same colour in RGB */
    /* This is used for transcode buffer display */
    --accent-rgb: 49,208,247;
}



 .conversion-queue-empty-region a, .remote-access-upload-form .remote-access-upload-speed-header, .nav-pills > li.active > a, .status-conversion-btn, .artwork-file-container a, .artwork-file-container a:hover, .upload-url-btn, .upload-url-btn:hover, .wizard-block .selected, .wizard-block-selected, .edit-section-modal .wizard-blocks-group > .wizard-block.selected, .edit-section-modal .wizard-blocks-group > .wizard-block.selected:hover, .help-block a, span[class*="NavBarActivityMenuStatusItem-statusButtonIndicator-"], a[class*="PageHeaderTabButton-isSelected-"], span[class*="NavBarActivityButton-label-"], div[class*="ServerSettingsServerMenuItem-friendlyName"], svg[id*="plex-icon-selected"], .btn-gray.selected, div[class*="SourceSidebarServerHeader-title-"], div[class*="SidebarListLink-selectedIcon-"], a[class*="Link-isSelected-"], button[class*="Link-isSelected-"], a[class*="Link-primary-"], button[class*="Link-primary-"], .card-announcement a, .upload-back-btn, .cancel-url-btn, .submit-url-btn,button[class*="IconButton-isActive-"],button[class*="IconButton-"]:hover, div[class*="MultiSelectPageHeader-selectedTitle-"], div[class*="PageHeaderMultiselectActions-count"]  {
    color:var(--accent-colour);
}

 a[class*="check-for-updates-btn"], a[class*=" active"], button[class*="Button-primary"], .badge, div[class*="SessionTile-playProgressPercent-"], div[class*="MetadataPosterCard-progressBar-"], div[class*="PosterCardOverlayProgress-progressBar-"], .keyboard-shortcuts-keys-key, div[class*="VolumeSlider-fill-"] {
    background-color: var(--accent-colour)!important;
}
p {
    margin: 0 0 0 0 !important;
}
div[class*="PageHeaderMultiselectActions-container-"]{
 	border: 2px solid var(--accent-colour);   
}
button[class*="PosterCardLink-hoveredLink"]{
    box-shadow:0 0 0 1px var(--accent-colour), 0 0 4px rgba(0,0,0,.3);
}
div[class*="MetadataPosterCard-legacyUnwatchedEpisodesBadge-"], div[class*="MetadataPosterCardOverlay-unplayedBadge-"], .btn {
    background-color: var(--accent-colour) !important;
    color:#fff;
}
div[class*="MultiSelectPageHeader-multiSelectPageHeader-"] {
 	box-shadow: 0 2px 0 0 var(--accent-colour);   
}
div[class*="MetadataPosterCard-legacyUnwatchedTag-"], div[class*="MetadataPosterCardOverlay-unplayedTag"] {
    position:absolute;
    top:-14px;
    right:-14px;
    width:28px;
    height:28px;
    background-color:var(--accent-colour);
    transform:rotate(45deg)
}
div[class*="SourceSidebarLink-isSelected-"] {
    background-color:hsla(0, 0%, 100%, .07);
    box-shadow:inset 4px 0 0 0 var(--accent-colour) !important;
}
div[class*="MetadataPosterCardFace-isSelected-"], button[class*="MetadataPosterCard-selectedLink-"], button[class*="MetadataPosterCard-hoveredSelectedLink-"]  {
    box-shadow: 0 0 0 2px var(--accent-colour), 0 0 4px rgba(0,0,0,.3) !important;
}
div[class*="MetadataPosterCardOverlay-selectCircle-"], div[class*="SelectButton-selectCircle-"], div[class*="SelectButton-isSelected-"]{
	border-color: var(--accent-rgb) !important;
    background-color: var(--accent-colour) !important;
}

a[class*="PosterCardLink-hoveredLink-"] {
    border-radius: 4px;
    background-color: rgba(30, 30, 30, .6);
    box-shadow: 0 0 0 1px var(--accent-colour), 0 0 4px rgba(0, 0, 0, .1);
}
.edit-lock-addon.locked {
    background-color:var(--accent-colour);
    border-color:var(--accent-colour);
}
.media-poster-container.selected {
    box-shadow:0 0 4px rgba(0, 0, 0, .3), inset 0 0 0 3px var(--accent-colour) !important;
}
span[class*="NavBarActivityButton-isHighlighted-"], span[class*="NavBarActivityButton-activityIcon-"] {
    background-color:var(--accent-colour) !important;
    color:#000 !important;
}
div[class*="SessionTile-transcodeProgressPercent-"] {
    background-color:rgba(var(--accent-rgb), 0.5)!important;
}
div[class*="MetadataPosterCardFace-face-"]:hover {
    box-shadow: 0 0 0 1px var(--accent-colour), 0 0 4px rgba(0, 0, 0, .3);
}
svg[class*="PlayButton-playCircle-"]:hover {
    border-color:var(--accent-colour) !important;
    background-color:var(--accent-colour) !important;
}
button[class*="ActionButton-labeledActionButton-"] {
    background-color:var(--accent-colour);
    color:#fff;
}
.media-poster-container.selected .media-poster {
    box-shadow:0 0 4px rgba(0, 0, 0, .3), inset 0 0 0 3px var(--accent-colour);
}
.media-poster-container.selected, .media-poster::before {
    border-color: var(--accent-colour) var(--accent-colour) transparent transparent !important;
}
.media-poster.selected, .media-poster::after {
    color:#fff!important;
}
.media-poster:hover {
    box-shadow: 0 0 4px rgba(0, 0, 0, .3), inset 0 0 0 2px var(--accent-colour)!important;
}
span[class*="DisclosureArrow-isSelected-"] {
    border-color:var(--accent-colour);
}
svg[class*="PlayPauseOverlay-playCircle-"], button[class*="PlayPauseOverlay-playButton"]:hover svg[class*="PlayButton-playCircle"] {
 	border-color:var(--accent-colour);
    background-color:var(--accent-colour);
}
div[class*="SeekBar-seekBarFill-"] {
 	background-color:var(--accent-colour)!important;   
}
div[class*="SeekBar-seekBarBuffer-"] {
    background-color:rgba(var(--accent-rgb), 0.5)!important;
}
span[class*="NavBarActivityButton-label-"] {
    color: var(--accent-colour) !important;
}
div[class*="MetadataPosterCardScanStatus-spinner-"] {
    border-color: var(--accent-colour) transparent transparent var(--accent-colour);
}
}
