style.css:
@-moz-document domain("subsplease.org") {
/*Dark Scrollbar*/
::-webkit-scrollbar { width: 10px; }
::-webkit-scrollbar-track { background: #fff0; }
::-webkit-scrollbar-thumb { background: #222; }
::-webkit-scrollbar-thumb:hover { background: #252525; }

body, html,

#searchdiv
{
  background: #3d3d3f;
}

.post-wrapper-single .post-wrapper-hentry,

.sidebar,

.anime1
{
    background: #363639;
}

.site-header,

.releases-header form input
{
    background: #2e2e2f !important;
}

.badge,

.anime0:hover, .anime1:hover,

.col-6
{
    background: #12121354 !important;
}

.anime0,
div.footer,

div.header
{
  background: #0000;
  transition: all .1s;
}

.time-new-badge
{
    background-color: #0a7642;
}

body, h1, h2, h3, h4, h5, h6,

a, a:visited,

.site-title a, .site-title a:visited,
.site-description,
.sf-menu > li > a, .sf-menu > li > a:visited,

.release-item a,
.badge,

.schedule-widget-show a, .schedule-widget-show a:visited,

.all-schedule-show a,
.all-shows-link a,

.toggle-menu-wrapper a, .toggle-menu-wrapper a:visited,

.search,

.releases-header form input
{
  color: #ccc !important;
}

.default
{
  color: #999;
}

*
{
  border: none !important;
  box-shadow: none !important;
}

tr.animeColumn th,

#searchdiv,
div.header
{
  border-bottom: 1px solid #ccc !important;
}
div.footer
{
  border-top: 1px solid #ccc !important;
}
#searchdiv
{
  border-left: 1px solid #ccc !important;
}
#searchdiv
{
  border-top: 1px solid #3d3d3f !important;
}

input#search.search
{
  border: 1px solid #ccc !important;
}
table th, table td
{
  border-bottom: 1px solid #ccc2 !important;
}

.schedule-widget-time img 
{
    filter: invert(1) hue-rotate(180deg) saturate(2) brightness(.8);
}
}
