style.css:
@-moz-document domain("suggestmemovie.com") {
/*V1_201008*/
:root {
    --minr: /*[[min]]*/;
    --scnr: /*[[scn]]*/;
    --acnr: /*[[acn]]*/;
    --txnr: /*[[txn]]*/;
/*[[rgb]]*/

}
/*min*/
.btn-info, .btn-info:hover, body, .form-control, .btn-primary:hover, .gsc-input-box, input, .gsc-webResult.gsc-result:hover {
    background: var(--minr)
}
/*minimp*/
.bg-dark {
    background: var(--minr)!important
}
/*txn*/
.btn-success, .btn-success:hover, .btn-secondary, .btn-secondary:hover, .btn-info, .btn-info:hover, body, .badge-secondary, a:hover, .form-control, .form-control:focus, .btn-primary, .btn-primary:hover, input, .gsc-result-info, .gs-webResult:not(.gs-no-results-result):not(.gs-error-result) .gs-snippet, .gs-fileFormatType, .gsc-results .gsc-cursor-box .gsc-cursor-page, .gs-webResult .gs-snippet, .gs-fileFormatType {
    color: var(--txnr)
}
/*txnimp*/
.text-muted {
    color: var(--txnr)!important
}
/*acnbcg*/
.btn-success, .btn-secondary:hover, .badge-secondary, .btn-primary, .gsc-search-button-v2, .gsc-search-button-v2:hover, .gsc-search-button-v2:focus, .gs-no-results-result .gs-snippet, .gs-error-result .gs-snippet {
    background: var(--acnr);
     animation: bcgrgb 30s infinite
}
/*acntxn*/
a, .gsst_a .gscb_a, .gs-webResult.gs-result a.gs-title:link, .gs-webResult.gs-result a.gs-title:link b, .gs-imageResult a.gs-title:link, .gs-imageResult a.gs-title:link b, .gsc-results .gsc-cursor-box .gsc-cursor-current-page, .gcsc-find-more-on-google, .gcsc-find-more-on-google-magnifier {
    color: var(--acnr);
    fill: var(--acnr);
    animation: txnrgb 30s infinite
}
/*acnbrd*/
.form-control, .form-control:focus, .gsc-search-button-v2, .gsc-search-button-v2:hover, .gsc-search-button-v2:focus {
    border-color: var(--acnr);
    animation: borderrgb 30s infinite
}
/*scn*/
.btn-success:hover, .btn-secondary, .progress, .card, .list-group-item, .form-control:focus, .gsc-control-cse, .gsc-webResult.gsc-result, .gsc-results .gsc-imageResult, .gsc-results .gsc-cursor-box .gsc-cursor-current-page, .gsc-results .gsc-cursor-box .gsc-cursor-page {
    background: var(--scnr)
}
/*scnimp*/
.row.my-3.mx-0.align-items-center.py-4.px-2.rounded {
    background: var(--scnr)!important
}
/*rbrd*/
.btn-success, .btn-success:hover, .btn-secondary, .btn-secondary:hover, .btn-info, .btn-info:hover, .img-thumbnail, .card, .border-top, .btn-primary, .btn-primary:hover, .gsc-control-cse, input.gsc-input, .gsc-input-box, .gsc-input-box-hover, .gsc-input-box-focus, .gsc-search-button-v2, .gsc-search-button-v2:hover, .gsc-webResult.gsc-result, .gsc-results .gsc-imageResult, .gsc-above-wrapper-area, .gsc-results .gsc-cursor-box .gsc-cursor-current-page, .gsc-results .gsc-cursor-box .gsc-cursor-page, .gs-no-results-result .gs-snippet, .gs-error-result .gs-snippet {
    border: transparent!important
}
/*br*/
.btn, .progress, .row.my-3.mx-0.align-items-center.py-4.px-2.roundedm, .img-thumbnail, .card, .badge, .card>.list-group:last-child .list-group-item:last-child, .form-control, .gsc-search-button-v2 {
border-radius: 0!important
}
/*rbxs*/
.form-control:focus {
    box-shadow: none
}
/*aim*/
.btn, a, .gsc-webResult.gsc-result {
    transition: .3s ease
}

}
