style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wikipedia.org") {

#p-Mitmachen, #p-navigation, #p-interaction, #p-logo, #siteNotice {
    display: none !important;
}

#p-lang {
    position: absolute !important;
    top: 2em;
}

#p-lang ul>li {
    display: none;
}

div.pBody>ul>li.interwiki-de {
    display: list-item !important;
    list-style-image: url(http://www.clickfire.com/images/flags/germany.gif) !important;
    list-style-position: inside !important;
}

div.pBody>ul>li.interwiki-en {
    display: list-item !important;
    list-style-image: url(http://www.clickfire.com/images/flags/u-k.gif) !important;
    list-style-position: inside !important;
}

div.pBody>ul>li.interwiki-fr {
    display: list-item !important;
    list-style-image: url(http://www.clickfire.com/images/flags/france.gif) !important;
    list-style-position: inside !important;
}

div.pBody>ul>li.interwiki-es {
    display: list-item !important;
    list-style-image: url(http://www.clickfire.com/images/flags/spain.gif) !important;
    list-style-position: inside !important;
}

div.pBody>ul>li.interwiki-it {
    display: list-item !important;
    list-style-image: url(http://www.clickfire.com/images/flags/italy.gif) !important;
    list-style-position: inside !important;
}

}
