body {
    font-family:'Roboto', sans-serif;
    font-size: 12pt;
}

body, p, li {
    line-height: 1.6;
}

a,
a:hover {
    color: #09f;
}

.post {
    padding-top: 0;
}

.content-body {
    max-width: 8in;
}

.header {
    line-height: 2;
}

.label-transparent {
    max-width: none;
}

.post blockquote,
.post blockquote p {
  font-size: inherit;
}

div.well.sidebar-nav {
  background-color: #1e1e1e !important;
  color: #d4d4d4 !important;
}

div.well.clearfix.post {
  background-color: #1e1e1e !important;
  color: #d4d4d4 !important;
}

.hero-unit.posts-filler span {
  background-color: #1e1e1e;
  color: #d4d4d4;
}

code {
  background-color: #555;
  color: #d4d4d4;
}

.content-body p {
  font-size: inherit;
  line-height: inherit;
}
}
