style.css:
@-moz-document url("https://fwkzt.com/forum/"), url-prefix("https://fwkzt.com") {

#af-wrapper .forum, #af-wrapper .topic, #af-wrapper .member, #af-wrapper .unread-topic, #af-wrapper .unapproved-topic {
border-bottom: #00000000
}

#af-wrapper .content-container, #af-wrapper .report-element, #af-wrapper #statistics, #af-wrapper .post-wrapper, #af-wrapper #profile-header .background-contrast, #af-wrapper #poll-results .poll-result-bar, #af-wrapper #memberslist-filter {


    background-color: #000000a8 !important;


    background: blur(30px);


}


#af-wrapper .forum-poster-avatar img.avatar, #af-wrapper .topic-poster-avatar img.avatar {


    animation:pulsesmall 1s infinite alternate;


}


#af-wrapper .content-element:nth-child(even), #af-wrapper .topic-sticky, #af-wrapper .topic-sticky .topic-poster, #af-wrapper .post-element, #af-wrapper .editor-element, #af-wrapper #statistics-online-users, #af-wrapper #profile-layer, #af-wrapper .spoiler .spoiler-head, #af-wrapper .report-content, #af-wrapper #poll-panel, #af-wrapper #profile-content {


    background-color: #00000000 !important;


}


#af-wrapper .button-normal, #af-wrapper .title-element, #af-wrapper #forum-header, #af-wrapper #profile-header .background-avatar, #af-wrapper #profile-navigation, #af-wrapper input[type="radio"]:checked:before {


    background-color: #000000a8 !important;


    background: blur(30px);


}


body {


    width: 100%;


    height: 100%;


    background: #000000!important;


    color: white!important;


    font-family:'Montserrat', sans-serif;


}


.header-img-1 {


    height: 0px;


    width: 0px;


}


.app {


    background: #17171700;


    margin: -2em 0 -3em 0;


    padding: 5em 0 5em 0;


}


.navbar {


    animation:pulse 1s infinite alternate;


    display: block!important;


    background: #0b0500;


    background: blur(50px);


    position: relative;


    width: 100%;


    margin-left: auto;


}


.navbar a {


    float: left;


    height: auto;


    padding: 1rem;


    color: white!important;


    border-left: 0px solid #00000000;


}


#af-wrapper, #af-wrapper #forum-breadcrumbs a:hover, #af-wrapper .main-title {


    color: #ffffff !important;


}


#af-wrapper, #af-wrapper #forum-breadcrumbs a:hover, #af-wrapper .main-title {


    color: #ffffff !important;


}


#af-wrapper {


    font-family: Verdana, Tahoma, sans-serif;


    font-size: 13px;


    color: #44400000;


    line-height: 1.65;


    text-shadow: none;


    align-content: center;


    padding-left: 10rem;


    padding-right: 10rem;


}


#af-wrapper input, #af-wrapper .forum-poster, #af-wrapper .topic-poster, #af-wrapper .member-last-seen, #af-wrapper .editor-element, #af-wrapper .content-container, #af-wrapper .forum-post-header, #af-wrapper #statistics-body, #af-wrapper .statistics-element, #af-wrapper #statistics-online-users, #af-wrapper .editor-row, #af-wrapper .editor-row-subject, #af-wrapper .signature, #af-wrapper .post-element, #af-wrapper .post-wrapper, #af-wrapper .forum-subforums, #af-wrapper .uploaded-file img, #af-wrapper .action-panel-option, #af-wrapper .topic-sticky .topic-poster, #af-wrapper #profile-layer, #af-wrapper #profile-layer .pages-and-menu:first-of-type, #af-wrapper #profile-content, #af-wrapper #profile-content .profile-row, #af-wrapper .history-element, #af-wrapper #memberslist-filter, #af-wrapper .content-element, #af-wrapper .ad-forum, #af-wrapper .ad-topic, #af-wrapper .spoiler, #af-wrapper .spoiler .spoiler-body, #af-wrapper .report-element, #af-wrapper .report-source, #af-wrapper .report-content, #af-wrapper .report-actions, #af-wrapper #profile-content .profile-section-header, #af-wrapper #poll-options, #af-wrapper #poll-panel, #af-wrapper #poll-panel #poll-headline, #af-wrapper #poll-results .poll-result-bar, #af-wrapper #usergroups-filter {


    background: #00000000!important


}


.header {


    height: 0px;


}


#af-wrapper #forum-search .search-icon {


    padding: 0;


    width: 0px;


    height: 44px;


    line-height: 44px;


    text-align: center;


}


#af-wrapper #forum-search {


    float: right;


    width: 238px;


    height: 44px;


    line-height: 44px;


    margin: 0;


    padding: 0;


    box-sizing: border-box;


    background-color: #fff0;


    color: #444;


    border: none;


    border-radius: 3px;


    cursor: text;


}


#af-wrapper .main-title {


    font-size: 0px;


    border: none;


    margin: 0;


    padding: 0px;


    font-weight: normal;


    color: #444;


    display: block;


    line-height: 0px;


}


/*=============animations===============*/


@keyframes pulse {


    from {


        box-shadow:0px 0px 10px 2px #6d0000;


    }


    to {


        box-shadow:0px 0px 28px 9px #6d0000;


    }


}


@keyframes pulsesmall {


    from {


        box-shadow:0px 0px 4px 1px #6d0000;


    }


    to {


        box-shadow:0px 0px 15px 8px #6d0000;


    }


}


@keyframes pulsesmall2 {


    from {


        box-shadow:0px 0px 0.5px 1px #6d0000;


    }


    to {


        box-shadow:0px 0px 11px 4px #6d0000;


    }


}


/*============================*/


.logout-btn {


    float: right!important;


    background: #4b0100;


}


footer {


    background: #0100009c;


    padding: 2rem;


    margin-top: 2rem;


}
.navbar a:hover {
    background: #0b0500;

}
.navbar a:active {
    background: #0b0500;

}
.dropdown .menu{
    background: #0b0500;

}
.navbar a:hover, .navbar .dropdown:hover, .dropdown:active {
    background: #0b0500;

}
#panel{
 background: url(https://i.imgur.com/reqE1hE.jpg)!important;
  background-repeat: no-repeat!important;
  background-attachment: fixed!important;
  background-position: absolute!important;

}

#af-wrapper .unread, #af-wrapper .fas {
    color: #980000;
}
.server-block h3:first-child {
    width: 100%;
    background: #a40200;
    padding: 1rem;
}
.server-block {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    background: #222;
    border: 1px solid #a40200;
    position: relative;
    width: 50%;
}
.server-block {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    background: #2222228c;
    border: 1px solid #a40200;
    position: relative;
    width: 50%;
}
#discord-title{
	background: #a40200
}
.dropdown .menu {
    background: #0b0500c7!important;
}
#table{
background:#2222228c;
}
.box {
    background: #2222228c;
    padding: 1rem;
}
.tribe-events .tribe-events-header {
    align-items: center;
    background-color: #000000ab;
    display: flex;
    flex-direction: row-reverse;
    flex-wrap: wrap;
    justify-content: space-between;
    margin: 0 -19.5px;
    padding: 0 19.5px 16px;
    position: relative;
}
#af-wrapper .user-online .avatar  {
    box-shadow: 0px 0px 5px #82f338;
    animation: pulsesmall3 1s infinite alternate;
}
#af-wrapper .post-author img.avatar {
    animation: pulsesmall3 1s infinite alternate;
    border: 0px;

}
@keyframes pulsesmall3 {
    from {
        box-shadow:0px 0px 0.5px 1px #82f338;
    }
    to {
        box-shadow:0px 0px 11px 4px #82f338;
    }
}
#af-wrapper .button-normal, #af-wrapper .title-element, #af-wrapper #forum-header, #af-wrapper #profile-header .background-avatar, #af-wrapper #profile-navigation, #af-wrapper input[type="radio"]:checked:before 
{
box-shadow: 5px 5px 2px 0px #4f0000;

}
#af-wrapper input[type="radio"]:focus, #af-wrapper input[type="checkbox"]:focus, #af-wrapper #profile-header {
    border-color: #4f0000 !important;
}
table {
    display: table!important;
    background: #171717b8;
    margin: 1rem;
    animation: fadein 1s;
}

}

@-moz-document url-prefix("https://fwkzt.com/login/") {
body {
 background: url(https://i.imgur.com/4vEZhPG.jpg)!important;
}
.login form {
    background: #333333a6!important;
