}
.btn-outline-warning.disabled,
.btn-outline-warning:disabled {
 color:var(--warning);
}
.btn-outline-warning.active,
.btn-outline-warning:active,
.show>.btn-outline-warning.dropdown-toggle {
 color:var(--inverse-text);
 background-color:var(--warning);
 border-color:var(--warning)
}
.btn-outline-danger {
