 background-color:var(--bg-1)
}
.custom-file-control {
 color:var(--bg-3);
 background-color:var(--main-bg);
 border:1px solid var(--tint-1);
}
.custom-file-control:before {
 color:var(--bg-3);
 background-color:var(--bg-1);
 border:1px solid var(--tint-1);
}

/*------NAVIGATION------*/

.nav-link.disabled {
 color:var(--bg-3);
 cursor:not-allowed
}
.nav-tabs {
 border-bottom:1px solid var(--bg-2)
}
