style.css:
@-moz-document regexp(".*viz.com.*") {
.bg-off-white {
	background: transparent!important;
}
body{
	background-color: rgb(16, 16, 16)!important;
	color: rgb(191, 191, 191)!important;
}
#section2{
	background-image: url(https://i.imgur.com/eOm0Fy0.png)!important;
}
.bg-white, .color-off-black, .reader-icon{
	background: transparent;
	color: rgb(191,191,191);
}
.bg-trans-white, .flex-items-center{
	background: rgb(20,20,20);
}
.o_utility-nav a, .o_primary-nav-link, .page_slider_label.center{
	color: rgb(191,191,191);
}
.bg-lighter-gray, #reader_wrapper.not-mobile #reader_bottom_container, #reader_header{
	background: rgb(16,16,16);
}
hr{
	border: none;
}
}
