	.input-content.paper-input-container > textarea,
	.input-content.paper-input-container > iron-autogrow-textarea,
	.input-content.paper-input-container > .paper-input-input,
	#title.ytd-rich-shelf-renderer,
	#content-text.ytd-backstage-post-renderer,
	ytd-comment-action-buttons-renderer {
		color: #fff;
	}
	#author-name.yt-live-chat-author-chip {
		color: var(--paper-cyan-a200);
	}
	#upnext.ytd-compact-autoplay-renderer {
		color: var(--paper-cyan-a200);
	}

	ytd-browse[page-subtype=channels] {
		background: #f1f1f100;
	}

	#channel-header.ytd-c4-tabbed-header-renderer {
		background-color: #f9f9f900;
	}
	#channel-container.ytd-c4-tabbed-header-renderer,
	#tabs-container.ytd-c4-tabbed-header-renderer {
		background-color: #8d3ed58a;
		background-image: linear-gradient(100deg, #00fffc7d 0%, #ff00b27d 100%);
	}

	#tabs-inner-container.ytd-c4-tabbed-header-renderer {
		background-color: #f9f9f900;
	}
	paper-tabs.ytd-c4-tabbed-header-renderer {
		color: #aeaeae;
	}

	ytd-mini-guide-renderer {
		background-color: #fff0;
	}
	ytd-mini-guide-entry-renderer {
		background-color: #70636300;
	}
	ytd-mini-guide-entry-renderer[active] .guide-icon.ytd-mini-guide-entry-renderer {
		color: #00e4ff;
	}


	ytd-post-renderer[uses-compact-lockup] {
		border-top: 2px solid #0ff;
		background-color: #76518d8c;
	}



	ytd-button-renderer.style-primary[is-paper-button] {
		background-color: #7608a4;
		color: #ffffff;
	}

	.more-button.ytd-backstage-post-renderer,
	.less-button.ytd-backstage-post-renderer {
		color: #838383;
	}

	ytd-comment-action-buttons-renderer.ytd-backstage-post-renderer ytd-button-renderer.ytd-comment-action-buttons-renderer[is-icon-button].style-default-active.ytd-comment-action-buttons-renderer,
	ytd-comment-action-buttons-renderer.ytd-backstage-post-renderer ytd-toggle-button-renderer.ytd-comment-action-buttons-renderer[is-icon-button].style-default-active.ytd-comment-action-buttons-renderer {
		color: #00b9f8;
	}

	ytd-settings-sidebar-renderer {
		background-color: #f1f1f100;
	}
	#title.ytd-settings-sidebar-renderer {
		color: #00e2ff;
	}
	ytd-page-introduction-renderer,
	#title.ytd-item-section-header-renderer,
	#title.ytd-settings-options-renderer,
	#title.ytd-settings-switch-renderer,
	#label.ytd-settings-checkbox-renderer {
		color: #fff
	}
	/*light end*/
	a:link {
		color: /*[[link]]*/
	}
	a:visited {
		color: /*[[visited]]*/
		!important
	}
	a:hover {
		color: #fff!important
	}
	a:active {
		color: #00ffed!important;
		text-shadow: 0 0 8px
	}


	#account-name.ytd-active-account-header-renderer {
		/*name*/
		text-shadow: 0 0 8px var(--paper-purple-a200);
	}

	yt-icon.ytd-topbar-logo-renderer,
	[id="logo"][class="style-scope ytd-masthead"] yt-icon.ytd-logo,
	tp-yt-app-drawer[opened] [class="style-scope ytd-logo"][id="logo-icon"] {
		/*logo*/
		animation-name: logoyad;
		filter: none;
		animation-duration: 0.8s;
		animation-iteration-count: 1;
		fill-opacity: 0;
		background: url("/*[[logoniyad]]*/") center center/100% no-repeat;
		max-width: 70px;
		max-height: 100px;
	}


	@keyframes logoyad {
		0% {
			transform: scalex(0)
		}
		25% {
			transform: scaleY(0.1)
		}
		50% {
			transform: scalex(0.7)
		}
		75% {
			transform: scaleY(0.15)
		}
	}
	.ytd-topbar-logo-renderer {
		animation: yadlogo;
		animation-duration: 10s;
		animation-iteration-count: infinite;
		animation-fill-mode: both;
		animation-direction: alternate;
	}
	@keyframes none
	{
		0% {
			filter: hue-rotate(0deg);
		}
		100% {
			filter: hue-rotate(360deg) saturate(6.1);
		}
	}
	.ytd-topbar-logo-renderer:hover {
		animation-name: rubberBand;
		animation-duration: 1s;
		animation-delay: 10s;
		animation-fill-mode: both;
		animation-iteration-count: 5;
	}
	@keyframes yadlogo2 {
		0% {
			-webkit-transform: scaleY(2);
			transform: scaleY(2);
			filter: blur(4px);
			opacity: 0;
		}
		100% {
			-webkit-transform: scaleY(1);
			transform: scaleY(1);
			filter: blur(0);
			opacity: 1;
		}
	}
	ytd-topbar-logo-renderer:not(:active) {
		animation: yadlogo2;
		animation-duration: 1s;
		animation-timing-function: ease;
		animation-iteration-count: 1;
		animation-direction: normal;
		animation-fill-mode: none;
	}
	@keyframes rubberBand {
		0% {
			transform: scale3d(1, 1, 1);
		}
		30% {
			transform: scale3d(1.25, 0.75, 1);
		}
		40% {
			transform: scale3d(0.75, 1.25, 1);
		}
		50% {
			transform: scale3d(1.15, 0.85, 1);
		}
		65% {
			transform: scale3d(.95, 1.05, 1);
		}
		75% {
			transform: scale3d(1.05, .95, 1);
		}
		100% {
			transform: scale3d(1, 1, 1);
		}
	}

	#country-code.ytd-topbar-logo-renderer,
	ytd-topbar-logo-renderer[is-logo-updated] #country-code.ytd-topbar-logo-renderer {
		color: var(--yt-spec-text-secondary);
		margin: 10px 0 0px 3px!important
	}

	ytd-thumbnail #thumbnail.ytd-thumbnail:not(:active) {
		animation: yadpoof;
		animation-duration: 0.3s;
		animation-iteration-count: 1;
		transition: opacity 0.5s;
	}

	@keyframes yadpoof {
		0% {
			transform: scale(1);
			filter: blur(0px);
		}
		100% {
			transform: scale(3.5);
			opacity: 0;
			filter: blur(50px);
		}
	}
	button.yt-icon-button > yt-icon:active {
		animation: yadpooficon;
		animation-duration: 0.1s;
		animation-iteration-count: 1;
		transition: opacity 0.5s;
	}
	@keyframes yadpooficon {
		0% {
			transform: scale(1.5);
			filter: blur(1px);
		}
		50% {
			transform: scale(5.5);
			opacity: 0.1;
			filter: blur(5px);
		}
		90% {
			transform: scale(15.5);
			opacity: 0;
			filter: blur(20px);
		}
		100% {
			transform: scale(20.5);
			opacity: 0;
			filter: blur(50px);
		}
	}

	.style-scope ytd-thumbnail {
		animation: yadroad;
		animation-duration: 0.7s;
		animation-iteration-count: 1;
	}

	@keyframes yadroad {
		0% {
			transform: scale(0.1);
			filter: blur(90px)
		}
		100% {
			transform: scale(1);
		}
	}
	/*my eyes hurt coding*/
	html:not(.style-scope) {
		--yt-spec-static-brand-red: #ff00f7!important
	}

	#thumbnail.ytd-moving-thumbnail-renderer {
		transition: opacity 0.5s;
		opacity: 0;
		width: 100%;
		height: 100%;
		max-height: var(--yt-img-max-height, none);
		max-width: var(--yt-img-max-width, 100%);
		position: absolute;
		top: 0;
		right: 0;
		bottom: 0;
		left: 0;
	}

	.ytp-swatch-background-color {
		animation-name: yadpogi;
		animation-iteration-count: infinite;
		animation-duration: 3.5s;
		animation-fill-mode: forwards;
		animation-timing-function: linear;
	}
	@keyframes yadpogi
	{
		0% {
			background-color: #fff;
			box-shadow: 0 0 9px 3px #ff00d4;
		}
		10% {
			background-color: #fff;
			box-shadow: 0 0 7px 3px #9d00ff;
		}
		20% {
			background-color: #fff;
			box-shadow: 0 0 9px 3px #5100ff;
		}
		30% {
			background-color: #fff;
			box-shadow: 0 0 7px 3px #00d9ff;
		}
		40% {
			background-color: #fff;
			box-shadow: 0 0 9px 3px #00ffc3;
		}
		50% {
			background-color: #fff;
			box-shadow: 0 0 7px 3px #b3ff00;
		}
		60% {
			background-color: #fff;
			box-shadow: 0 0 9px 3px #ff8400;
		}
		70% {
			background-color: #fff;
			box-shadow: 0 0 7px 3px #ff6a00;
		}
		80% {
			background-color: #fff;
			box-shadow: 0 0 9px 3px #f00;
		}
		90% {
			background-color: #fff;
			box-shadow: 0 0 7px 3px #ee436b;
		}
		100% {
			background-color: #fff;
			box-shadow: 0 0 7px 3px #ff00d4;
		}
	}
