@-moz-document url-prefix("https://www.google"), url-prefix("https://images.google"), url-prefix("https://encrypted.google") {
/* Change the Google logo on the home pages of Google, doodled Google, Google Images, Google Videos */
/* (Doesn't work correctely with Google Videos home page. You cannot use doodles, even on the Google doodles site.) */

body {
    background: url("https://images.alphacoders.com/982/982018.png");
    background-size: static;
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-size: cover;
    opacity:0.9;
    color: rgba(0, 0, 0, 1.0);




}



#lga {
	box-sizing: border-box !important;
	background: url("https://rebrand.ly/b4fhhjx") center no-repeat !important;  /*PS4_1.gif*/
	/* ============================================== */
	/* Code reviewed by MaestormXR */
	 /* i really want this to be global */
      /* the code used was from the original
      https://userstyles.org/styles/173462/google-hestia-animated
      by Andrei Zhuk*/
	/* Twitter - https://twitter.com/Polschehen */
	/* Patreon - https://www.patreon.com/polsche */
	/* Ko-Fi - https://ko-fi.com/polsche */
	/* ============================================== */
	bottom: 200px !important; /* Change the value if the animation is shifted up/down. Default : 240px or 200px */
	z-index: -999 !important;
	position: relative !important;
	padding-bottom: 700px !important;
	margin: auto !important;
}


/* Remove the text "images" from the Google Images home page. */
#hplogo {
	text-indent: -9000px !important;
	white-space: nowrap !important;
}


/* Change the Google logo at the top left of the search results page. Doesn't change the doodled logo. */
#logo {
	background: url("https://rebrand.ly/b4fhhjx") 50% 80% no-repeat !important; /*PS4_3.gif*/
	background-size: contain !important;
	padding-bottom: 80px !important;
	bottom: 70px !important;
}

/* Change the Google doodled logo at the top left of the search results page. */
/* (Doesn't work as expected.) */
.logo.doodle img {
	background: url("https://rebrand.ly/b4fhhjx") 50% 80% no-repeat !important; /*PS4_3.gif*/
	background-size: cover !important;
}


/* Add Internet Explorer to the bottom right of the search results page. */
.mw {
	background: url("https://rebrand.ly/b4fhhjx") 95% 100% no-repeat !important;  /*PS4_2.gif*/ /* Change first value here if she is dancing too far left/right. Default : 80% */
	background-attachment: fixed !important;
}


/* Change the background of the Informations section on the search results page. */
#wp-tabs-container {
	background: white !important; /* Change the color if it doesn't match your theme. Example : white color in HTML : "white" or "#ffffff" or "rgb(255, 255, 255) or "hsl(0, 0%, 100%)" */
}
.sfbg{
    margin: -9000px !important;
	white-space: nowrap !important;
}
}

