@-moz-document url-prefix("https://www.youtube.com/") {
*{color:#eee!important;box-shadow:none!important; font-family: unset}
#text.ytd-channel-name,#contentContainer > *,#channel-container > *,#tabs-inner-container{background-color:rgba(22,22,22,1)!important}
html,body,#primary,#secondary{background-image:url(https://images2.alphacoders.com/589/thumb-1920-589968.png)!important;background-attachment:fixed;background-size:cover;background-color:rgba(33,33,33,0.5)!important}
#container,#columns,#guide-inner-content,#search-icon-legacy,#style-scope,.ytd-page-manager,.ytd-browse,#grid,#grid-disabled,.ytd-multi-page-menu-renderer,#content > ytd-mini-guide-renderer,.ytd-page-manager,#page-manager > ytd-browse,.ytd-mini-guide-renderer,#chips,.ytd-feed-filter-chip-bar-renderer{background-image:url(https://images2.alphacoders.com/589/thumb-1920-589968.png)!important;background-position-x:0;background-position-y:0;background-attachment:fixed;background-size:cover;color:#eee!important;background-color:rgba(33,33,33,0.5)!important}
#country-code{visibility:hidden!important}
#search,#search-form,.style-scope,.ytd-searchbox,.ytd-searchbox-spt{border-color:#444;border-radius:2px;background-image:none!important}
.yt-icon-container style-scope ytd-topbar-logo-renderer{width:128px!important;height:128px!important}
#channel-container,#tabs-inner-container,.banner-visible-area{border:1px solid #444}
#gs_ok50 img{filter:brightness(0.8) invert(1);visibility:visible!important}
::-webkit-scrollbar{width:12px;border:1px solid #212121}
::-webkit-scrollbar-track{-webkit-border-radius:16px;border-radius:16px}
::-webkit-scrollbar-thumb{-webkit-border-radius:16px;border-radius:16px;background:#444;border:1px solid #212121}
}

