        border: 1px solid var(--text) !important;
        border-radius: 3px 0 0 3px;
        box-shadow: 0 1px 1px rgba(0,0,0,0.075) inset;
        box-sizing: border-box;
        cursor: text;
        min-height: 30px;
        overflow: hidden;
        padding: 8px;
        position: relative;
        transition: border 0.2s linear 0s, box-shadow 0.2s linear 0s;
        width: 100%;
    }
    
    /*Message Recepeients*/
    .ac-token {
        background: var(--grey) !important;
        border: 1px solid var(--text) !important;
        border-radius: 14px;
        box-sizing: border-box;
        cursor: default;
        display: inline-block;
        line-height: normal;
        margin: 2px 4px 2px 0;
        overflow: hidden;
        padding: 0 18px 0 8px;
        position: relative;
        vertical-align: middle;
        text-overflow: ellipsis;
        white-space: nowrap;
        max-width: 85%;
    }
    
    /*Contact List*/
    .ac-result-container {
        background: var(--light-grey) !important;
        border: 1px solid var(--text) !important;
        border-radius: 10px;
        box-sizing: border-box;
        box-shadow: 0 2px 5px rgba(0,0,0,0.2);
        font-size: 14px;
        font-size: .875rem;
        max-height: 250px;
        min-height: 50px;
        left: 0;
        overflow: auto;
        position: absolute;
        top: 36px;
        width: 100%;
        z-index: 2;
    }
    
    /*Messges*/
    .message-list .messages>li {
        background: var(--grey) !important;
        box-sizing: border-box;
        cursor: pointer;
        height: 140px;
        padding: 20px 10px;
        border-bottom: 1px solid var(--text) !important;
        display: flex;
        overflow: hidden;
    }
    
    /*White Drope Down | Message Filter*/
    select {
        width: 220px;
        border: 1px solid var(--text) !important;
        background-color: var(--grey) !important;
        color: var(--text) !important;
    }
    
    /*Hot Fixes================================================================*/
    
    /*Inbox Tab | Course Filter Caret*/
    .caret {
        border-top: 4px solid var(--text) !important;
    }
    
    /*Inbox Tab | Course Filter Drop Down Item Wrapper*/
    .dropdown-menu.open {
        background: var(--grey) !important;
    }
    
    /*Inbox Tab | Course Filter Drop Down Item*/
    .dropdown-menu li>a {
        background: var(--grey) !important;
    }
    
    /*Inbox Tab | Course Filter Thing*/
    .btn-group .div-contain {
        background: var(--grey) !important;
    }
    
    /*Inbox Tab | Course Fiter Line*/
    .dropdown-menu .divider {
        border-bottom: 1px solid var(--sub-text) !important;
        background: #ffffff00 !important;
    }
    
    /*Discussion Reply File Attatchment*/
    .dXATK_cAug:hover, .dXATK_cAug[aria-disabled], .dXATK_cAug[aria-selected] {
        color: var(--text) !important;
    }
}
