@-moz-document url-prefix("https://en.help.roblox.com") {
/*
{{---######---CODE_FOR_SUPPORT---######---}}
*/
#header {
	background-color: #0074bd;
	height: 40px;
}
/* Logo */
.logo img {
	width: 0;
	max-height: 30px;
}
.logo a::after {
	display: inline-block;
