	width: 120px;
	height: 30px;
	position: absolute;
	left: 6px;
	top: 6px;
}
body {
	background-color: #e3e3e3;
}
.blocks-item {
	border: 1px solid transparent;
	border-radius: 3px;
	color: #00a2ff;
	max-width: 100%;
	text-align: center;
	box-shadow: 0 1px 4px rgba(25, 25, 25, 0.3);
	background-color: #fff;
}
.blocks-item-title {
	color: #00a2ff;
}
.blocks-item-description {
	color: #757575;
}
.search-full input[type="search"] {
	border: 1px solid #b8b8b8;
	border-radius: 3px;
}
.search input[type="search"]:focus {
	border: 1px solid #00a2ff;
	color: #555;
}
.blocks-item:hover,
.blocks-item:focus,
.blocks-item:active {
	background-color: #00a2ff;
}
.blocks-item-link:focus {
	box-shadow: none;
}
.blocks-item:hover *,
.blocks-item:focus *,
.blocks-item:active * {
	color: #fff;
}
.container {
	background-color: #fff;
	box-shadow: 0 1px 3px rgba(150, 150, 150, 0.74)
}
a,
.breadcrumbs li a:visited {
	color: #00a2ff;
}
.icon-star::before {
	color: #00a2ff;
}
.search::before {
	color: #ddd;
}
