style.css:
@-moz-document
regexp("https?://ulms\\.ulsan\\.ac\\.kr/(local/ubion/setting/syllabus.*|report/ubcompletion/user_progress.*|index.php\\?lang=.*|course/.*|mod/ubboard/.*|mod/assign/.*)?") {
    /*CSS for main*/
    body {
        font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
        font-size: 14px;
        line-height: 1.42857143;
        color: #d9d9d9 !important;
        background-color: #fff;
    }
    
    #page-header .navbar .container-fluid .navbar-brand {
        height: 68px;
        margin: 0;
        padding: 0;
        background-color: #377B01 !important;
    }
    
    #page-header .navbar.navbar-default {
        background-color: #377b01 !important;
        border: 0;
    }
    
    .site_notices .notices li a {
        display: block;
        position: relative;
        padding: 5px 10px;
        color: #d9d9d9 !important;
        text-decoration: none;
    }
    
    .site_notices .nav-tabs>li.active>a {
        background: transparent;
        border: 1px solid transparent;
        color: #d9d9d9 !important;
    }
    
    .progress_courses .course_lists .my-course-lists .course-name h3 {
        margin: 0;
        font-size: 16px;
        font-weight: 700;
        color: #d9d9d9 !important;
        margin-bottom: 4px;
        padding-top: 3px;
        overflow: hidden;
        white-space: nowrap;
        text-overflow: ellipsis;
        line-height: 24px;
    }
    
    .progress_courses .course_lists .my-course-lists a {
        display: block;
        padding: 21px 20px;
        text-decoration: none;
        background-color: #333333 !important;
    }
    
    .progress_courses .course_lists .my-course-lists .course-image .courseimage {
        width: 54px;
        height: 54px;
        border-radius: 54px;
        content: url(https://raw.githubusercontent.com/pithecuse527/Stylish-UOU/main/UOU-Elegant-Dark/img/teacher.png);
    }
    
    .progress_courses .course_lists .my-course-lists .course-name .prof {
        font-size: 12px;
        color: #d9d9d9 !important;
        margin: 0 0 2px 0;
        overflow: hidden;
        white-space: nowrap;
        text-overflow: ellipsis;
    }
    
    .progress_courses .course_semester {
        padding-bottom: 6px;
        font-weight: 700;
        font-size: 22px;
        color: #d9d9d9 !important;
        margin-bottom: 12px;
    }
    
    #page-container {
        float: left;
        width: 100%;
        background: url(https://ulms.ulsan.ac.kr/theme/coursemosv2/pix/layout/side_menu_bg.png) repeat-y 218px #34373c!important;
    }
    
    .coursemos-layout-course #region-main, .coursemos-layout-course.dir-ltr.editing.used-region-side-post.used-region-side-pre #region-main, #region-main {
        padding: 32px 40px;
        position: relative;
        min-height: 600px;
        background-color: #34373c!important;
        border-width: thin;
        border-style: ridge;
        border-color: #737373;
    }
    
    .image img {
        content: url(https://raw.githubusercontent.com/pithecuse527/Stylish-UOU/main/UOU-Elegant-Dark/img/announce.png);
        width: 42px;
        margin: 0;
        height: 42px;
        background-color: #34373c !important;
    }

    .block {
        margin: 0;
        border-radius: 0;
        border: 0;
        box-shadow: none;
        background-color: #34373c !important;
    }

    .block .header {
        padding: 22px 18px;
        border: 1;
        border-radius: 0;
        border-bottom: 1px solid #ebebeb;
        background-color: #404040 !important;
    }
    
    .block .header h2 {
        font-size: 14px;
        color: #d9d9d9 !important;
        font-weight: 700;
    }
    
    .block .content .timeline h5 {
        margin: 0 0 4px 0;
        overflow: hidden;
        white-space: nowrap;
        color: #d9d9d9 !important;
        text-overflow: ellipsis;
    }
    
    .block .content .timeline p {
        margin: 0;
        color: #d9d9d9 !important;
        font-size: 12px;
        overflow: hidden;
        white-space: nowrap;
        text-overflow: ellipsis;
    }
    
    .block .content .timeline .icon {
        width: 42px;
        margin: 0;
        height: 42px;
        content: url(https://raw.githubusercontent.com/pithecuse527/Stylish-UOU/main/UOU-Elegant-Dark/img/geography.png);
        background-color: #34373c !important
    }
    
    .block .content .timeline li {
        padding: 10px 0;
    }
    
    .block .content .timeline .btn-more {
        border-radius: 0;
        border-color: #cacbcd;
        background-color: #34373c !important;
        padding: 6px 12px;
        width: 190px;
    }
    
    .block.block-notification .content.ubnotification .media .media-left .media-object {
        background-color: #34373c !important;
    }
    
    .ubnotification .media-body p {
        color: black !important;
        margin-bottom: 0;
    }
    
    .activity a {
        color: #d9d9d9 !important;
    }
    
    /*CSS for course*/
    body.coursemos-layout-course.coursemos-layout-coursehome #page-header .navbar.navbar-default {
        background-color: #377B01 !important;
    }
    
    #coursemos-course-menu .block-coursehome .content ul.topmenu>li.expand {
        background-color: #3d3e42 !important;
        /* : ; */
    }
    
    #coursemos-course-menu .block-coursehome .content ul.topmenu>li>a {
        color: #d9d9d9 !important;
    }
    
    #coursemos-course-menu .block-coursehome .content ul.topmenu>li>ul>li>a {
        color: #d9d9d9 !important;
        overflow: hidden;
        white-space: nowrap;
        text-overflow: ellipsis;
        display: block;
    }
    
    .block.hidden .header h2 {
        color: #d9d9d9;
        font-weight: 400;
    }
    
    .format-ubstopics .course_box {
        border: 1px solid #d9dbdb;
        background-color: #34373c !important;
    }
    
    .format-ubstopics .course-content ul.ubstopics li.section .content .summary, .format-ubstopics .course-content .section-summary .summarytext {
        color: #d9d9d9 !important;
        margin: 0;
    }
    
    .format-ubstopics .course-content h2.main, .format-ubstopics .course-content .section-title-one {
        font-size: 16px;
        margin: 0;
        color: #d9d9d9 !important;
        font-weight: 700;
        padding: 21px 28px 20px;
        border-bottom: 1px solid #d9dbdb;
        background-color: #34373c !important;
    }
    
    .format-ubstopics .course_box0 h2.main {
        font-size: 20px;
        color: #d9d9d9 !important;
        padding: 29px 0 28px;
        border-bottom: 1px solid #ebebeb;
        margin: 0 30px;
        background-color: transparent;
    }
    
    .format-ubsweeks .course_box0 h2.main {
        font-size: 20px;
        color: #d9d9d9 !important;
        padding: 29px 0 28px;
        border-bottom: 1px solid #ebebeb;
        margin: 0 30px;
        background-color: transparent;
    }
    
    .format-ubsweeks .course-content .course_box_current h2.main, .format-ubsweeks .course-content .section-title-one .course_box{
        font-size: 16px;
        margin: 0;
        color: #d9d9d9 !important;
        font-weight: 700;
        padding: 21px 28px 20px;
        border-bottom: 1px solid #d9dbdb;
        background-color: #34373c !important;
    }
    
    .format-ubsweeks .user_attendance .user_attendance_table .attendance li {
        float: left;
        border-right: 1px #ebebeb solid;
        background-color: #34373c !important;
        height: 80px;
        text-align: center;
        font-size: 12px;
        font-weight: 600;
        color: #999;
    }
    
    .format-ubsweeks .course_box {
        border: 1px solid #d9dbdb;
        background-color: #34373c !important;
    }
    
    .format-ubsweeks .course-content ul.ubsweeks li.section ul.section li.activity a {
        color: #d9d9d9;
    }
    
    .format-ubsweeks .user_attendance .user_attendance_table .attendance .attendance_section.active {
        background-color: #34373c !important;
    }
    
    .format-ubsweeks .course-content h2.main, .format-ubsweeks .course-content .section-title-one {
        font-size: 16px;
        margin: 0;
        color: #d9d9d9 !important;
        font-weight: 700;
        padding: 21px 28px 20px;
        border-bottom: 1px solid #d9dbdb;
        background-color: #34373c !important;
    }
    
    .form-control {
        display: block;
        width: 100%;
        height: 34px;
        padding: 6px 12px;
        font-size: 14px;
        line-height: 1.42857143;
        color: #d9d9d9;
        background-color: #34373c !important;
        background-image: none;
        border: 1px solid #ccc;
        border-radius: 4px;
        -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
        box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
        -webkit-transition: border-color ease-in-out .15s,-webkit-box-shadow ease-in-out .15s;
        -o-transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
        transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
    }
    
    /*CSS for mod*/
    .table-bordered>thead>tr>td, .table-bordered>tbody>tr>td, .table-bordered>tfoot>tr>td {
        background-color: #34373c !important;
        vertical-align: middle;
    }
    
    .ubboard_list .ubboard_container {
        padding: 19px;
        border: 1px solid #e0e0e0;
        background-color: #34373c !important;
    }
    
    .ubboard .ubboard_table {
        width: 100%;
        background-color: #34373c !important;
    }
    
    .ubboard_view .subject {
        margin: 0;
        background-color: #34373c !important;
        color: #d9d9d9 !important;
        border-top: 2px solid #34373c !important;
        height: 50px;
        border-bottom: 1px solid #bababa;
    }
    
    .ubboard_view .info {
        border-bottom: 1px solid #bababa;
        padding: 8px 25px;
        color: #d9d9d9 !important;
        background-color: #34373c !important;
    }
    
    .ubboard_view .content {
        padding: 20px 30px 10px;
        background-color: #34373c !important;
    }
    
    .well {
        box-shadow: none;
        border-radius: 0;
        background-color: #34373c !important;
    }
    
    .ubboard_list .ubboard_container {
        padding: 19px;
        border: 1px solid #e0e0e0;
        background-color: #34373c !important;
    }
    
    .ubboard .ubboard_table th.header {
        vertical-align: middle;
        background-color: #34373c !important;
    }
    
    /*CSS for report*/
    .table-bordered tbody th {
        background-color: #34373c !important;
        color: #d9d9d9 !important;
    }
    
    .table-bordered>thead>tr>th {
        text-align: center;
        background-color: #34373c !important;
        border-color: #e9e9e9;
    }
    
}
