style.css:
/* variabili */
@-moz-document domain(radiobandieranera.org) {
   :root{
    --neroRBN: #1D1D1D;
    --grigioInput: #35363A;
    --grigioChiaro: #F0F0F0;
  }

  body{
    color: white;
  }

  #main{
    background-color: var(--neroRBN);
    border: 1px solid var(--neroRBN);
  }

  #featured{
    background-color: var(--neroRBN);
    border: 1px solid var(--neroRBN);
  }

  #breadcrumbs{
    color: white;
    border-bottom-color: var(--neroRBN);
  }

  #post-entry .post-meta .post-content{
    color: white;
  }

  #post-entry .post-meta{
    background-color: var(--neroRBN);
  }

  ul.sidebar_list .widget_text .textwidget{
    background-color: var(--neroRBN);
  }

  .player-meta .track-artist{
    color: white;
  }

  .player--classic .player-body{
    background-color: var(--neroRBN);
  }

  .player .controls .streamtime{
    color: white;
  }

  .player-meta li{
    color: white;
  }


  ul.sidebar_list li ul, ul.sidebar_list li div{
    background-color: var(--neroRBN);
  }

  ul.sidebar_list .widget_search{
    background-color: var(--neroRBN);
  }

  ul.sidebar_list .widget_search #searchform #s{
    color: white;
    background-color: var(--grigioInput);
  }

  #topsearch #searchform #s{
    background-color: var(--neroRBN);
    color: white;
  }

  ul.sidebar_list{
    color: white;
  }

  ul.sidebar_list li div{
    color: white;
  }

  #post-entry .post-meta-single h1, #post-entry .post-meta-single h2{
    color: white;
  }

  #post-entry .post-meta-single .post-date, #post-entry .post-meta-single .post-tags{
    color: white;
  }

  #post-entry .post-meta{
    border: 1px solid var(--neroRBN);
  }

  .mejs-controls .mejs-horizontal-volume-slider .mejs-horizontal-volume-total, .mejs-controls .mejs-time-rail .mejs-time-total{
    margin-top: 20px;
  }

  #post-entry .post-meta-single .post-content{
    color: white;
  }

  #post-entry .post-meta-single{
    background-color: var(--neroRBN);
  }

  #respond h4{
    color: white;
  }

  ul.sidebar_list li{
    border: 1px solid var(--nerRBN);
  }

  #cf .tf{
    background-color: var(--grigioInput);
    color: white;
  }

  #cf label{
    color: white;
    border: 1px solid var(--neroRBN);
  }

  #cf .af{
    background-color: var(--grigioInput);
    border: 1px solid var(--neroRBN);
  }

  #post-navigator-single{
    color: white;
  }

  #post-entry .post-meta-single h1#page{
    color: white;
  }

  .radio-clock-title{
    color: white;
  }

  .radio-server-time, .radio-user-time, .radio-server-date, .radio-user-date{
    color: white;
  }

  .radio-server-zone, .radio-user-zone{
    color: white;
  }

  a:hover, a:active{
    color: white;
  }

  #master-schedule-tab-panels .master-schedule-tabs-show .show-time, #master-schedule-tab-panels .master-schedule-tabs-show .show-user-time, #master-schedule-tab-panels .master-schedule-tabs-show .show-host-names{
    color: white;
  }

  #master-schedule-tab-panels .master-schedule-tabs-show .show-encore, #master-schedule-tab-panels .master-schedule-tabs-show .show-file, #master-schedule-tab-panels .master-schedule-tabs-show .show-genres{
    color: white;
  }

  .nocomments{
    color: var(--grigioChiaro);
  }

  #master-schedule-tabs .master-schedule-tabs-day-name{
    color: white;
  }

  #master-schedule-tabs .master-schedule-tabs-day.current-day{
    border: 2px solid white;
  }

  #master-schedule-tabs .master-schedule-tabs-day{
    border: 1px solid var(--grigioChiaro);
    background-color: var(--grigioInput);
  }

  #post-entry .post-meta-single{
    background-color: var(--neroRBN);
  }

  #master-schedule-tab-panels .master-schedule-tabs-panel.active-day-panel{
    border: 1px solid white;
    background-color: var(--neroRBN);
  }

  #post-entry .post-meta-single h1#page{
    color: white;
  }
}
