style.css:
@-moz-document domain("www.youtube.com") {
:root {
	--color: /*[[Color]]*/ ;
	--color-disabled: /*[[Disabled]]*/ ;
	--color-check-profile: /*[[CheckProfile]]*/ ;
	--color-logo: /*[[Logo]]*/ ;
}

/* SVG Fill Color */
.ytp-large-play-button.ytp-touch-device .ytp-large-play-button-bg,
.ytp-cued-thumbnail-overlay:hover .ytp-large-play-button-bg,
#heart-button button[aria-label='Remove heart'] yt-icon,
#hearted.ytd-creator-heart-renderer,
path[fill='#FF0000'],
path[fill='#ff0000'],
path[fill='#f80000'],
path[fill='#FF8983'],
path[fill='#f00'],
path[fill='#F00'],
circle[fill='red'] {
	fill: var(--color-logo);
}
/* Html5 player */
.html5-play-progress,
.ytp-play-progress,
paper-button.ytd-subscribe-button-renderer {
	background: var(--color);
}
.ytp-settings-button.ytp-hd-quality-badge:after,
.ytp-settings-button.ytp-4k-quality-badge:after,
.ytp-settings-button.ytp-5k-quality-badge:after,
.ytp-settings-button.ytp-8k-quality-badge:after {
	background-color: var(--color);
}
.ytp-menuitem[aria-checked='true'] .ytp-menuitem-toggle-checkbox,
.ytp-swatch-background-color {
	background: var(--color);
}
.ytp-menuitem .ytp-menuitem-toggle-checkbox {
	background: var(--color-disabled);
}
.ytp-menuitem sup {
	color: var(--color);
}

/* Url buttons */
.ytp-chrome-controls
	.ytp-button.ytp-youtube-button:not([aria-disabled='true']):not(
		[disabled]
	):hover
	.ytp-svg-fill-logo-tube-lozenge {
	fill: var(--color);
}
.ytd-menu-service-item-renderer svg,
.ytd-subscription-notification-toggle-button-renderer svg {
	color: var(--color);
}
.ytd-badge-supported-renderer svg {
	fill: var(--color-check-profile);
}
.toggle-bar.paper-toggle-button {
	background: var(--color-disabled);
}
.toggle-button.paper-toggle-button {
	background: var(--color-disabled);
}
#top-level-buttons
	> ytd-toggle-button-renderer:nth-child(2)
	.style-default-active {
	color: var(--color-disabled);
}
#notification-count.ytd-notification-topbar-button-renderer {
	background-color: var(--color);
}
}
