style.css:
@-moz-document url-prefix("https://www.eldarya.fr/"), url-prefix("https://fa1.eldarya.com/"), url-prefix("https://www.eldarya.ru/"), url-prefix("https://www.eldarya.com/"), url-prefix("https://www.eldarya.com.br/"), url-prefix("https://www.eldarya.de/"), url-prefix("https://www.eldarya.es/"), url-prefix("https://www.eldarya.fi/"), url-prefix("https://www.eldarya.hu/"), url-prefix("https://www.eldarya.it/"), url-prefix("https://www.eldarya.pl/")
{
  /*Zwiekszenie czcionki */
          body
          {
            font-size:17px;
          }
  
  /*Usuniecie krysztalu */
          #header #crystal-images-inner
          {
            display:none;
          }
         #top-bar::before
  		{
          background:none;
        }
  
  /*Skorygowanie layoutu w bibliotece */
        #library-container
        {
            font-size:14px;
        }
  
    /*Skorygowanie lyoutu targu*/
  li.marketplace-abstract.marketplace-search-item
    {
        width:150px;
    }
  
  /*Skorygowanie lyoutu garderoby i strychu*/
  #appearance-items-categories.active.mCustomScrollbar._mCS_1
    {
        height:400px;
        max-height:400px !important;
    }

  .attic-categories-list
    {
        height:350px;
        max-height:350px !important;
    }
  
   /*Usuniecie blura i podmiana tel */
        .home-interface-container #left-bg img 
  	{
          width: 110%;
          height: 110%;
          filter: none;
          transform: translate(-5%, -5%);
  }
  
        .appearance-interface-container #avatar-background img 
      {
          width: 110%;
          height: 110%;
          filter: none;
          transform: translate(-5%, -5%);
      }
  
  #container .library-main-container 
  {
    background-image: url(https://www.eldarya.pl/static/img/home/treasure-hunt/bg-village.jpg);
}

  .page-main-container
  {
    background-image: url(https://www.eldarya.pl/static/img/home/treasure-hunt/bg-village.jpg);
}
  
}
