/* ==UserStyle==
@name           T24 Koyu Tema
@namespace      USO Archive
@author         kukla
@description    `Koyu tema sevenler için ve gece kullanımı için oluşturuldu.`
@version        20201202.13.59
@license        CC-BY-NC-4.0
@preprocessor   uso
==/UserStyle== */
@-moz-document url-prefix("https://t24.com.tr") {
    :root {
        /*primary colors*/
        --color-text: #0f0;
        --color-bg: black;
        --color-a: #00fcff;
        --color-p: yellow;
        --color-h: red;
        /*secondary colors*/
        --color-bg-2: #474747;
        /*hover colors*/
        --color-p-hover: #04ff00;
        /*borders*/
        --border-type-1: solid #deff00 2px;
    }

    *,
    ._3lN9O,
    ._2N123,
    ._1kyUj,
    ._2N123 ._3eDek ul,
    ._2EDuO,
    ._2nJwp *,
    ._3E0W_,
    ._1nMmz {
        background-color: var(--color-bg)!important;
        color: var(--color-text)!important;
        border: none!important;
        box-shadow: none!important;
    }

    a,
    ._2N123 ul li a,
    ._2EDuO .urQzr li div a,
    ._3VWKZ ._3fpo7 p,
    ._3RmGa ._1MRq9 a,
    a > p {
        color: var(--color-a)!important;
    }

    p,
    ._3qV-o p,
    ._1kyUj ._2wcEi ._2exRC p,
    ._1bENp p,
    ._3GtK6 ._1Lwj4,
    ._3E0W_ .r1ybC p,
    ._3VWKZ ._11UQy,
    ._3VWKZ p,
    ._62jws ._1fHSU p,
    ._1T5c1 ._2IPF1 ._112z3 {
        color: var(--color-p)!important;
    }

    ._3GtK6 ._1Lwj4:hover {
        color: var(--color-p-hover)!important;
    }

    ._3_quq,
    ._3E0W_ h3 {
        color: var(--color-h)!important;
    }

    /* EXTRAS */
    /*dropdown "diğer on top navmenu"*/
    ._2N123 ._3eDek:hover ul,
    li._35ZL6 > ul {
        border: var(--border-type-1)!important;
        border-radius: 5px;
        background-color: var(--color-bg)!important;
        text-align: center;
    }

    ._2N123 ._3eDek:hover ul > *,
    li._35ZL6 > ul * {
        background-color: var(--color-bg)!important;
    }

    ._2N123 ._3eDek:hover ul > li *:hover,
    li._35ZL6 > ul li *:hover {
        background-color: var(--color-bg-2)!important;
        border: var(--border-type-1)!important;
        border-radius: 5px;
    }

    /* SLIDER */
    ._3rpHD,
    ._3qV-o {
        border: var(--border-type-1)!important;
        border-radius: 5px;
    }

    /* Images */
    img{
        border-radius: 5px;
        border: var(--border-type-1)!important;
    }
    
    /* Images no border*/
    ._3lN9O ._3VbPy ._2gcy9,
    ._19NuZ img,
    .pO6_w img,
    .r1ybC img{
        border:none!important;
    }
    
    

    /* Height issued */
    ._3rpHD,
    ._1T5c1 {
        height: 100%!important;
    }

    /* Widht issued */
    ._1P9Qi {
        width: max-content;
    }

    /* Ads */
    .adunitContainer,
    #ad_position_box {
        display: none!important;
    }
}
