    text-decoration: none !important;
}


/* Score value text color highlight */
.quiz_score .score_value {
    color: white;
}
/* Module section items' small text color */
.item-group-condensed .ig-header {
    color: #eee;
}
/* Module pages title text color */
.pages.show .page-title {
    color: white;
}
/* Disscussion board overview inner background color */
.ic-discussion-row {
    background-color: var(--defaultGrey) !important;
}
/* Disscussion board entries */
.discussion_entry {
    background: var(--defaultGrey);
}
/* Closed For Comments Disscussion board background color */
.discussions-v2__container-image {
    background-color: var(--defaultGrey) !important;
    border: 0.125rem dashed white !important;
}
/* Dicussion subscript color */
.MlJlv_ebWM {
    color: #bbb;
}
/* Graded disscussion alert text color */
.alert-info {
    background-color: var(--whiteGrey);
}
/* Graded disscussion point text color */
.discussion-points {
    color: #eee;
}
/* Graded disscussion dude date text color */
.discussion-pubdate, .discussion-tododate {
    color: #eee;
}
/* TODO: change input text color */
.mce-content-body > p .default-theme > p {
    color: black !important;
    font-family: Arial, Helvetica, sans-serif;
    /* Change font for better readability */
}
/* Collab page font color */
/* Mini calender view linked day background color */
.day_wrapper[role="link"] {
    border-radius: 10%;
    background-color: #666;
}
.list-view a.active {
    background-color: var(--defaultGrey);
}
/* Left pannel selection highlight color */
.list-view a.active,
.list-view a.active:hover,
.list-view a.active:focus {
    background: #444;
}
.zm-comp-header-figure {
    filter: invert(1) !important;
}
.collectionViewItems .ui-tabs-nav .ui-helper-reset .ui-helper-clearfix .ui-widget-header .ui-corner-all {
    background-color: var(--defaultGrey);
}
/* Occonisionally appear in instructor entered text,
 * which stays black. This is used to change it to white */
.user_content span {
    color: #eee !important;
}
.item_name {
    color: #111 !important;
}
.ig-details__item {
    color: #aaa;
}
.zm-comp-header-logo {
    fill: #eee;
}
.ant-tabs-tab-active {
    color: #eee;
}
}
