style.css:
@-moz-document url-prefix("https://ravesli.com") {
body {
	background: #1b1b1b;
}

.content p, ol, .tabluc td {
	color: #efefef;
}

.zagolovokstat, #zagvstat, .czagvstat, .about-the-author, #zagvstatmin, .czagvstatmin {
	color: #ffffff;
}

code {
	    background: #34495e;
}

.prumitka {
	background: #2f2f2f;
}

.toc {
	background: #2f2f2f;
}

.toc_list li a {
	color: #2980b9;
    border-bottom: 1px solid #31719a;
}

.top3 ul li a, .current-cat  {
	color: #efefef;
}

.top3 .fa {
    color: #efefef;
}

.zagolovok a {
	color: #d2d2d2;
}

.zagolovok a:visited {
	color: #2980b9;
}

.toc_list li {
color: white;
}

.comment-respond {
	color: #95b3d7;
}

#respond h3 {
	color: white;
}

#respond input[type=text], #respond textarea {
	border: 1px solid #b1b1b1;
	color: white;
	background: #2f2f2f;
}

.comment-respond a {
	color: #3b5998;
}
}
