style.css:
@-moz-document domain("www.nitrotype.com")
{
  body
  {
    background-color: #2d2e2e;
    overflow-y: scroll;
    background: none;
    overflow-x: hidden;
    position: relative;
  }

  .card--grit
  {
    background-image: url("https://wallpaperaccess.com/full/1188230.png");
  }

  html
  {
    background: fixed url("https://wallpaperaccess.com/full/1188230.png") no-repeat, #2d2e2e;
    box-sizing: border-box;
    color: #2d2e2e;
    font-family: "Montserrat", sans-serif;
    font-size: 16px;
    font-weight: 300;
    line-height: 1.6;
    background-size: 120%;
  }

  .bg--gradient
  {
    background: top left repeat, linear-gradient(90deg, #101010 60%, #1f2021 100%);
  }

  .card--a
  {
    background-image: url("https://www.vamosrayos.com/b/2020/01/cool-hd-1080p-images-background-pictures-desktop-download.jpg");
    color: #fefeff;
    background-size: 150%;
  }

  .btn--primary
  {
    background: #0c0c0c;
    color: #0be212;
  }

  .theme--pDefault .profile-title
  {
    color: #FFFFFF;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }

  .theme--pDefault .profile-levelNum
  {
    color: #FFFFFF;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }

  .theme--pDefault .profile-body
  {
    color: #FFFFFF;
  }

  .dash-copyContainer
  {
    background: #1d1e23;
    -webkit-box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    border-radius: 5px;
    flex: 1;
    overflow: hidden;
    padding: 15px;
    display: flex;
  }

  .dash-copy
  {
    color: #1fec05;
    font-family: "Roboto Mono", "Courier New", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
    font-size: 28px;
  }

  ::selection
  {
    background-color: #25e90b;
    color: white;
  }

  .bg--gradient
  {
    background: url(https://wallpaperaccess.com/full/1188230.png) no-repeat;
  }

  .theme--pDefault .profile-bg
  {
    background: url("https://wallpaperaccess.com/full/1188230.png") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ffffff 100%);
    color: #d3d3d3;
  }

  .btn--inky, .btn--inky:focus:not(:hover)
  {
    color: #ffffff;
  }

  .theme--pDefault .profile-bgHead
  {
    background: url("https://cdn10.bigcommerce.com/s-raqyrv37/products/94/images/421/lime_colored_sand__37341.1454364400.1280.850.jpg?c=2") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
  }

  .theme--pDefault.is-gold .profile-bg
  {
    background: url("https://cdn10.bigcommerce.com/s-raqyrv37/products/94/images/421/lime_colored_sand__37341.1454364400.1280.850.jpg?c=2") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
  }

  .theme--pDefault.is-guest .profile-bgHead
  {
    background: #fff0;
  }
}
