style.css:
@-moz-document domain("github.com") {
:root {
    --color-bg-primary: #515151;
    --color-bg-secondary: #484848;
    --color-bg-canvas-inset: #000;
    --color-bg-canvas: #575757;
    --color-bg-tertiary: #2d2d2d;
    --color-bg-overlay: #595959;

    --color-sidenav-selected-bg: #86654b;

    --color-timeline-badge-bg: #92b5d9;

    --color-text-primary: #00fbe6;
    --color-text-secondary: #FFF;
    --color-text-disabled: #d70101;
    --color-text-link: #00fbff;
    --color-text-tertiary: #00fff1;

    --color-btn-bg: #343c39;
    --color-btn-danger-disabled-bg: #242424;
    --color-btn-primary-text: #ef0;
    --color-btn-text: #16ff00;

    --color-social-count-bg: #0f0f0f;

    --color-input-contrast-bg: #393939;
    --color-input-bg: #626262;

    --color-state-hover-secondary-bg: #797676;

    --color-auto-gray-6: #ffe100;

    --color-counter-bg: #35ff31;
    --color-counter-secondary-text: #1500ff;

    --color-project-sidebar-bg: #000;

    --color-header-search-bg: black;

    --color-diff-blob-num-text: #82ff5e;
}

/* COLORS */
.octicon,
a.link-gray,
.text-gray,
.text-small,
.Header .octicon-mark-github,
.Header .form-control,
.pl-e,
.pl-en {
    color: #ffba00!important;
}

a.muted-link {
    color: #ac712b!important;
}

a.js-selected-navigation-item,
header .octicon,
header span,
strong,
.hide-full-screen a,
.pl-s .pl-s1,
.pl-smi {
    color: #00fbe6!important;
}

li,
p,
a.text-gray-dark,
#readme .Box-title,
time-ago,
span.color-text-primary.ws-normal.text-left,
#year-list-container a,
.pl-pds,
.pl-s,
.pl-s .pl-pse .pl-s1,
.pl-sr,
.pl-sr .pl-cce,
.pl-sr .pl-sra,
.pl-sr .pl-sre {
    color: yellow!important;
}

.pl-c {
    color: lightgreen;
}

/* BACKGROUNDS */
/*background-primary: #25272a;
  background-secondary: black;*/
.Box .Box-body,
#readme .markdown-body,
#js-pjax-container > div.container-xl.px-3.px-md-4.px-lg-5 > div > div.flex-shrink-0.col-12.col-md-9.mb-4.mb-md-0,
#user-activity-overview > div,
#year-list-container,
#year-list-container > div > div.js-profile-timeline-year-list.bg-white.js-sticky,
#js-pjax-container > div.mt-4.position-sticky.top-0.d-none.d-md-block.bg-white.width-full.border-bottom.color-border-secondary,
#js-pjax-container > div.container-xl.px-3.px-md-4.px-lg-5,
#js-pjax-container,
body > div.position-relative.js-header-wrapper > header,
body,
#js-contribution-activity > div > div > h3 > span {
    background: black!important;
}


div.pl-5,
div.TimelineItem-badge,
#user-19561417-pinned-items-reorder-form > ol > li > div,
#js-issues-search,
.flash {
    background: #25272a;
    background-color: #25272a;
}

.Details .Box-row {
    background-color: #5e5e5e!important;
}
.Box-row--focus-gray.navigation-focus {
    background-color: #000!important;
}
.Box-header {
    background-color: #404040!important;
}

div.file-navigation .btn,
.label,
.Label {
    background: #28755f!important;
}

.overflow-hidden {
    background: black;
    color: yellow;
}

#jump-to-results {
    background: black;
    color: yellow;
}

#jump-to-results a:hover {
    background: #555;
}
}
