/*popover error and message*/


.p-toast .p-toast-message.p-toast-message-error {
    vertical-align: middle;
    background: #000000;
    border: solid #ff0000;
    color: #ff0000;
    box-shadow: 0px 0px 10px 2px #ff0000;
    animation: toast 0.8s both linear;
    
}

@keyframes toast {
  0% {   
            transform: scale3d(1, 1, 1);
  }
  30% {
            transform: scale3d(1.25, 0.75, 1);
  }
  40% {
            transform: scale3d(0.75, 1.25, 1);
  }
  50% {
            transform: scale3d(1.15, 0.85, 1);
  }
  65% {
            transform: scale3d(0.95, 1.05, 1);
  }
  75% {
            transform: scale3d(1.05, 0.95, 1);
  }
  100% {
            transform: scale3d(1, 1, 1);
  }
}


.p-toast .p-toast-message.p-toast-message-error .p-toast-message-icon, .p-toast .p-toast-message.p-toast-message-error .p-toast-icon-close {
