style.css:
@-moz-document domain("subsplease.org") {
[data-theme="dark"] {
    --common-background-color: #19191f;
    --main-background-color: #20202a;
    --main-color: #d4d4d4;
    --heading-color: #fdfdfd;
    --fieldset-border: #33333c;
    --a-color: #AC0E9D;
    --blockquote-color: #444;
    --blockquote-cite-color: #25252e;
    --blockquote-cite-color-hover: #666;
    --accent-one: #fb4040;
    --accent-two: #33333c;
    --mark-background: #03071c;
    --pre-background: #050505;
    --class-site-background-color: #0e0e0e;
    --badge-color: #33333c;
    --badge-unrel-text: #424242;
    --links-unrel-text: #313131;
    --button-color: #33333c;
    --class-site-color: #de8a64;
    --main-border-color: #33333c;
    --menu-color: #595959;
    --class-top-bar-background-color: #33333c;
    --menu-text-color: #c2c2c2;
    --class-page-links-color: #999;
    --site-description-color: #bbb;
    --class-comment-list-background: #0d0d0d;
    --class-avatar-border: #262626;
    --class-comment-list-author-color: #ccc;
    --class-comment-respond-background-color: #090909;
    --class-comment-alert-color: #3fc6d4;
    --parent-anchor-arrow-color: #404040;
    --time-new-badge-color: #fff;
    --class-badge-front-color: #b4b4b4;
    --class-time-new-badge-background-color: #7289da;
    --alert-color: #262632;
    --alert-border-color: #33333c;
    --kbd-box-shadow-color: rgba(0, 0, 0, 0.25);
    --forms-input-textarea-background-color: rgba(0, 0, 0, 0);
    --class-screen-reader-text-box-shadow: rgba(255, 255, 255, 0.6);
    --class-skip-link-box-shadow: rgba(255, 255, 255, 0.2);
    --class-media-element-volume-background: rgba(0, 0, 0, 0.33);
    --class-sf-menu-ul: rgba(255, 255, 255, 0.1);
    --media-large-desktop-box-shadow: rgba(255, 255, 255, 0.175);
}

[data-theme="dark"] .site-title a, [data-theme="dark"] .site-title a:visited {
    color: var(--heading-color);
}

.site-description {
    opacity: 1;
}

[data-theme="dark"] .site-description {
    color: var(--main-color);
}

[data-theme="dark"] button, [data-theme="dark"] input[type="button"], [data-theme="dark"] input[type="reset"], 
[data-theme="dark"] input[type="submit"], [data-theme="dark"] #latest-load-more span {
    color: var(--main-color);
}

[data-theme="dark"] input[type="text"], [data-theme="dark"] input[type="number"], [data-theme="dark"] input[type="email"], 
[data-theme="dark"] input[type="url"], [data-theme="dark"] input[type="password"], [data-theme="dark"] input[type="search"], 
[data-theme="dark"] select, [data-theme="dark"] textarea {
    border: none;
}

::placeholder {
    color: var(--main-color);
    opacity: 0.33;
}

input:checked + .slider {
    background-color: var(--class-time-new-badge-background-color);
}

.search-box-input.col-10 {
    border-radius: 2px 0px 0px 2px !important;
}

.search-button.col-6 {
    border-radius: 0px 2px 2px 0px !important;
}

#latest-load-more span, .time-new-badge {
    border-radius: 2px !important;
}

.sidebar, .home-post, .post-wrapper-hentry {
    border-radius: 0.25rem !important;
}

.home #secondary {
    border-radius: 0.25rem 0.25rem 0rem 0rem !important;
}

.home #discord-widget {
    border-radius: 0rem 0rem 0.25rem 0.25rem !important;
}
}
