style.css:
div[data-test-id="modal-outer"] {
    display: none !important
}

div[data-test-id="mail-right-rail"] > div:first-child {
    display: none !important
}

div[data-test-id="mail-right-rail"] > div:nth-child(2) > div:nth-child(2) {
    display: none !important
}

div[data-test-id="mail-right-rail"] > div:nth-child(2) > div:first-child {
    height: 100% !important
}

div[data-test-id="mail-right-rail"] div[data-test-id="comms-properties-bar"] {
    height: 100% !important;
    flex-direction: column !important;
    justify-content: start !important;
}

div[data-test-id="mail-right-rail"] div[data-test-id="comms-properties"] {
    flex-direction: column !important;
}

div[data-test-id="mail-right-rail"] div[data-test-id="comms-properties"] > [data-iskeynav="true"] {
    margin: 0 0 3em 0 !important
}

div[data-test-id="mail-right-rail"] div[data-test-id="popover-container"] span[data-test-id="settings-link-label"] {
    display: none !important
}
