style.css:
@-moz-document url-prefix("https://www.roblox.com/My/Money.aspx#/#") {
/* Script made by ThePastaNebula */

#MasterContainer {
    background-color:#232527!important;
}

html {
    background-color: #232527!important;
}



.btn-control.btn-control-small {
    height: 18px;
    line-height: 18px;
    font-size: 11px;
    background-image: url(https://i.ibb.co/vdHKyt2/gifgit.png);
    color:white!important
}

body {
    background-color: #232527!important;
    margin: 0;
}

.loading {
    background-color:#232527!important
}

#Body {
    background-color:#232527!important;  
}

.empty {
    color:white!important
}

.StandardPanelWhite {
    background-color:#232527!important
}

.Date {
    color:#fff!important
}

.form-select {
    margin: 0 1px 0;
    height: 21px;
}

.Member {
    color:#fff!important
}

.Description {
    color:#fff!important
}

.Amount {
    color:#fff!important
}

.table-header th {

    font-weight: 600;
    background-color: #3e3e3e!important;
    border-top: none;
}

.form-label {
    color:white!important;
}

a:link, a:active {
    color: #3979bf;
}

a:visited {
    color:#29578a
}

.container-footer {
    background-color: #232527;
    padding: 123px;
}

.input-group-btn .input-dropdown-btn {
    background-color: #373737;
    border: 1px solid #b8b8b8;
    color: #fff;
}

.SquareTabGray.selected, .SquareTabGray.selected:hover, .SquareTabGray.ui-tabs-selected, .SquareTabGray.ui-tabs-selected:hover {
    margin-top: 0;
    padding: 9px 7px 12px 7px;
    position: relative;
    top: 1px;
    border-color: #ccc;
}

.SquareTabGray {
    list-style: none;
    float: left;
    background-color: #343434;
    padding: 7px;

    font-weight: bold;
    font-size: 15px;
    color: #fff;
    margin: 4px 2px 0 1px;
    border-bottom-width: 0;
    position: relative;
    top: 2px;
}

.SquareTabGray.selected, .SquareTabGray.selected:hover, .SquareTabGray.ui-tabs-selected, .SquareTabGray.ui-tabs-selected:hover {
    background-color: #232527;
    margin-top: 0;
    padding: 9px 7px 12px 7px;
    position: relative;
    top: 1px;
    border-color: #ccc;
}

.form-select {
    background:#282a2d!important;
    color:white!important;
}

.SquareTabGray a {
    text-decoration: none;
    color: #fff;
    cursor: pointer;
}

.SquareTabGray a:hover {
    background: #35383c!important;
}

.btn-control, .btn-control:active, .btn-control:link, .btn-control:visited, .btn-control:hover {
    border: 1px solid #777;
    color: #fff;
    background: #232527!important;
}

.SquareTabGray a:hover {
    background: #3e3e3e!important;
}

.SquareTabGray:hover {
    background-color: #3e3e3e;
    color: #fff;
    z-index:0;
} 

.SquareTabGray.selected a:hover {
    background: #232527!important;
}

.icon-default-economy-small, .icon-robux-16x16, .icon-robux-gold-16x16, .icon-robux-gray-16x16, .icon-robux-white-16x16 {
    background-image: url(https://images.rbxcdn.com/e3fed0d20e85a51518b2756e66343a5e-economy_small_dark.svg);
    background-repeat: no-repeat;
    background-size: auto auto;
    width: 20px;
    height: 20px;
    display: inline-block;
    vertical-align: middle;
}

body {
    background-color: #232527!important;
    margin: 0;
}

#MyTransactions_tab .loading, #TradeItems_tab .loading {
    background: #232527 url(https://www.roblox.com/images/ProgressIndicator4.gif) no-repeat center !important;
    height: 150px;
    position: relative;
    width: 780px;
}

.dark-theme .input-group-btn .input-dropdown-btn {
    background-color: transparent;
    border-color: #bdbebe;
    color: hsla(0,0%,100%,.7);
    border-radius: 0;
}

.text-footer-nav:focus, .text-footer-nav:hover {
    color: #fff;
    font-size: 20px;
    font-weight: 300;
}

.rbx-body {
    background: #232527!important;    
}





.dark-theme .input-group-btn .input-dropdown-btn.active, .dark-theme .input-group-btn .input-dropdown-btn:active {
    background-color: transparent;
    color: #fff;
}


