style.css:
/* i really want this to be global */

.topMenu {
    display: none;
}

.chatIcon--desktop {
    visibility: hidden;
}

.layout__Header {
    background: #629dc9;
}

.btn-primary {
    background-color: #016cb0;
    border-color: #005187;
}

.btn-primary:hover {
    background-color: #177cbd;
}

.countDownTimer__pause-layer.icon-play-button {
    color: #016cb0;
}
.countDownTimer__Circle--color1 {
    stroke: #016cb0;
}

.answerButton__LeftLine {
    border-left: .25rem solid #016cb0;
}

.countDownTimer__Circle--color2 {
    color: #629dc9;
    stroke: #629dc9;
}

.answerButton__Text {
    font-weight: 500;
}

.questionDetails__Text {
    font-size: 1.6rem;
    font-weight: 500;
}

.alertsBox {
    background-image: url(https://i.imgur.com/nJp0tAE.png);
    background-size: contain;
    background-repeat: no-repeat;
    height: 6%;
    margin-left: 26px;
    margin-top: 2px;
}

.alertsBox {
    z-index: 0;
}

.resultHeader__Share {
    visibility: hidden;
}

.yourResult__Name {
    font-weight: 700;
    font-size: 1.8rem;
}

.yourResult__PositionRow {
    margin-top: 15px;
}

.yourResult__FotoCol {
    margin-top: 17px;
}

.gameStateLine__Theme {
    margin-top: 20px;
}
