style.css:
@-moz-document domain("1001fonts.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

#subtags .cloud em, #subtags .cloud a
{
	text-transform: capitalize!important;
}

#sampleText,

.btn-outline-dark,
.btn-outline-light,
.fontPreviewWrapper,
.navbar .form-inline .input-group .btn-light,
.page-link,
.typeface-action-buttons.btn-group-sm .btn,
.typeface-info-buttons.btn-group-sm .btn,

:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

/*___ Fonts ________________________________________________________________________*/

.fontName,
.siteSearch input[type="text"]
{
	font-size: 2.5rem!important;
}

#font-categories .category,
#sampleText input,
#permalink,
#subtags .cloud em,
#subtags .cloud a,

.selectedTxt,

body
{
	font-size: 1.2rem!important;
}

ul.newList li
 {
	font-size: 15px!important;
}

.count,
.list-filter .filter-tags>ul>li a
{
	line-height: 1rem!important;
}

/*___ Remove Items _________________________________________________________________*/

#adzoneLeft,
#adzoneTop,

.adzone-sidebar-wrapper
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Margins ______________________________________________________________________*/

#sponsoredFonts
{
	margin: 1.2rem!important;
}

.preview-link
 {
	margin: 3px 16px!important;
}

a.fontPreviewImg {
    margin: 0.8em 2em;
}

/*___ Paddings _____________________________________________________________________*/

#mainbody,

.preview-link
{
	padding: 1.2rem!important;
}

#sampleText input,

.navbar .navbar-nav .nav-item .nav-link,
.siteSearch input[type=text]
{
	padding: 13px 26px!important;
}


/*___ Heights ______________________________________________________________________*/

#subtags {
    height: 8em;
}

.list-filter .filter-tags>ul {
    height: 4rem!important;
}

/*___ Widths _______________________________________________________________________*/

body
{
	max-width: none!important;
}

.show_ads .contentblock
{
	width: 98%!important;
}

#header.hl .siteSearch input[type=text],
#header.hxl .siteSearch input[type=text],
#sampleText input
{
    width: 25rem;
}
}

@-moz-document domain("1001fonts.com") {

}

@-moz-document domain("1001fonts.com") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

#moreSubtags:hover,

.hiLite,
.page-item.active .page-link,

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/*__________________________________________________________________________________*/

#breadcrumbs>div:first-child,
#moreSubtags,

.btn-outline-dark,
.button,
.cc-color-override--1298983837.cc-window,
.count,
.newListSelected,

body,
li,
p
 {
	color: #fff!important;
}

/*___ Khaki ________________________________________________________________________*/

.fontName,

h1,
h2
 {
	color: #F0E890!important;
}

/*___ Burlywood ____________________________________________________________________*/

.author a,

h3,
h4
 {
	color: #e0b880!important;
}

/*___ Sandy Brown __________________________________________________________________*/

.author,

h5,
h6,
label
 {
	color: #F0A060!important;
}

/*___ Blue _________________________________________________________________________*/

time
 {
	color: #07beff!important;
}

/*___ Green ________________________________________________________________________*/

selector
 {
	color: #07ff24!important;
}

/*___ Red __________________________________________________________________________*/

selector
 {
	color: #ff0707!important;
}

/*__________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*__________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

}

@-moz-document domain("1001fonts.com") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

selector
{
	background-image: none!important;
}

/*___ Background Gray ______________________________________________________________*/

#font-categories,
#tagPanel,

.list-filter .browsing-options,

footer
{
	background: #060505!important;
	background-color: #060505!important;
}

#breadcrumbs,
#displayOptions,

.breadcrumb,
.list-filter
{
    background-color: #1a1a1a!important;
}

/*___ Background 0000 ______________________________________________________________*/

#mainbody,
#moreSubtags,#moreSubtags:hover,
#pageNav a,
#permalink:hover,
#sampleText,
#siteLinks a:hover,
#subtags .cloud a:active,#subtags .cloud a:focus,#subtags .cloud a:hover,

.btn-outline-dark,.btn-outline-light:hover,
.btn-trans,.btn-trans:hover,
.button,
.button.fontDownload,
.button.fontDownload:active,
.button.fontDownload:focus,
.lc0:hover,.lc1:hover,
.list-filter .filter-tags a:hover,
.navbar .form-inline .input-group .btn-light,
.newListSelected:hover,
.page-link,
.siteSearch,
.ui-tooltip a:hover,

body .content-container,body>.container,body>.container-fluid,
body>.container-lg,body>.container-md,body>.container-sm,body>.container-xl,
ul.tags .tags__tag,
ul.tags a:not(.btn):active,ul.tags a:not(.btn):focus,ul.tags a:not(.btn):hover
{
	background: #0000!important;
	background-color: #0000!important;
}

/*__ Blue __________________________________________________________________________*/

#siteLinks a,
#siteLinks a.highlight,

.btn-light:not([aria-label="Click to search"]),
.btn-primary,
.ui-tooltip a
{	
	background: radial-gradient(circle, #1b8bc8a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

#siteLinks a::before,
#siteLinks a.highlight::before,

.btn-light:not([aria-label="Click to search"])::before,
.btn-primary::before,
.ui-tooltip a::before
{
	background: radial-gradient(circle, #000 0%, #1b8bc8 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

#siteLinks a:hover::before,
#siteLinks a.highlight:hover::before,

.btn-light:not([aria-label="Click to search"]):hover::before,
.btn-primary:hover::before,
.ui-tooltip a:hover::before
 {
  opacity: 1!important;
}

/*__ Red __________________________________________________________________________*/

.cc-window .cc-btn
{	
	background: radial-gradient(circle, #d90008a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.cc-window .cc-btn::before
{
	background: radial-gradient(circle, #000 0%, #d90008 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.cc-window .cc-btn:hover::before
 {
  opacity: 1!important;
}

/*___ Green ________________________________________________________________________*/

.button.fontDownload,
.btn-success,
.font-submission-btn,

footer #submitFont a.highlight
{	
	background: radial-gradient(circle, #5d8c1ba8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.button.fontDownload::before,
.btn-success::before,
.font-submission-btn::before,

footer #submitFont a.highlight::before
{
	background: radial-gradient(circle, #000 0%, #5d8c1b 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.button.fontDownload:hover::before,
.btn-success:hover::before,
.font-submission-btn:hover::before,

footer #submitFont a.highlight:hover::before
 {
  opacity: 1!important;
}

/*___ Plain Black __________________________________________________________________*/

#mainbody,

.show_ads .contentblock,
.ui-tooltip-tipsy
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

/*___ Plain ________________________________________________________________________*/

#fontCategories,
#sponsoredFonts,

ul.newList
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

#fontCategories:hover,
#mainbody:hover,
#sponsoredFonts:hover,

.show_ads .contentblock:hover,
.ui-tooltip-tipsy:hover,

ul.newList:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/*___ Red Gradient _________________________________________________________________*/

#header,
#tagPanel .activeTagDelete,

.navbar,
.cc-color-override--1298983837.cc-window,

ul.tags .tags__tag:hover
{
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
}

/*__ Code ___________________________________________________________________________*/

.preview-link
{
	background: #d4acac!important;
	background-size: 100%!important;
}

/*__________________________________________________________________________________*/

#pageNav .currentPage,
#tagPanel .activeTag,

.badge-light
 {
	background: #111!important;
	background: url(https://image.ibb.co/e8fWZk/red_carbon_pattern_by_mystica_264_d4ceu9m.png) #111!important;
	background: url(https://image.ibb.co/msdy5U/Red_Fabric.jpg) #111!important;
}

/*___ Highlight Background _________________________________________________________*/

.hiLite,
.newListHover
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}

/*___ Radial Background _________________________________________________________*/

.navbar .navbar-nav .nav-item .nav-link.aActive,
.navbar .navbar-nav .nav-item .nav-link:hover
{
	background: radial-gradient(ellipse at center, #b72133 0%,#0e0e0e00 70%)!important;
	padding: 13px 26px!important;
}


}

@-moz-document domain("1001fonts.com") {
/* Image Settings */

.btn:not(:disabled):not(.disabled),
.font-list .font-preview,
.font-list .txt-preview,
.lc0,.lc1,

ul.newList li:not(.hiLite)
 {
	filter: brightness(75%)!important;
}

/*__________________________________________________________________________________*/

.btn:not(:disabled):not(.disabled):hover,
.font-list .font-preview:hover,
.font-list .txt-preview:hover,
.hiLite,
.lc0:hover,.lc1:hover,

ul.newList li:hover
 {
	filter: brightness(100%)!important;
}

/*__________________________________________________________________________________*/

.fontPreview
 {
	border-radius: 22px!important;
}

/*__________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

/*__________________________________________________________________________________*/

.dimmed,.dimmer:hover .dimmed,
.hiLite
 {
	opacity: 1!important;
}

}

@-moz-document domain("1001fonts.com") {
/* New Icons */

input[type="checkbox"],
input[type="radio"] {
    appearance: none!important;
}

.fa, .fas {
    font-size: 31px!important;
}

/* Avatar ______________________________________________________________________________*/

.fa-user:before {
    content: "";
}

.fa-user:before {
	background: url(https://i.ibb.co/f2wDXnH/Avatar.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-user:before {
    background-size: 32px!important;
    height: 26px!important;
    padding-left: 33px!important;
    width: 1px!important;
}

/* Checkmark _______________________________________________________________________________*/

.custom-control-input:checked~.custom-control-label:before,
.custom-control-label:before {
    background-color: #fff0;
    border: none;
}

input[type="checkbox"] {
    appearance: none;
}

.custom-control-label:after, .custom-control-label:before {
    top: -7px;
}

/* Checked ___________________________*/

.custom-checkbox .custom-control-input:checked~.custom-control-label:after
{
	background: url(https://i.ibb.co/HgsTqFS/checkbox-checked.png) no-repeat!important;
	box-sizing: border-box;
}

/* Unchecked ________________________*/

.custom-checkbox .custom-control-input~.custom-control-label:after
{
	background: url(https://i.ibb.co/fM9ptv1/checkbox-empty.png) no-repeat!important;
	box-sizing: border-box;
}

.custom-checkbox .custom-control-input~.custom-control-label:after,
.custom-checkbox .custom-control-input:checked~.custom-control-label:after
{
    background-size: 27px!important;
    height: 34px!important;
    padding-left: 22px!important;
    width: 1px!important;
}

/* Close (X) ___________________________________________________________________________*/

.fa-times:before {
    content: "";
}

.fa-times:before
{
	background: url(https://i.ibb.co/kxQCnBQ/close-48x48.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-times:before
{
	background-size: 25px!important;
	height: 26px!important;
	padding-left: 26px!important;
	width: 1px!important;
}

/* Heart _______________________________________________________________*/

.fa-heart:before {
    content: "";
}

/* On ___________________*/

[data-original-title="Remove favorite"]
{
	background: url(https://i.ibb.co/FB9kX4W/healthy-heart.png) no-repeat!important;
	box-sizing: border-box;
}

/* Off ___________________*/

[data-original-title="Add to favorites"]
{
	background: url(https://i.ibb.co/cD452WP/healthy-hear-darkt-32x32.png) no-repeat!important;
	box-sizing: border-box;
}

[data-original-title="Add to favorites"],
[data-original-title="Remove favorite"]
{
    background-size: 24px!important;
    height: 27px!important;
    padding-left: 20px!important;
    width: 1px!important;
}

/* Link ___________________________________________________________________________*/

.fa-link:before {
    content: "";
}

.fa-link:before
{
	background: url(https://i.ibb.co/drymw1D/links1.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-link:before {
    background-size: 27px!important;
    height: 27px!important;
    padding-left: 27px!important;
    width: 1px!important;
}

/* Lock ___________________________________________________________________________*/

.fa-lock:before {
    content: "";
}

.fa-lock:before
{
	background: url(https://i.ibb.co/PDvx7bz/lock124x124.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-lock:before {
    background-size: 27px!important;
    height: 27px!important;
    padding-left: 27px!important;
    width: 1px!important;
}

/* Search ___________________________________________________________________________*/

.fa, .fas {
    font-size: 24px;
}

.fa-search:before {
    content: "";
}

.fa-search:before
{
	background: url(https://i.ibb.co/cJFxJM6/search3.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-search:before {
    background-size: 32px!important;
    height: 26px!important;
    padding-left: 33px!important;
    width: 1px!important;
}

/* Sign In ______________________________________________________________________________*/

.fa-sign-in-alt:before {
    content: "";
}

.fa-sign-in-alt:before {
	background: url(https://i.ibb.co/fpGB5C8/login.png) no-repeat!important;
	box-sizing: border-box;
}

.fa-sign-in-alt:before {
    background-size: 32px!important;
    height: 26px!important;
    padding-left: 33px!important;
    width: 1px!important;
}

/* Sticky Note _______________________________________________________________*/

.lc {
	background: url(https://i.ibb.co/VmC0y8y/postit.png) no-repeat!important;
	box-sizing: border-box;
}

.lc {
    background-size: 22px!important;
    height: 26px!important;
    padding-left: 22px!important;
    width: 10px!important;
}

}

@-moz-document domain("1001fonts.com") {
/* Scrollbar Settings */

/* Width 
_____________________________________________________________________________________________________*/


 ::-webkit-scrollbar {

 	width: 1.2em!important;
 }

 /* Color of handle 
__________________________________________________________________________________________________________________________________*/


 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track 
_____________________________________________________________________________________________________*/



 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom 
_____________________________________________________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of the bottom corner of the scrollable element, where two scrollbar meet. 
_____________________________________________________________________________________________________*/

::-webkit-scrollbar-corner
 {
 	background: #530205!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

::-webkit-scrollbar-corner:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

}
