style.css:
@-moz-document domain(wattsupwiththat.com) {
.container, .post-template-default .site-content .container {
	max-width: 100%;
}

.container, .page-template-default:not(.woocommerce) .site-content .container, .post-template-default .site-content .container {
	max-width: 100%;
}

body.boxed_content_layout #page {
	-webkit-box-shadow: 0 0 15px rgba(0,0,0,.1);
	box-shadow: 0 0 15px rgba(0,0,0,.1);
	margin: 0 auto;
	width: 80%;
	max-width: 100%;
	background: rgba(244,244,244,.3);
}

#comments {
	max-width: 100%;
	margin: 50px auto;
	color: #333;
	font-style: normal;
}

#wpdcom {
	max-width: 100%;
	padding: 0;
	margin: 7px auto 15px;
}

.btn-theme:hover, .mnp-top-header, .navigation-bar, .navigation-bar-top, button[type="submit"], input[type="reset"]:hover, input[type="button"]:hover, input[type="submit"]:hover {
	background-color: #650089;
}

.bottom-footer {
	background-color: #650089;
	padding: 15px 0;
	text-align: center;
	color: #bbb;
}

.back-to-top a, .btn-theme, .entry-meta .cat-links a, .entry-meta div.cat-links a, .featured-slider .owl-nav button.owl-next:hover::before, .featured-slider .owl-nav button.owl-prev:hover::before, .main-navigation ul li a:hover, .main-navigation ul li.current-menu-ancestor > a, .main-navigation ul li.current-menu-item > a, .main-navigation ul li.current_page_ancestor > a, .main-navigation ul li.current_page_item > a, .main-navigation ul li.show > a, .main-navigation ul li:hover > a, .mnp-news-ticker .mnp-news-ticker-label, .mnp-post-image-wrap .entry-meta .cat-links a, .mnp-post-image-wrap + .entry-header .entry-meta .cat-links a, .navbar .mnp-home-icon a, .navigation.pagination .current, .navigation.pagination a:hover, .post-slider .owl-nav button.owl-next:hover::before, .post-slider .owl-nav button.owl-prev:hover::before, .related-posts .owl-nav button.owl-next:hover::before, .related-posts .owl-nav button.owl-prev:hover::before, .search-toggle.active, .search-toggle:focus, .search-toggle:hover, body.title-style3 .widget-title-wrapper .magazinenp-title, body.title-style3 .widget-title-wrapper .widget-title, body.title-style4 .magazinenp-title::after, body.title-style4 .widget-title::after, button[type="submit"], input[type="reset"], input[type="button"], input[type="submit"] {
	background-color: #1151d3;
}

.navbar-main, body.title-style3 .widget-title-wrapper {
	border-bottom-color: #1151d3;
}
body {
	background-color: #1e1e1e;
}

body, input, select, textarea {
	color: #ccc;
	font: 15px Roboto,sans-serif;
	line-height: 1.7;
	word-wrap: break-word;
}

.mnp-popular-tags-wrap {
	background: #555;
    color: #bbb;
	border-top: 1px solid #aaa;
	border-bottom: 1px solid #ccc;
	padding: 5px 0;
}

.mnp-popular-tags-wrap ul.tags-list li a {
	padding: 4px 6px;
	border-radius: 2px;
	font-size: 12px;
	font-weight: 600;
	background: #650089;
    color: #bbf;
	border: 1px solid #f88;
}

.mnp-news-ticker {
	background-color: #999;
	position: relative;
	overflow: hidden;
	font-weight: 700;
	box-shadow: 0 1px 0 rgba(0,0,0,.07);
	-webkit-box-shadow: 0 1px 0 rgba(0,0,0,.07);
	-moz-box-shadow: 0 1px 0 rgba(0,0,0,.07);
	height: 50px;
}

.mnp-news-ticker .mnp-ticker-posts {
	display: block;
	margin-bottom: 0;
	position: relative;
	overflow: hidden;
	padding-left: 0;
	background-color: #999;
	height: 50px;
}

.mnp-news-ticker .mnp-ticker-posts::after {
	content: "";
	position: absolute;
	top: 0;
	right: 15px;
	bottom: 0;
	width: 80px;
	background-image: -webkit-gradient(linear,left top,right top,from(rgba(255,255,255,0)),to(#999));
	background-image: -webkit-linear-gradient(left,rgba(255,255,255,0),#999);
	background-image: -moz-oldlinear-gradient(left,rgba(255,255,255,0),#999);
	background-image: -o-linear-gradient(left,rgba(255,255,255,0),#999);
	background-image: linear-gradient(to right,rgba(255,255,255,0),#999);
}

.post, .type-attachment {
	background-color: #bbb;
	box-shadow: 0 1px 1px rgba(0,0,0,.1);
	-webkit-box-shadow: 0 1px 1px rgba(0,0,0,.1);
	-moz-box-shadow: 0 1px 1px rgba(0,0,0,.1);
	margin-bottom: 30px;
	padding: 25px;
}

.no-sticky-sidebar .widget {
	margin-bottom: 30px;
	padding: 15px;
	background-color: #888;
	box-shadow: 0 1px 1px rgba(0,0,0,.1);
	-webkit-box-shadow: 0 1px 1px rgba(0,0,0,.1);
	-moz-box-shadow: 0 1px 1px rgba(0,0,0,.1);
}

.widget_tag_cloud a {
	margin: 3px;
	color: #bbf;
    background-color: #650089;
	border: 1px solid #f88;
	padding: 0 10px;
	display: inline-block;
}

.navigation.pagination .page-numbers {
	display: inline-block;
	color: #333;
	padding: 15px 25px;
	margin: 0 1px 3px;
	background-color: #bbb;
	box-shadow: 0 1px 1px rgba(0,0,0,.1);
	-webkit-box-shadow: 0 1px 1px rgba(0,0,0,.1);
	-moz-box-shadow: 0 1px 1px rgba(0,0,0,.1);
}

.mnp-post-boxed, .post-block {
	position: relative;
	margin-bottom: 14px;
	background-color: #bbb;
}

div.mnp-post-image {
	background-size: contain !important;
	background-color: #bbb !important;
}

.mnp-post-image-wrap .entry-meta .cat-links a, .mnp-post-image-wrap + .entry-header .entry-meta .cat-links a {
	background-color: #1151d3;
	color: #fff;
	padding: 2px 5px 2px;
	margin-top: 5px;
	text-shadow: none;
	border-radius: 0;
	margin-right: 5px;
}

.navigation.post-navigation .nav-links {
	position: relative;
	background-color: #bbb;
	margin-bottom: 30px;
	box-shadow: 0 1px 1px rgba(0,0,0,.1);
	-webkit-box-shadow: 0 1px 1px rgba(0,0,0,.1);
	-moz-box-shadow: 0 1px 1px rgba(0,0,0,.1);
}

.comments-area, .site-main .type-page {
	box-shadow: 0 1px 1px rgba(0,0,0,.1);
	background-color: #bbb;
}

#wpdcom .ql-toolbar {
	padding: 8px 8px 3px 5px;
	background-color: #555;
	border-top: none;
	border-radius: 0 0 3px 3px;
	border-color: #ddd;
}

#wpdcom.wpd-layout-3 .wpd-comment .wpd-comment-header {
	background-color: #555;
	margin-left: -1px;
	padding: 10px 0 7px;
}

#wpdcom.wpd-layout-3 .wpd-comment .wpd-comment-subheader .wpd-comment-date {
	flex-grow: 1;
	font-size: 11px;
	line-height: 22px;
	color: #555;
	padding-top: 1px;
}

#wpdcom.wpd-layout-3 .wpd-comment .wpd-comment-footer .wpd-reply-button {
	background-color: #555;
	border: 1px solid #aaa;
	border-radius: 15px;
	padding: 1px 20px 1px 16px;
}

#wpdcom.wpd-layout-3 .wpd-comment .wpd-reply-to {
	font-size: 13px;
	display: flex;
	flex-direction: row;
	justify-content: flex-start;
	margin-left: 36px;
	margin-top: 1px;
	align-items: center;
	background-color: #ddd;
	padding: 5px 10px;
}

#wpdcom .wpd-comment-header .wpd-comment-share {
	color: #888;
	cursor: default;
}

#wpdcom .wpd-comment-header .wpd-comment-link {
	cursor: pointer;
	font-size: 12px;
	color: #888;
}

#wpdcom.wpd-layout-3 .wpd-comment .wpd-comment-right {
	padding-bottom: 0;
	border-left: 1px solid #555;
	border-bottom: 1px solid #555;
	width: calc(100% - 35px);
}

#wpdcom.wpd-layout-3 .wpd-comment .wpd-comment.wpd-reply:last-child .wpd-comment-footer {
	margin-bottom: 0;
	padding-bottom: 0;
	border-bottom: none;
    background-color: #c8c8c8
}

#breadcrumb {
	border-bottom: 1px solid rgba(0,0,0,.1);
	background-color: #999;
	padding: 15px 0;
}

body, input, select, textarea {
	color: #333;
	font: 15px Roboto,sans-serif;
	line-height: 1.7;
	word-wrap: break-word;
}

#custom_html-6, #custom_html-5 {
    display: none;
}
}
