style.css:
@-moz-document url-prefix("https://www.youtube.com") {
/*scrollbar override! Use whenever you like*/
  
/*fullscreen fix*/
ytd-app[scrolling]{
  overflow-x: hidden;
}
/*other*/
  


html[dark] {
    scrollbar-color: unset;
    background: #03142d !important;
}

html {
    scrollbar-color: unset;
      background: #03142d !important;
}

/* Scrollbar */
 ::-webkit-scrollbar {
    width: 15px !important;
    height: 15px !important;
    display: block !important;
    background: #03142d !important;
    background-image: url(https://cdn.discordapp.com/attachments/648916305998774301/793843800186552340/image_2.png), url(https://cdn.discordapp.com/attachments/648916305998774301/793843800186552340/image_2.png) !important;
    background-position: top center, bottom center !important;
    background-size: contain !important;
    background-repeat: no-repeat !important;
}
/* Thumb */
::-webkit-scrollbar-thumb {
    background: linear-gradient(-45deg, #12A0FF, #19D9FF) !important;
    border-left: 2px solid !important;
    border-right: 2px solid !important;
    border-color: #141414 !important;
    box-shadow: 0 0 5px linear-gradient(-45deg, #12A0FF, #BB00BB);
}
::-webkit-scrollbar-thumb:hover {
    transition: 1.5s;
    background: linear-gradient(-45deg, #0080DD, #06A6CC) !important;
    box-shadow: 0 0 10px linear-gradient(-45deg, #12A0FF, #BB00BB);
}
::-webkit-scrollbar-thumb:active {
    background: linear-gradient(-45deg, #A80505, #D6710D) !important;
    box-shadow: 0 0 10px linear-gradient(-45deg, #FF0000, #FFFF00);
}
/* Buttons */
::-webkit-scrollbar-button:single-button {
    background-position: center center;
    background-size: contain;
    display: block;
    height: 16px;
    width: 16px;
    background: #14141488;
}
::-webkit-scrollbar-button:single-button:hover {
    background: #14141444;
}
::-webkit-scrollbar-button:single-button:active {
    background: #14141422;
}


/*cinematic glow blocker*/
#cinematics.ytd-watch-flexy {
    display: none;
}
/*searchbar*/
.gsfs {
    color: #FFF;
    background: #03142d;
}
.sbfl_b {
    background: none;
    color: #12A0FF;
}
.sbqs_c {
    color: #FFF;
}
html[dark] .sbsb_a{
    background: #03142d;
}
html[dark] .sbdd_b {
    border: none;
    background: none;
    box-shadow: 0px 8px 25px 3px #0075ff;
}


/*merch?*/

ytd-merch-shelf-renderer[darker-dark-theme] {
    background-color: #051630;
}

ytd-menu-popup-renderer tp-yt-paper-listbox.ytd-menu-popup-renderer {
    background: #02142e;
}

/*Live chat final battle*/
ytd-watch-flexy[flexy][js-panel-height_] #chat.ytd-watch-flexy:not([collapsed]).ytd-watch-flexy, ytd-watch-flexy[flexy][js-panel-height_] #chat-container.ytd-watch-flexy:not([chat-collapsed]).ytd-watch-flexy {
    /* color: #FFF; */
    background: transparent;
}

ytd-live-chat-frame[modern-buttons][collapsed] #show-hide-button.ytd-live-chat-frame>ytd-toggle-button-renderer.ytd-live-chat-frame {
    background-color: #0b4489;
}


yt-live-chat-message-input-renderer {
    display: block;
    padding: 16px;
    color: #fff;
    background: #051c40 !important;
    font-size: 13px;
    position: relative;
    --yt-emoji-picker-renderer-content-margin: 0 24px;
    --yt-emoji-picker-category-margin-left: 24px;
}


#input-panel.yt-live-chat-renderer::after {
    content: none !important;
    display: block;
    height: 0px !important;
    margin: 0px 24px;
    background: transparent !important;
    color: transparent;
}


.yt-spec-button-shape-next--mono.yt-spec-button-shape-next--text {
    color: #ffffff;
    background: transparent !important;
}
.yt-spec-button-shape-next--mono.yt-spec-button-shape-next--text:hover {
    color: #ffffff;
    background: #082a60 !important;
}


yt-live-chat-header-renderer {
    height: 48px;
    padding: 8px;
    box-sizing: border-box;
    font-size: var(--yt-live-chat-header-font-size,18px);
    background-color: #0c2a58 !important;
    color: #FFF;
    display: flex;
    flex-direction: row;
    align-items: center;
    display: var(--yt-live-chat-header-renderer-display,flex);
}

#items.yt-live-chat-ticker-renderer {
    height: 32px;
    overflow: hidden;
    white-space: nowrap;
    padding: 0 24px 8px;
    background: #061b3c;
}
ytd-button-renderer.yt-chip-cloud-renderer {
    --yt-button-color: #3c1717;
    --yt-button-icon-size: 32px;
    border-radius: 16px;
    height: 32px;
    padding: 0;
    width: 32px;
    display: flexbox;
    display: flex;
    align-items: center;
    justify-content: center;
}

yt-live-chat-banner-manager[has-active-banner] {
    background: linear-gradient(180deg, #12A0FFDD,transparent, transparent) !important;
}


#items.yt-live-chat-ticker-renderer {
    height: 32px;
    overflow: hidden;
    white-space: nowrap;
    padding: 0 24px 8px;
    background: #061b3c;
}

#left-arrow.yt-chip-cloud-renderer, #right-arrow.yt-chip-cloud-renderer {
    z-index: 200;
    position: relative;
    height: 100%;
    display: flexbox;
    display: flex;
    flex-direction: row;
    justify-content: center;
}

