style.css:
@-moz-document url("https://toyhou.se/"), url-prefix("https://toyhou.se/~browse/feed"), url-prefix("https://toyhou.se/~browse/popular"), url-prefix("https://toyhou.se/~library") {
:root {
     /* Change the URL in quotes to whatever you want the bg to be. */
     --bg: url(https://i.ibb.co/WtYyCfc/Gravity-Falls.jpg);
 }
 /* FRONT PAGE*/
 body {
     font-family: -apple-system, system-ui, BlinkMacSystemFont, Segoe UI, Roboto, Helvetica Neue, Arial, sans-serif;
     font-size: .9rem;
     font-weight: 400;
     line-height: 1.5;
     color: #eef4ed;
     background-color: white;
     background-image: var(--bg);
     background-size: cover;
     max-height: 100vh;
     overflow-y: scroll;
     position: relative;
     background-attachment: fixed;
 }
 .card-block.bg-faded {
     background-color: rgba(0, 0, 0, 0.7);
 }
 .card.mb-2 {
     background-color: rgba(0, 0, 0, 0.0);
 }
 /* NAVIGATION BAR*/
 .navbar {
     background: rgba(0, 0, 0, 0.6);
 }
 .navbar-inverse .navbar-brand, .navbar-inverse .navbar-brand:focus, .navbar-inverse .navbar-brand:hover, .navbar-inverse .navbar-toggler, .navbar-inverse .navbar-toggler:focus, .navbar-inverse .navbar-toggler:hover {
     color: #a470ab;
 }
 .navbar-inverse .navbar-nav .nav-link {
     color: rgb(245, 235, 247, .75);
 }
 .navbar-inverse .navbar-nav .nav-link:focus, .navbar-inverse .navbar-nav .nav-link:hover {
     color: rgba(164, 112, 171, .75);
 }
 .navbar.dropdown-menu {
     background-color: rgba(0, 0, 0, 0.0);
     border: none;
     border-bottom: .125em solid repeating-linear-gradient(-45deg, #6c6351, #6c6351 15px, #6e6952 0, #6e6952 30px);
     margin: 0;
     border-top-right-radius: 0;
     border-top-left-radius: 0;
 }
 /* FOOTER BAR */
 .footer {
     background: rgba(0, 0, 0, 0.6);
 }
 /* FRONT PAGE CHARACTERS */
 .btn-primary {
     color: #fafaf6;
     border-color: #1b1b1b;
     opacity: 0.7;
 }
 .btn.btn-default {
     background-color: rgba(0, 0, 0, 0.6);
     border-color: none;
 }
 /* FEED & POPULAR CHARACTERS */
 .list-unstyled.gallery-row.mb-0 {
     background-color: rgba(0, 0, 0, 0.6);
 }
 .img-thumbnail {
     padding: .25rem;
     background-color: rgba(0, 0, 0, 0.6);
     border: 1px solid #545b75;
     border-radius: .25rem;
     transition: none;
 }

 .breadcrumb {
     padding: .75rem 1rem;
     margin-bottom: 1rem;
     list-style: none;
     background-color: rgba(0, 0, 0, .7);
     border-radius: .25rem;
 }
}

