style.css:
@-moz-document domain("mercadolibre.com.mx") {
/*-
Mercado Libre Modo Oscuro V7.1
Diseño: Lord Balliwns
Edicion: Lord Balliwns
Con el apoyo de Proyecto Tigger Swan y Dinastia Irbiloc
-*/
html {filter: invert(1); background: #000;}
* {font-weight: bold;}
img, icon, .nav-logo, svg, .payments-cho_badge, .ui-pdp-payment-sprites__icon-container, .ui-search-header--exhibitor, .ui-search-filter-dd.ui-search-filter-dd-color, .andes-badge--small.andes-badge--notification {filter: invert(1)}
/*----*/
.button-container.button-container--vertical.button-container--transparent, .cta, .action, .myml-ui-item__info.u-media-object__fill, .ch-btn.myml-ui-list-item-container__primary-action, .sales-list__message-action, .myml-ui-list-item__secondary-action.u-myml-float-right, .purchases-filters-navbar, .purchases-dropdown-filters__filter, .andes-breadcrumb__item, .ui-pdp-share, .ui-pdp-syi, .andes-button.andes-button--loud, .andes-button.andes-button--quiet, .ui-pdp-action-modal__link, .dashboard-header__link,  .help-footer__link, .tracking-details, .counterpart_action, .myml-ui-breadcrumb__element, .ui-vpp-rating__level__progress-bar-container .ui-vpp-rating__level__progress-bar__fill-background, .andes-tabs__slider, .ui-pdp-media__title a, .ui-pdp-media__action.ui-box-component__action, .carousel__link--view-more, .ui-recommendations__header__subtitle.ui-recommendations__headersubtitle, .myml-nav__section-icon, .nav-categs-departments, .payments-large, .rating-stars, .rating-form-action.ch-form-actions, .quality-meter-bar {filter: invert(1)}
/*----*/
.exhibitor__picture img, .section-details .ui-icon.ui-icon--mercado-pago, .myml-nav__section-icon.myml-nav__section-icon--credit_market svg {filter: invert(0)}
/*----*/
.ui-pdp-color--GREEN, .ui-pdp-seller__status-title, .advertising-vip-right .advertising-vip-right__items .item-list .item-description .item__benefits .ui-item__installments--free, .ui-recommendations .ui-item__installments--free,.ui-recommendations .ui-item__discount-text, .ui-recommendations .ui-item__shipping-free, .ui-search-color--LIGHT_GREEN, .ui-search-layout--gallery .ui-search-result .ui-search-item__promise--next_day .ui-search-item__promise__text, .ui-search-layout--grid .ui-search-result .ui-search-item__promise--next_day .ui-search-item__promise__text, .ui-search-layout--gallery .ui-search-result .ui-search-price__discount, .ui-search-layout--grid .ui-search-result .ui-search-price__discount, .ui-search-layout--stack .ui-search-result .ui-search-item__promise--next_day .ui-search-item__promise__text, .ui-search-item__shipping--free {color: #ff59af!important;}
.tracking-timeline__description--green, .advertising-vip-right .advertising-vip-right__items .item-list .item-description .item-discount {color: #c64ab5;}
.ui-search-layout--gallery .ui-search-result .ui-search-item__promise--next_day .ui-search-item__promise__text, .ui-search-layout--grid .ui-search-result .ui-search-item__promise--next_day .ui-search-item__promise__text, .ui-search-layout--stack .ui-search-result .ui-search-item__promise--next_day .ui-search-item__promise__text {background-color: #ddd}
.banner--success, .installments-list .status-tag--success {background-color: #ff59af;}
.tracking-timeline__dot--green, .ui-thermometer[value="5"] .ui-thermometer__level--5 {background-color: #c64ab5;}
.ui-thermometer[value="3"] .ui-thermometer__level--3 {background: #000fbb;}
.ui-search-animated-switch__switch-bar--on, .payment-data-section-leading:before {background: #cb7c05;}
.myml-ui-list-item-container__title.error {color: #46b5b7;}
.tracking-timeline__node--green, .tracking-submilestone__progress--green {border-left: 2px solid #c64ab5;}
.loans-summary--success {border-top: 3px solid #ff59af}
.myml-nav__menu .myml-nav__section-heading:hover {border-left-color: #cb7c05;}

}
