style.css:
@-moz-document domain("web.threema.ch") {
/* By Tancred#0001 */
/* Header and bottom */
div.conversation, #navigation-header, #main, #conversation #conversation-footer, #messenger #detail > div.ng-scope .detail-header, md-dialog, md-menu-content.md-default-theme, md-menu-content, md-dialog.md-default-theme, md-dialog, #messenger #detail > div.ng-scope, md-card._md {
    background-color: #212121;
}
.md-button.md-raised:not([disabled]) {
    background-color: #1a1a1a;
}
#navigation-conversations {
    background-color: #212121;
}
#navigation-conversations .conversation-wrapper .conversation.active, #navigation-conversations .conversation-wrapper .conversation:hover, #navigation-contacts .contact:hover {
    background-color: #121212;
    border-left: 8px solid #121212;
}
span.title.ng-binding, #main {
    color: #eee;
}
span.ng-scope, span.ng-binding.ng-isolate-scope, span.no-draft, #navigation-conversations .conversation-wrapper .conversation .conversation-box .message-box .latest-message .message-text, md-dialog.md-default-theme, .md-button.md-default-theme.md-primary, .ng-binding {
    color: #ccc;
}
#navigation-conversations .conversation-wrapper .conversation, #navigation-contacts .contact {
    border-left: 8px solid #212121;
    background-color: #212121;
}
#navigation-conversations .conversation-wrapper {
    border-bottom: 1px solid #151515;
}
#background-image {
    filter: brightness(30%)
}
#title #logo svg {
    fill: #bbb;
}
.material-icons.md-medium-dark {
    color: #aaa;
}
#conversation {
    background-image: url(https://web.threema.ch/img/wallpaper_light.png);
    background-blend-mode: overlay;
    background-color: #222;
}
#conversation-chat .chat .message-out .message-body, #conversation-chat .chat .message-out .bubble-triangle::after {
    background-color: #2c3429;
}
#conversation-chat .chat .message-in .message-body, #conversation-chat .chat .message-in .bubble-triangle:after {
    background-color: #111;
}
compose-area > div:first-child > div:nth-of-type(2) div.compose, #navigation-header .search input[type="text"] {
    background-color: #424242;
    border: none;
    color: white;
}
compose-area > div:first-child > div:nth-of-type(2) {
    border: none;
    background-color: transparent;
}
#navigation-header .search input[type="text"]:focus {
    outline: none;
}
canvas.qrcode {
    border: 5px solid white;
}
/* Login password */
.ng-valid.md-input.ng-dirty.ng-valid-parse.ng-touched.ng-empty::placeholder {
    color: #aaa;
}
/* Add contact */
.threema-id.ng-pristine.md-input.ng-empty.ng-invalid.ng-invalid-required.ng-valid-maxlength.ng-valid-minlength.ng-touched {
    border-color: #ccc;
}
/* Create group / New distribution list */
md-autocomplete.md-threema-theme {
    background: #424242;
}
md-autocomplete.md-threema-theme input, md-autocomplete.md-threema-theme input::placeholder {
    color: #ccc;
}
.md-autocomplete-suggestions-container.md-threema-theme {
    background: #424242;
}
.receiver-badge.contact-badge .contact-badge-identity {
    color: #ccc;
}
.md-button.md-raised:not([disabled]):hover, .md-button.md-default-theme.md-raised:not([disabled]).md-focused, .md-button.md-raised:not([disabled]).md-focused {
    background-color: #424242;
}
/* Top header (with name in it) */
#navigation-topheader {
    background-color: #212121;
    box-shadow: none;
    border-bottom: 1px solid rgba(0, 0, 0, 0.8);
}
/* Scroll bar */
::-webkit-scrollbar {
    border: 1px solid #212121;
    background-color: #212121;
}
/* Section divider */
#messenger.show-detail #navigation {
    border-right: none;
}
/* Icons */
.material-icons.md-dark, .material-icons.md-24.ng-scope.md-threema-theme {
    color: rgba(255, 255, 255, 0.87);
}
/* Unread notification border */
#navigation-conversations .conversation-wrapper .conversation.unread.active, #navigation-conversations .conversation-wrapper .conversation.unread {
    border-image: none;
    border-left: 5px solid #f44336;
    padding-left: 7px;
}
/* Pinned border */
#navigation-conversations .conversation-wrapper .conversation.starred.active, #navigation-conversations .conversation-wrapper .conversation.starred {
    border-image: none;
    border-left: 5px solid #ffc107;
    padding-left: 7px;
}
/* SVG and IMG icons */
.ng-scope.md-threema-theme.material-icons img, .message-icon.ng-isolate-scope img {
    -webkit-filter: invert(100%);
    /* safari 6.0 - 9.0 */
    filter: invert(100%);
}
/* Emoji picker */
.md-primary.emoji-trigger.trigger.is-enabled.material-icons:focus {
    outline: none;
}
/* Burger menu (triple dots) */
md-menu-content.md-threema-theme {
    background-color: #212121;
}
/* Settings window */
md-toolbar.md-threema-theme:not(.md-menu-toolbar) {
    background-color: #212121;
}
md-dialog.md-threema-theme {
    background-color: #424242;
}
.md-subheader.md-threema-theme {
    background-color: #212121;
}
.md-subheader-content .ng-scope {
    color: rgba(5, 166, 63)
}
md-radio-button.md-threema-theme:not([disabled]).md-primary.md-checked .md-off, md-radio-button.md-threema-theme:not([disabled]) .md-primary.md-checked .md-off, md-radio-button.md-threema-theme:not([disabled]).md-primary .md-checked .md-off, md-radio-button.md-threema-theme:not([disabled]) .md-primary .md-checked .md-off, md-radio-group.md-threema-theme:not([disabled]).md-primary.md-checked .md-off, md-radio-group.md-threema-theme:not([disabled]) .md-primary.md-checked .md-off, md-radio-group.md-threema-theme:not([disabled]).md-primary .md-checked .md-off, md-radio-group.md-threema-theme:not([disabled]) .md-primary .md-checked .md-off, md-radio-button.md-threema-theme .md-off {
    border-color: rgba(0, 0, 0, 0.54);
}
md-radio-button.md-threema-theme:not([disabled]).md-primary .md-on, md-radio-button.md-threema-theme:not([disabled]) .md-primary .md-on, md-radio-group.md-threema-theme:not([disabled]).md-primary .md-on, md-radio-group.md-threema-theme:not([disabled]) .md-primary .md-on {
    background-color: rgba(0, 0, 0, 0.54);
}
/* Troubleshoot window */
md-input-container.md-threema-theme .md-input, md-input-container .md-placeholder, md-input-container label:not(.md-no-float):not(.md-container-ignore) {
    color: #ccc
}
md-input-container.md-default-theme .md-input, md-input-container .md-input, md-input-container.md-threema-theme:not(.md-input-invalid).md-input-focused .md-input, md-input-container.md-threema-theme:not(.md-input-invalid).md-input-resized .md-input {
    border-color: #ccc;
}
/* About window */
._md.md-dialog-fullscreen.md-threema-theme.md-transition-in .md-dialog-content {
    color: #ccc;
}
}
