/* G L O B A L */

:root {
  --paddingimgproduit: 45vh;
  --paddingprofil: 30vh;
  --taille-header: 145px;
  --taille-header-negatif: -145px;
  --taille-avatar-user: 60px; /* Default : 50px, recommandé : 60px */
}

body {
 font-family:'Space Grotesk', sans-serif;
}

/* Avatar users plus gros */

.d-avatar {
    border-radius: 50px;
    height: var(--taille-avatar-user);
    width: var(--taille-avatar-user);
    margin-right: 20px;
  }

/* Marge commentaire plus grande */

.etco-comment-form {
    margin-left:80px !important;
}
   
   
/* Toutes les images légèrement arrondie */

img {
