/* Fond gradient au-dessus du header */
.pvi-hero-overlay::before {
	position: absolute;
	top: 0;
	left: 0;
	height: 35%;
	width: 100%;
	background: linear-gradient(to bottom,#0000009c 0,rgba(0, 0, 0, 0) 100%);
	content: "";
}

.pvi-hero {
  position: relative;
  top: -145px;
  margin-bottom: -145px;
  /* -webkit-mask-image: -webkit-linear-gradient(black, transparent 0%, black); */
}

div.d-grid-extend:nth-child(1) {
  margin-bottom: -145px;
}
.header::before {
 height:0px;
}

.pvi-hero::after {
  height: 100%;
  width: 100%;
  position: absolute;
  top: 0px;
  left: 0px;
  background-image: inherit;
  filter: blur(30px);
  background-clip: inherit;
  background-size: cover;
  -webkit-mask-image: linear-gradient(0deg, transparent 60%, black);
}

.pvi-product-scrating { /* Note sur image avec ombre */
	text-shadow: 5px 5px 5px #000000a3;
}

/* Boutons admin positionnés */

.pvi-hero-stats {
  position: absolute;
  top: 0;
  z-index: 9999999;
}

.pvi-distrib-button {
  top: 0;
  position: absolute;
  right: 70px;
  z-index: 9999999;
}

/* Page produit - Panneau éclaireur dark */

