a .elsh-product {
  border-radius: 10px;
}

/* Grandes images sur autres onglet page produit */

.d-cover-strip {
  position: relative;
  top: var(--taille-header-negatif);
  margin-bottom: var(--taille-header-negatif);
}

.d-cover-overlay {
  padding-top: var(--paddingimgproduit);
}


/* Transition Produit */

@-webkit-keyframes growIn { 
    0% { 
        -webkit-transform: scale(0.2); 
        opacity:0;
    } 
    50% { 
        -webkit-transform: scale(1.2); 
        
    } 
    100% { 
        -webkit-transform: scale(1); 
        opacity:1;
    } 
} 
@keyframes growIn { 
    0% { 
        transform: scale(0.2); 
        opacity:0;
    } 
    50% { 
        transform: scale(1.2); 
        
    } 
    100% { 
        transform: scale(1); 
        opacity:1;
    } 
}

.pvi-hero.naked .pvi-hero-overlay {
    padding-top: 175px;
  }
  
  /* Petits boutons action sur page produit */
  
  .d-button.pvi-product-scaction, .d-button.pvi-product-contextmenu {
    content: "";
    font-size: 0px;
    border-radius: 30px;
    width: 40px;
    height: 40px;
    background-color: #00000075;
  }
  
  .eins-current.blue, .eins-current.green, .eins-current.white .d-button.pvi-product-scaction {
    position: relative;
    left: -5px;
    top: 3px;
  }
  
  .eins-recommend.blue, .eins-review.blue, .eins-current.green, .eins-current.white  {
    position: relative;
    top: 3px;
    left: -4px;
  }
  
  .eins-sprite.eins-extend {
      position: relative;
      left: -2px;
  }

/* Transition home page */

.d-media.small.d-product-checked-wrapper.pvi-hero-figure { 
    animation: 0.5s ease-in-out 0s 1 growIn;
    animation-delay: 1s;
    animation-fill-mode: forwards;
