style.css:
@-moz-document domain(supramkv.com) {
   .xb-content-wrapper {
          background-color: #222 !important;
        background-image: url(data:image/png;base64,) !important;
      }
    .p-body-inner {
        background: #222 !important;
    }
    .is-sticky .p-nav {
        background: #222 !important;
    }
    .dataList-cell.dataList-cell--alt, .dataList-cell.dataList-cell--action {
        background: #222 !important;
    }
    .block-container {
        background-color: #444 !important;
        border-color: #444 !important;
        color: #ddd !important;
    }
    .dataList-cell.dataList-cell--highlighted {
        background: #333 !important;
    }
    .dataList-cell.dataList-cell:hover {
        background: #555 !important;
    }
    a {
        color: #fff !important;
    }
    .menu-content {
        background: #555 !important;
        color: #fff !important;
    }
    .menu-linkRow {
        color: #fff !important;
    }
    .menu-linkRow:hover {
        background: #888 !important;
    }
    h2.block-header {
        background-color: #000 !important;
    }
    .block.block-forumStats .block-container .block-minorHeader {
        background-color: #000 !important;
        color: #ddd !important;
    }
    .block-footer {
        color: #ddd !important;
        background: #111 !important;
    }
    .p-sectionLinks {
        color: #ddd !important;
        background: #111 !important;
    }
    .tabs--standalone {
        color: #ddd !important;
        background: #111 !important;
    }
    .tabs--standalone .tabs-tab.is-active {
        color: #ddd !important;
        background: #333 !important;
    }
    .block--category .block-header .block-header--left a {
        color: #fff !important;
        background-color: #444 !important;
    }
    * {
        border-color: #000 !important;
    }
    .block-body .node:hover {
        background-color:#222 !important;
    }
    a:hover {
        color:#ccc !important;
    }
    .menu-row.menu-row--alt {
        color: #ddd !important;
        background-color: #444 !important;
    }
    .menu-tabHeader {
        background-color: #222 !important;
        color: #aaa !important;
    }
    .menu-tabHeader .tabs-tab.is-active {
        color: #fff !important;
    }
    .menu-footer {
        background: #222 !important;
    }

    .message-inner {
        background: #333 !important;
        color: #ddd !important;
    }
    .message .message-cell.message-cell--user {
        background: #222 !important;
    }
    .bbCodeBlock--expandable {
        background: #222 !important;
        color: #ddd !important;
    }
    .bbCodeBlock-title {
        background: #111 !important;
    }
    .pageNav-jump {
        background: linear-gradient(0deg, #000, #222) !important;
    }
    .p-title {
        color: #fff !important;
    }
    .pageNav-page {
        background: linear-gradient(0deg, #000, #222) !important;
    }
    .pageNav-page--current {
        background: #7e0101 !important;
    }
    .button.button--link, a.button.button--link {
        background: #444 !important;
        color: #ddd !important;
    }
    .input:focus, .input.is-focused {
        background: #333 !important;
    }
    .input {
        color: #ddd !important;
        background: #333 !important;
    }
    ::placeholder {
      color: #ddd !important;
    }
    .fr-toolbar {
        color: #ddd !important;
        background: #333 !important;
    }
    .fr-toolbar .fr-command.fr-btn, .fr-popup .fr-command.fr-btn {
        color: #ddd !important;
    }
    .fr-box.fr-basic .fr-wrapper {
        background: #555 !important;
    }
    .formRow > dt {
        background: #333 !important;
    }
    .structItem-pageJump a {
        background: #333 !important;
    }
    .message .reactionsBar {
        background: #222 !important;
    }
    .block-tabHeader {
        background: #333 !important;
    }
    .formSubmitRow-bar {
        background: #333 !important;
    }
    .inputGroup.inputGroup--joined .inputGroup-text {
        background: #333 !important;
        color: #ddd !important;
    }
    .button.button--alt, a.button.button--alt {
        background-color: #444 !important;
    }
    .blockLink.is-selected {
        background: #333 !important;
    }
    .blockLink:hover {
        background: #666 !important;
    }
    .p-body-sidebar .block .block-minorHeader, .p-body-sideNavContent .block .block-minorHeader {
        color: #ddd !important;
        background: #222 !important;
        border: 1px solid #000 !important;
        box-shadow: inset 0px 1px 0px 0px #1a0000 !important;
    }
    .js-alertsMenuBody .alert.is-unread, .alert-list .alert.is-unread {
        color: #ddd !important;
        background: #555 !important;
    }
    .js-alertsMenuBody .alert.is-recently-read{
        color: #ddd !important;
        background: #333 !important;
    }
	.js-alertsMenuBody .alert.is-read
    {
        color: #ddd !important;
        background: #333 !important;
    }
    .reaction--1 .reaction-text {
        color: #fff !important;
    }
  .memberHeader-content {
      background: #333 !important;
  }
  .structItemContainer-group.structItemContainer-group--sticky::before {
      color: #ddd !important;
      background: linear-gradient(0deg, #333, #444) !important;
  }
  .structItemContainer-group.structItemContainer-group--sticky::after {
      color: #ddd !important;
      background: linear-gradient(0deg, #444, #333) !important;
  }
  .block-container .structItemContainer-group .is-sticky {
      background: #333 !important;
  }
.tagItem {
    background: #666 !important;
}
.fr-box.fr-basic .fr-element {
    color: #fff !important;
}
.tooltip--preview .tooltip-content-inner .tooltip-content-cover {
    background: transparent !important;
}
.tooltip--preview .tooltip-content {
    background: #555 !important;
}
.bbWrapper {
    color: #fff !important;
}
.XenBase label.iconic i {
    color: #ccc !important;
}
  .bbCodeInline {
	background: transparent !important;
	border: transparent !important;
}
}
