style.css:
@-moz-document url-prefix("https://dic.nicovideo.jp/") {
/* main */
body#basebody{background-color: #2b2b22;}
#header{background-color: #5b5b5b;}
.st-box,.st-box_side,.index-weekly-recommend-piko, .index-weekly-recommend-article, .index-option-recommend-tool, .index-option-recommend-service,.index-weekly-recommend-piko-contents, .index-weekly-recommend-article-contents, .index-option-recommend-tool-contents, .index-option-recommend-service-contents,.index-weekly-recommend, .index-option-recommend,.index-weekly-recommend-piko-info small,.index-weekly-recommend-article-detail,.index-newly-list{background-color: #404040;color: #dddddd;}
a:link, a:visited, a.nicovideo:link, a.dic:link, a.ext:link, a.auto:link, a.nicovideo:visited, a.dic:visited, a.ext:visited, a.auto:visited,a.auto-hdn:link, a.auto-hdn:visited{color: #51aa1f;}

.st-box_heading-pushword::before,.side-pushword_counter_clap::before, .side-praised_counter_clap::before,.st-box_heading-kaite::before{mix-blend-mode: color-burn;}

/* twitter */
div.twitter_nicopedia,.timeline-Widget,.twitter-forrow{background-color: #404040;}
.timeline-Tweet:hover{background-color: #515151;}
.Identity-screenName{color: #a4a4a4;}
.timeline-Tweet-inReplyTo > .u-linkBlend{color:#c1c1c1}
.SandboxRoot{color: #dddddd;}
.timeline-Header-title{color:#e1e1e1}

/* article */
.a-title{background-color: #404040;color: #dddddd;}
.a-title h1{color: #ddd;}
.a-contents{background-color: #404040;}
div[class="a-contents"]{color:#ddd}

.article-title-counter-homeru::before{opacity: 1;mix-blend-mode: screen;}

div[class="a-list_relationSearch"] > div > a > img{mix-blend-mode:screen}
div.article th{background-color:#333}

/* 文字色系総当たり */
div[id="article"][class="article"] > p > span[style="color: #ff0000;"]{color:#ff4e4e !important}
div[id="article"][class="article"] > p > span[style="color: #ffffff;"]{color:#404040 !important}
div[id="article"][class="article"] > p > span[style="color: #ffffff;"] > a[style="color: #ffffff;"]{color:#404040 !important}
div[id="article"][class="article"] > p[style="font-size: large; color: #ffffff;"],div[id="article"][class="article"] > p[style="font-size: large; color: #ffffff;"] > a[style="color: #ffffff;"]{color:#404040 !important}
div[id="article"][class="article"] > p > span[style="color: #ffffff;"] > span > a[style="color: #ffffff;"]{color:#404040 !important}
div[id="article"][class="article"] > p[style*="color: #ffffff;"]{color:#404040 !important}
div[id="article"][class="article"] > p[style*="color: #ffffff;"] > a[style="color: #ffffff;"]{color:#404040 !important}
div[id="article"][class="article"] > p > b > span[style="color: #000000;"]{color:#ddd}

/* 記事終わりの方 */
.mobile-qr-code-container{border: 1px solid #8c8c8c;background-color: #797979;}
.st-button_homeru,.st-button_nicoad,.a-button_watchlist{background-color: #5b5b5b;}
.st-button_nicoad-campaign a::after{top: 1.2px;}
[title="ニコニコ市場で検索"] > img{mix-blend-mode:unset !important}

/* 掲示板 */
.st-bbs,.st-bbs-contents,.bpost-oekaki,.bpost-oekaki_contents,.bpost-pico,.bpost-pico_contents{background-color: #404040;}
.st-bbs h2::before{mix-blend-mode: color-burn;}

.st-bbs h2,.st-bbs-contents > dl > dd.st-bbs_resbody,.st-bbs_resNo,.bpost-postForm_item,.bpost-area_checkbox,.bpost-list ul,.bpost-oekaki h1,.bpost-oekaki_contents,.bpost-pico,.bpost-pico_contents{color: #ddd;}
.st-bbs_name{color: #d5a345;}
.st-button_bbsRes, .st-button_premium{color: #444444;}
div.res_preview_normal{color: #c1c1c1;font-size: 0.85em;}



}
