style.css:
@-moz-document domain("studio.fitbit.com") {
    .pt-non-ideal-state {
        width: 100%;
        max-width: 100%;
        background-color: #18181A;
    }

    .pt-spinner .pt-spinner-head {
        stroke: #6495ed;
    }

    .pt-non-ideal-state-title,
    .pt-non-ideal-state-description {
        color: #ccc;
    }

    .pt-toast {
        background-color: #18181A;
    }

    .pt-toast:not([class*="pt-intent-"]) {
        border: 1px solid #6495ed;
        box-shadow: 0 0 12px 1px #6495ed;
    }

    .pt-toast:not([class*="pt-intent-"]) .pt-icon-standard {
        color: #ccc;
    }

    .pt-toast.pt-intent-success {
        ;
        border: 1px solid #13b974;
        box-shadow: 0 0 12px 1px #1ddd8e;
    }

    .pt-toast.pt-intent-danger {
        border: 1px solid #f23737;
        box-shadow: 0 0 12px 1px #ee5252;
    }

    .pt-dialog {
        background: #18181A;
        color: #ccc;
        border: 1px solid #6495ed;
        box-shadow: 0 0 12px 1px #6495ed;
    }

    .pt-dialog .pt-dialog-body ul {
        max-height: 65vh;
        overflow: auto;
    }

    .pt-dialog .pt-dialog-header {
        background: #000;
    }

    .pt-dialog .pt-dialog-header * {
        color: #ccc;
    }
    .pt-dialog .pt-dialog-header .pt-dialog-close-button:hover {
        transition: all 0.3s ease;
        color: #737373;
    }
    .pt-dialog .pt-non-ideal-state {
        background: #18181A;
    }

    .pt-button:hover {
        background-color: #32373B !important;
    }

    button.pt-button.pt-icon-build.pt-disabled:not([class*="pt-intent-"]) {
        font-weight: bold;
        -webkit-animation: glow 1.5s ease-in-out infinite alternate;
        -moz-animation: glow 1.5s ease-in-out infinite alternate;
        animation: glow 1.5s ease-in-out infinite alternate;
    }

    .pt-button.pt-intent-primary:not(.pt-disabled):not(.logout-button),
    main a.pt-button.pt-large[class*="pt-intent-"],
    main a.pt-button.pt-large[class*="pt-icon-"] {
        color: #18181A !important;
        background: #6495ed !important;
    }

    .pt-button.pt-intent-primary:hover:not(.pt-disabled):not(.logout-button),
    main a.pt-button.pt-large[class*="pt-intent-"]:hover:not(.pt-disabled),
    main a.pt-button.pt-large[class*="pt-icon-"]:hover:not(.pt-disabled) {
        transition: all 0.3s ease;
        background: #6495ed !important;
        box-shadow: 0 0 12px 1px #6495ed;
    }

    .pt-button:not([class*="pt-intent-"]):not(.pt-minimal):not(.pt-icon-cross) {
        background: #f5f8fa;
        color: #182026;
    }

    .pt-button:hover:not([class*="pt-intent-"]):not(.pt-minimal):not(.pt-icon-cross) {
        background: #f5f8fa !important;
        box-shadow: 0 0 12px 1px #f5f8fa;
    }

    .pt-button.pt-intent-danger:hover {
        background-color: #db3737 !important;
        box-shadow: 0 0 12px 1px #db3737;
    }

    main a.pt-button.pt-icon-add.pt-large::before {
        color: #18181A !important;
    }


    .pt-button.pt-minimal.pt-intent-primary.logout-button {
        color: #ccc;
    }

    a.pt-button.pt-minimal.pt-intent-primary.logout-button:hover {
        background-color: #32373B !important;
    }

    .project-create .pt-button:not([class*="pt-intent-"]) {
        background: #ccc;
        background-image: none;
    }

    .project-create .pt-button:not([class*="pt-intent-"]):hover {
        box-shadow: none;
        background-color: #989a9d !important;
    }

    .pt-navbar {
        background-color: #000 !important;
    }

    .fileBrowserPane {
        background-color: #18181A;
    }
    .fileBrowserPane > span > div:not(.options-selected):not(.directory-hover) {
        background-color: #18181A;
        color: #eaeaea;
    }

    .fileBrowserPane div.directory-hover,
    .fileBrowserPane div.directory-hover .pt-non-ideal-state {
        background: #32373B;
        color: #eaeaea;
    }

    .fileBrowserPane div:not(.directory-hover) .pt-tree-node-icon {
        color: #6495ed;
    }

    .fileBrowserPane .pt-tree-node-label {
        font-size: 15px;
    }

    .fileBrowserPane .pt-tree-node-caret:hover {
        color: #6495ed;
    }

    .pt-tree-node.pt-tree-node-selected > .pt-tree-node-content {
        background-color: #1a7ec1;
    }

    .pt-button[class*="pt-icon-"]::before {
        color: #ccc;
    }

    .pt-tab-panel,
    .console-container,
    .pt-tabs,
    .Pane,
    .pt-flex-expander,
    .console-item-error,
    .diagnostics-pane {
        background-color: #18181A;
    }

    .pt-tab-panel .console-container {
        border-top: none;
    }

    .ui-container {
        background-color: #18181A;
    }

    .console-item-info,
    .console-line {
        color: #ccc;
    }

    .console-item-error {
        color: #d03636;
        font-weight: bold;
        text-shadow: none;
    }

    .console-item-exception {
        color: #d03636;
        font-weight: bold;
        text-shadow: none;
        background-color: #000;
    }

    .console-item-warn {
        color: #FFCC33;
        background-color: #18181A;
    }

    .pt-tree-node-content:hover {
        /* 	background-color: #383B3D; */
        background: #32373B;
    }


    .pt-tabs {
        border-bottom: none;
    }

    .pt-tab-list .pt-tab .diagnostic-title {
        color: #ccc;
    }


    .pt-tab-list .pt-tab:hover .diagnostic-title {
        font-weight: bold;
    }

    .pt-tab-indicator {
        background-color: #ccc !important;
    }

    .console-container .console-line {
        border: none;
        border-bottom: 2px solid #333 !important;
    }

    main > header > h1 {
        color: #ccc;
    }


    ul.pt-menu.project-list-menu {
        border-radius: 0;
        border-color: #32373B;
        overflow:auto;
        max-height:calc(100vh - 260px);
    }

    div.project-list-item {
        border-radius: 0;
        background-color: #18181A;
        color: #ccc;
    }

    div.project-list-item a.pt-menu-item {
        display: inline-block;
        width: 100%;
        padding: 7px 30px;
    }

    div.project-list-item a.pt-menu-item:hover {
        border: 1px solid #6495ed !important;
        box-shadow: 0 0 12px 1px #6495ed !important;
    }

    .pt-menu-item:hover,
    .pt-submenu > .pt-popover-open > .pt-menu-item {
        background-color: #176eaa;
        cursor: pointer;
    }

    .editor-container .pt-non-ideal-state {
        background-color: #18181A;
    }

    .Resizer {
        background-color: #32373B;
        opacity: 1;
    }

    .Resizer.vertical:hover {
        border-left: 5px solid #6495ed;
        border-right: 5px solid #6495ed;
        opacity: 0.4;
    }

    .Resizer.horizontal:hover {
        border-top: 5px solid #6495ed;
        border-bottom: 5px solid #6495ed;
        opacity: 0.4;
    }

    .editorPane .pt-non-ideal-state-title,
    .editorPane .pt-non-ideal-state-description,
    .editorPane .pt-non-ideal-state-icon {
        color: #ccc !important;
    }

    .fileBrowserPane .pt-non-ideal-state-title,
    .fileBrowserPane .pt-non-ideal-state-description {
        color: #ccc;
    }

    .project-create {
        color: #ccc;
    }


    .project-create input[type="text"] {
        color: #ccc;
        background-color: #000;
    }

    .project-create input[type="text"]:not(.pt-intent-danger) {
        border: 1px solid #6495ed;
    }
    .project-create input[type="text"]::placeholder {
        color: #626161;
    }

    .project-create .pt-card {
        color: #ccc;
        background-color: #353535;
    }

    .project-create-template-chooser input:checked ~ .radio-card {
        border: 2px solid #6495ed;
    }

    .project-create .pt-card h5 {
        color: #ccc;
    }

    .project-create form {
        background-color: #000;
        border: 1px solid #6495ed;
    }

    .diagnostic-item-message {
        color: #ccc !important;
    }

    .pt-popover .pt-popover-arrow .pt-popover-arrow-fill {
        fill: #6495ed;
    }
    .pt-popover .pt-menu {
        border: 1px solid #6495ed;
        background: #18181A;
        color: #ccc;
        box-shadow: 0 0 12px 1px #6495ed;
    }

    .pt-popover .pt-menu .pt-menu-divider {
        border-color: #6495ed;
    }

    .pt-popover .pt-menu .pt-menu-item {
        transition: all 0.17s ease;
    }

    .pt-popover .pt-menu .pt-menu-item[class*="pt-icon-"]:not([class*="pt-intent-"]):before {
        color: #6495ed;
    }

    .pt-dark .pt-navbar-divider {
        border-left-color: #646464;
    }

    /** NOTE: BUILD BUTTON */
    .pt-navbar-group > button.pt-button.pt-intent-success {
        color: rgb(46, 225, 151) !important;
    }

    .pt-dark .pt-button-group.pt-minimal .pt-button::after {
        background: #646464;
    }

    .fileBrowserPane .options-selected {
        background-color: rgba(92, 134, 166, .4)
    }

    .fileBrowserPane > span > div:not(.options-selected):not(.directory-hover) {
        color: #eaeaea;
    }

    .project-configuration-editor.pt-dark {
        background: #18181A;
    }

    .project-configuration-editor.pt-dark select,
    .project-configuration-editor.pt-dark select:active,
    .project-configuration-editor.pt-dark select:hover,
    .project-configuration-editor.pt-dark select option,
    .project-configuration-checkbox-group .pt-card.pt-interactive,
    .project-configuration-supported-locales .pt-card,
    .project-configuration-app-cluster-storage .pt-card {
        background: #1e1e1e;
    }

    .pt-input:focus {
        border: 1px solid #6495ed !important;
        box-shadow: 0 0 12px 1px #6495ed !important;
    }

    .project-configuration-color-area {
        background-color: #2b2a2a !important;
    }

    .project-configuration-editor.pt-dark select:focus {
        border: 1px solid #6495ed;
        box-shadow: 0 0 12px 1px #6495ed;
    }

    .pt-dark .pt-card.pt-interactive {
        border: 1px solid transparent;
        margin: 6px 0;
    }

    .pt-dark .pt-card.pt-interactive:hover {
        border: 1px solid #6495ed;
        box-shadow: 0 0 12px 1px #6495ed;
    }

    .project-configuration-editor.pt-dark input[type="text"] {
        background: #18181A;
    }

    .project-configuration-editor.pt-dark #file-name {
        background: #2b2a2a;
    }

    @keyframes glow {
        from {
            color: #fff;
            text-shadow: 0 0 10px #6495ed, 0 0 20px #6495ed, 0 0 30px #6495ed, 0 0 40px #6495ed, 0 0 50px #6495ed, 0 0 60px #6495ed, 0 0 60px #6495ed, 0 0 60px #6495ed;
        }
        to {
            color: #dddcdc;
            text-shadow: 0 0 20px #6495ed, 0 0 30px #6495ed, 0 0 40px #6495ed, 0 0 50px #6495ed, 0 0 60px #6495ed, 0 0 60px #6495ed, 0 0 60px #6495ed, 0 1 60px #6495ed;
        }
    }
}
