style.css:
@-moz-document regexp("chrome-extension://.*") {
::-webkit-scrollbar {
	width: 10px
}
#header::-webkit-scrollbar {
	width: 4px
}
#header::-webkit-scrollbar-thumb {
	background: #222
}
::-webkit-scrollbar-thumb {
	background: #272727
}
::-webkit-scrollbar-thumb:hover {
	background: #333
}
div::-webkit-scrollbar-thumb {
	background: #444
}
div::-webkit-scrollbar-thumb:hover {
	background: #555
}

input[type="checkbox"]:not(.slider):hover, .style-name:hover input[type="checkbox"]:checked, .style-name:hover input[type="checkbox"] {
	background: #111;
	border-color: #ccc;
}
#header, #options, #filters label:hover, #filters .filter-selection:hover {
	color: #b9b9b9;
	background: #111;
	border-color: #333
}
#sections {
	height: unset
}
#header {
	border-right: none!important;
	padding: 0;
	padding-left: 5px;
	box-shadow: none
}
.filter-selection {
	left: 0
}
.filter-selection select {
	width: 230px!important
}
#header .filter-selection label, .filter-selection select {
	height: 24px;
}
#header .filter-selection label .checkmate {
	margin-top: 0
}
#manage-text {
	margin-top: 5px
}
#search-wrapper {
	margin-bottom: 0;
	width: 240px
}
#options-wrapper {
	margin-top: 8px
}
.options-column > div[class="option"] {
	margin-bottom: 8px
}
#header summary, #header #filters summary {
	margin: 0;
}
#mozilla-format-buttons {
	margin-top: 5px;
	margin-bottom: 5px
}
#header h1 {
	text-align: center;
	margin: 0;
	padding-top: 8px;
	padding-bottom: 8px;
	font-size: 22px;
	font-weight: 1000
}
#header summary h2 {
	border-bottom: none!important;
	font-size: 13px
}
#filters {
	border-bottom: 1px dashed #333;
	margin-bottom: 8px
}
#sort-wrapper, #style-actions {
	width: 241px;
	margin-bottom: 0px;
	margin-top: 8px;
	margin-left: 13px;
}
#searchMode {
	box-shadow:-3px 0 2px -3px #6d6d6d!important;
	background: #222!important;
	border-radius: 0;
	width: 86px!important
}
.section-editor .section, .section-editor .section:not(:first-child) {
	margin: 10px;
	margin-bottom: 10px;
	padding: 0;
	padding-bottom: 10px;
	border: none;
	border-radius: 5px;
	background: #151515;
	box-shadow: 0 0 10px -5px black;
}
#sections > .section:not(.removed) > label {
	width: calc(100% - 20px);
	background: #151515!important;
	padding: 6px 10px;
	border-radius: 5px;
	box-shadow: 0px 3px 4px -3px #00000082
}
#sort-wrapper .select-arrow, .svg-icon.select-arrow, .filter-selection .select-arrow {
	margin: 0;
	height: 100%;
	width: 14px;
	top: 0;
	right: 4px;
}
#header details, #header details:not([open]), #mozilla-format-container {
	background: #111;
	margin: 0;
	margin-left: 4px;
	margin-right: 5px;
	margin-bottom: 8px;
	padding: 8px;
	padding-left: 8px;
	padding-right: 0;
	border: 1px dashed #333;
}
#mozilla-format-container h2 {
	margin: 0;
	margin-bottom: 8px
}
#mozilla-format-container {
	margin-top: 15px;
	width: 255px
}
#basic-info-enabled {
	justify-content: center
}
#basic-info, #stylus-edit #actions {
	padding-left: 4px
}
#stylus-edit #actions {
	padding-bottom: 8px
}
#search, #manage\.newUI\.sort, input:not([type]), #newUIoptions input[type="number"], #options [type="number"], select {
	background: #222;
	color: #b9b9b9;
	font-weight: bold;
	font-size: 13px;
	padding: 0;
	padding-left: 10px;
	height: 30px;
	border: none;
	border-radius: 3px;
	box-shadow: 2px 2px 0 0 #0d0d0d;
}
#newUIoptions input[type="number"], #options [type="number"] {
	height: 25px;
	margin-top: 8px;
	margin-bottom: 8px;
	margin-left: 3px;
}
input[type=number]::-webkit-inner-spin-button {
  height: 25px;
	cursor: pointer;
	opacity: .2; 
}
select {
	width: 123px;
}
.applies-to select {
	width: 360px
}
.dropbtn {
	width: 110px
}
#options-buttons button {
	width: 79px
}
#filters select {
	background: #111;
	box-shadow: none
}
button, #backup-buttons .dropbtn, #options-buttons button, #backup-buttons button {
	background: #222;
	border: none;
	margin: 0;
	margin-right: 2px;
	margin-left: 2px;
	padding: 5px;
	color: #b9b9b9;
	font-weight: bold;
	font-size: 12px;
	border-radius: 3px;
	box-shadow: 2px 2px 2px -2px #000;
	transition-duration: .2s
}
button:hover, button:not(:disabled):hover, #options-buttons button:hover, #backup-buttons button:hover {
	background: #262626;
	box-shadow: 4px 4px 4px -4px #000;
}
#actions button, #actions > div > a {
	margin: 0;
	margin-right: 2px;
	margin-left: 2px;
	width: 86px
}
.svg-icon, .svg-icon.info:hover, .svg-icon.settings:hover {
	fill: #777;
}
a:hover .svg-icon.info, a:hover .svg-icon, .svg-icon:hover, .newUI .entry:hover .svg-icon:hover, input[type="checkbox"]:not(.slider):checked + .svg-icon.checked, .svg-icon.select-arrow {
	fill: #ccc;
}
body {
	color: #b9b9b9;
	background: #111;
}
#name {
	margin-bottom: 8px;
	width: 98%;
}
.newUI .entry > * {
	border-bottom: 1px dashed #1a1a1a;
	padding-bottom: 1px;
	padding-top: 2px
}
#add-style-label {
	background: #063a06
}
#add-style-label:hover {
	background: #024702
}
.newUI .entry.odd {
	background: #111
}
.newUI .entry.odd:hover {
	background: #111
}
#message-box > div, #message-box-buttons, #message-box-title {
	background: #1d1d1d;
}
#message-box.danger #message-box-title {
	background: firebrick;
}
#help-popup {
	background: #1c1c1c;
}
.newUI .target img {
	border-radius: 50%;
}
.newUI .target {
	line-height: 17px;
}
.newUI .style-name {
	font-size: 12px;
	text-indent: unset;
	padding-left: 15px;
}
.entry .checkmate, .entry input[type="checkbox"]:not(.slider) {
	height: 16px;
	width: 16px
}
.entry .svg-icon.checked {
	height: 12px;
	width: 12px
}
.newUI .has-favicons .target {
	font-size: 11px;
}
a, a:visited {
	color: #b9b9b9;
}
a:hover, .disabled a:hover,.newUI .entry.enabled .style-name:hover .style-name-link {
	color: #eee
}
.newUI .no-update:not(.update-problem):not(.update-done) .up-to-date, .newUI .can-update .update {
	color: black;
}
#reset-filters svg {
	height: 15px;
	padding: 0
}
.active #filters-stats {
	padding: 0 5px
}
#toc {
	padding: 0;
	padding-top: 5px
}
#toc li {
	padding: 5px 0;
	font-size: 11px;
	margin-right: 8px
}
.single-editor {
	height: calc(100% - 20px);
}
.single-editor .CodeMirror {
	width: unset
}
.CodeMirror {
	border-radius: 10px;
	margin-top: 10px;
	margin-bottom: 5px!important;
	font-size: 13px;
	outline: transparent;
	border: none;
}
.resize-grip {
	border:none
}
.resize-grip:after {
	border-color: #444
}
.CodeMirror-search-field {
	width: 100%!important
}
.CodeMirror-gutters {
	padding: 0!important
}
.CodeMirror-linenumber {
	padding: 0 3px
}
.CodeMirror, .applies-to, .edit-actions{
	margin-left: 8px;
	margin-right: 8px
}
.applies-to label {
	height: 30px;
	margin-right: 8px
}
.add-applies-to, .remove-applies-to {
	height: 30px;
	margin-left: 4px
}
#confirm > div, .menu-items-wrapper {
	background: #202020;
	border: solid 2px rgba(0, 0, 0, 0.52);
}
#backup-buttons .dropdown-content {
	display: block;
	background: #222;
	min-width: unset;
	width: 108px;
	box-shadow: none;
	left: 2px;
	top: 0;
	border: 1px solid #333;
	border-radius: 2px
}
#backup-buttons .dropdown-content a {
	color:#b9b9b9;
	padding: 5px 8px
}
#backup-buttons .dropdown-content a:hover {
	background: #222;
	color:#eee
}
#sync-dropbox-export, #sync-dropbox-import {
	display: none!important
}
#manage-text {
	padding-left: 5px;
	padding-bottom: 8px;
}
#manage-text a {
	background: #141414;
	border: 1px solid #1a1a1a;
	padding: 5px;
	border-radius: 3px;
	box-shadow: 2px 2px 0 0 #0d0d0d;
	transition-duration: .2s;
	text-decoration: none
}
#manage-text > :not(:last-child):after {
    content: "";
    margin: 0 2px;
}
#update-all-no-updates:before {
	content: ">>> ";
}
#update-all-no-updates {
	margin-left: 35px
}
#backup-buttons button {
	margin-top: 5px;
	width: 248px
}
#backup-buttons button:not(#sync-styles) {
	width: 122px;
}
#check-all-updates, #manage-options-button {
	width: 220px!important
}
#add-style-wrapper > a, #add-style-wrapper button {
	width: 100px!important;
	margin-bottom: 12px;
	margin-top: 5px;
}

@media (max-height: 730px) {
	#options-buttons button {
		width: 78px
	}
	#manage-text a {
		padding: 4px
	}
}
@media (max-width: 850px) {
	#header {
		border:none;
	}
	.fixed-header #header {
		min-height: 0;
		position: unset;
		padding: 0;
		background: #111;
	}
	#search-wrapper, #sort-wrapper, #header summary {
    max-width: unset;
	}
	#actions {
    display: inline-block;
		padding-left: 3px
	}
	#stylus-options #actions {
    display: flex;
	}
	#actions #mozilla-format-container {
		margin-top: 0;
		width: 180px
	} 
	#basic-info {
    padding-bottom: 8px;
		padding-left: 5px
	}
	#name {
    margin-bottom: 0
	}
	#newUIoptions input[type="number"], #options [type="number"] {
    height: 25px;
    margin:0
	}
	#options #tabSize-label {
    top: 0;
	}
	#options .option.aligned > label {
		padding-right: 13px
	}
	#sections-list, #lint {
    width: 100%;
  }
	#sections > *, 	#sections > :not(.single-editor) {
    margin: 5px .5rem;
    padding: 0;
	}
	select {
    width: 200px;
	}
	.newUI .entry .checkmate {
		position: relative;
    left: unset;
    top: unset;
    bottom: unset;
    margin-right: 15px;
  }
}

/* -------------------------- POPUP --------------------------- */
#stylus-popup {
	width: 200px!important;
	border: 1px solid #333;
	background: #1d1d1d
}
#stylus-popup .style-name {
	padding-left: 32px;
	line-height: 0
}
.entry .style-name::before,.newUI .entry .style-name:hover::before {
	background: linear-gradient(to right, transparent, #251f7366 7%, transparent);
	transition: opacity .2s
}
#stylus-popup #installed {
	border: none;
	padding-bottom: 5px;
	padding-top: 5px;
}
#options-close-icon {
	position: absolute;
	top: 0;
	left: 0;
	width: 100vw;
	height: 100vh;
	z-index: -1;
}
#options-close-icon .svg-icon {
	display:none;
}
#options-title:before {
	position: unset;
	padding: 4px 41px 4px 0px;
	background-repeat: no-repeat
}
#stylus-popup .style-edit-link:hover .svg-icon,#options-close-icon:hover .svg-icon {
	fill: #eee
}
#stylus-popup #installed .actions a {
	padding-right: 5px
}
#stylus-popup #installed .style-name .checker, #stylus-popup #installed .style-name .svg-icon.checked {
	top: 6px
}
#stylus-popup .frame-url::before {
	content: "â¤· ";
	color: #777;
}
#stylus-popup .menu-button, #hotkey-info, .entry:nth-child(-n+10):before, .entry:nth-child(11):before, #popup-options button:last-child {
	display: none
}
#stylus-popup .entry-content {
	padding-right: 1px
}
#popup-options {
	order: 1;
	padding: 6px
}
#popup-options button {
	padding: 3px 0;
	margin-right: 2px;
	margin-left: 2px;
	flex: 1;
	min-width: 0;
	font-size: 11px
}
#stylus-popup > .actions {
	margin: 0!important;
	display: flex;
	flex-direction: column
}
#stylus-popup > .actions > #disable-all-wrapper, #stylus-popup > .actions > .main-controls {
	display: none;
	order: 2
}
#stylus-popup > .actions:hover #disable-all-wrapper {
	display: block
}
#stylus-popup > .actions:hover > .main-controls {
	display: table-cell
}
#stylus-popup > .actions > .main-controls {
	padding-left: 8px;
	padding-right: 8px;
	margin-bottom: 0!important;
	border-bottom: 1px solid #333
}
#disable-all-wrapper {
	margin-left: 8px;
	margin-right: 8px
}
#write-for-frames {
	position: relative;
	margin-left: 2px;
}
#write-style, #find-styles, #disable-all-wrapper {
	border: 1px solid #303030;
	padding: 8px;
	border-radius: 3px;
	background: #1d1d1d;
	margin-bottom: 5px
}
#find-styles-link {
	text-decoration: none;
}
#write-style-for {
	margin-left: 8px;
	font-style: italic
}
#write-style .match {
	margin-top: 4px;
	margin-left: 12px;
	min-width: unset
}
#write-style .match .match::before {
	border: none;
	content: "â¤·";
	margin-left: -10px;
	color: #777
}
#write-style > .match > .breadcrumbs {
	font-weight: bold;
	font-size: 11px
}
#find-styles-inline-group span {
	margin-left: 5px
}
body > div:not(#installed):not(#message-box):not(.colorpicker-popup):not(.blocked-info){
	padding: 0;
}
.blocked-info p {
    padding-bottom: 0;
}

/* -------------------------- Options --------------------------- */

html {
	overflow: auto
}
#stylus-options #actions {
	border:none!important;
	box-shadow: 0 0 5px #000000e0;
}
#stylus-embedded-options {
	background: transparent;
	backdrop-filter: blur(2px);
}
#stylus-options {
	background: transparent;
	max-height: calc(100vh - 32px);
	border: 1px solid #333;
	border-radius: 15px;
	box-shadow: 0 0 16px #0d0d0d;
	overflow: hidden;
	width: 85%;
	max-width: 900px;
}
#options-title {
	position: unset;
	background: #151515;
	box-shadow: 0 0 5px #000000e0;
	border-bottom: none;
	padding: 10px 0 10px calc(40%);
}
#stylus-options .block {
	border-bottom: 1px dashed #333;
}
#options input[type="color"], .onoffswitch {
    width: 30px;
}
.onoffswitch span {
	width: 30px;
	border: none;
	background: #222;
	box-shadow: none;
}
.onoffswitch span::before {
	background: #333;
	right: 15px;
	box-shadow: 0 0 3px #0d0d0d;
}
.onoffswitch input:checked + span {
    background: #183c18;
}
.onoffswitch input:checked + span::before {
    background-color: #3d8824;
    box-shadow: 0 0 3px #0d0d0d;
}
#stylus-options [type="number"] {
	width: 50px;
	text-align: left
}
.sync-now {
	background: #023202
}
.sync-now:hover {
	background: #013f01!important
}
#stylus-options [type="color"] {
	width: 35px;
	height: 35px;
	background: #111;
	border: none
}
#stylus-options button {
	width: fit-content
}

/* -------------------------- Search --------------------------- */

#search-replace-dialog:not(:focus-within):not(:hover),#search-replace-dialog [data-type="actions"] {
	opacity: .92;
}
#search-replace-dialog {
	overflow: hidden;
	background: transparent!important;
	backdrop-filter: blur(3px);
	top: 41px;
	right: 30px;
	width: 30%;
	min-width: 400px;
	box-shadow: 0 0 20px -5px #0d0d0d;
	border: 1px solid #333;
	border-radius: 8px;
}
#search-replace-dialog > *, #search-replace-dialog [data-type="status"] {
	background: transparent
}
#search-replace-dialog textarea {
	overflow: hidden;
	color: #eee!important;
	background: rgba(0, 0, 0, .25)!important;
	border-radius: 5px
}
#search-replace-dialog textarea:focus {
	outline: none
}
#search-replace-dialog [data-type="content"] {
	margin: 3px;
}
#search-replace-dialog svg,#search-replace-dialog svg:hover {
	fill: #eee!important
}
#search-replace-dialog [data-action="case"] {
	color: #ccc!important
}
#search-replace-dialog [data-type="status"] {
	display: none
}
}
